css:
@-moz-document domain(searx.org) {
body {
    color: #aaa;
    background-size: cover !important;
    background-color: #FFF;
    /* One of the very few things I've changed from the original stbinan theme is the following line */
    background: url("https://i.imgur.com/x7bnXWA.jpg") fixed center !important;
}
.searx-navbar {
    background-color: #10101036;
    border-color: rgba(0, 0, 0, 0);
}
.searx-navbar .navbar-nav > .active > a,
.navbar-default .navbar-nav > .active > a:hover,
.navbar-default .navbar-nav > .active > a:focus {
    color: #555;
    background-color: #050505;
}
.form-control {
    color: #bbb;
    background-color: #242424;
    background-image: none;
    border: 1px solid #666;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.075) inset;
}
.btn-default {
    color: #bbb;
    background-color: #242424;
    border-color: #666;
}
.highlight {
}
.panel {
    background-color: #101010;
    box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.05);
}
.panel-default {
    border-color: #666;
}
.panel-default > .panel-heading {
    color: #bbb;
    background-color: #050505;
    border-color: #666;
}
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
    background-color: #242424;
}
.label-default {
    background-color: rgba(0, 0, 0, 0);
}
.label {
    color: #bbb
}
.table-striped > tbody > tr:nth-child(2n + 1) > td,
.table-striped > tbody > tr:nth-child(2n + 1) > th {
    background-color: #101010;
}
.table > thead > tr > th,
.table > tbody > tr > th,
.table > tfoot > tr > th,
.table > thead > tr > td,
.table > tbody > tr > td,
.table > tfoot > tr > td {
    border-top: 0px solid #DDD;
}

a {
    color: #bbb;
}

/* The lines bellow have been modified by me (FenyX), so if something's wrong here it's my fault ;) */
.text-hide img {
    height: 0 !important;
    width: 0 !important;
    /* these numbers match the new image's dimensions */
    padding-left: 700px !important;
    padding-top: 342px !important;
    /*
    background: url("https://i.imgur.com/SZngYUN.png") no-repeat !important;*/
    background: url("https://i.imgur.com/31GnYDa.png") no-repeat center;
    /*background-size: 300px 100px;*/
    /*https://i.imgur.com/f7pl4tx.png*/
    opacity: 0.8;
}

.well {
    background-color: #242424 !important;
    border: 1px solid #666 !important;
}


.search_categories input[type="checkbox"]:checked + label,
#categories input[type="checkbox"]:checked + label {
    background: #606060;
    color: #e1e1e1;
    border-bottom: #aaa;
}
#advanced-search-container label,
#advanced-search-container .input-group-addon {
    background: #606060;
    color: #e1e1e1;
}

.text-center {
    max-height: 290px;
}
.panel,
.panel-default {
    height: 0px;
    width: 0px;
    overflow: hidden;
    border: 0px;
}


/*              RESULT PAGE             */

/*search suggestions*/
.tt-dataset-search-results{
    color:red!important;
}

/*search results*/
    .result_header a{
        /*titolo articolo*/
        color: #e6e6e6 !important;
        font-family: "DDG_ProximaNova", "DDG_ProximaNova_UI_0", "DDG_ProximaNova_UI_1", "DDG_ProximaNova_UI_2", "DDG_ProximaNova_UI_3", "DDG_ProximaNova_UI_4", "DDG_ProximaNova_UI_5", "DDG_ProximaNova_UI_6", "Proxima                     Nova", "Helvetica Neue", "Helvetica", "Segoe UI", "Nimbus Sans L", "Liberation Sans", "Open Sans", FreeSans, Arial, sans-serif;
        font-size: 18.8667px;
        line-height: 23.0167px;
        vertical-align: middle;
        letter-spacing: normal;
        word-spacing: 0px;

        font-weight: 400;
        font-style: normal;
        font-variant: normal;
        text-transform: none;
        text-align: start;
        text-indent: 0px;
    }

    .result_header a:hover {
        /*titolo articolo on hover*/
        text-decoration: underline;
    }

    .result-content {
        /*corpo articolo*/
        font-family: "DDG_ProximaNova", "DDG_ProximaNova_UI_0", "DDG_ProximaNova_UI_1", "DDG_ProximaNova_UI_2", "DDG_ProximaNova_UI_3", "DDG_ProximaNova_UI_4", "DDG_ProximaNova_UI_5", "DDG_ProximaNova_UI_6", "Proxima                     Nova", "Helvetica Neue", "Helvetica", "Segoe UI", "Nimbus Sans L", "Liberation Sans", "Open Sans", FreeSans, Arial, sans-serif;
        font-size: 16px;
        color: #fffc;
    }

    .external-link {
        /*link articolo*/
        font-family: "DDG_ProximaNova", "Helvetica Neue", "Helvetica", "Segoe UI", "Nimbus Sans L", "Liberation Sans", "Open Sans", FreeSans, Arial, sans-serif;
        font-size: 14.4px;
        color: #c2c2c2;
    }

    .external-link:hover {
        text-decoration: underline;
        cursor: pointer;
    }

    .result-content .highlight {
        
        color: #65d75e!important;
    }

    

/*bordi on hover sul risultato corrente*/
    .result,
    .result-default {
        border: solid 1px transparent;
    }
    .result:hover,
    .result-default:hover {
        background: #cccccc30;
        border-radius: 9px!important;
        border: solid 1px #ffffff57!important;
    }
/*end bordi on hover sul risultato corrente*/
}
