css:
@-moz-document domain("freefeed.net")
             , domain("mokum.place")
             , domain("myfeed.rocks")
{ 
/* Atlantic Mavi, CSS dark blue style. Version:0.9Beta.  Credits: Aphasia, Bianconiglio */
body, .create-post, .post-editor, .posts, .box, .box-body, .p-my-groups, .single-notification, .phabricator-remarkup, 
.notification-list, .box-body, .post-body, .single-post, .expandable, .expoanded, .comment-body  {
background-color: #102040;
color: #f7f7f7; 
    }
.box-body, .box-footer { border-radius: 5px; }
.box-footer { text-align: center; }
li { padding-left: 8px; color: #ccc; }
.post-like { padding-left: 0; }
.fold-comments-sticky { background-color: #f0f040; color: black; }
.comment-textarea { color: black; }
.comment-likes-list, .user-name-wrapper, .tile-list, .post.timeline-post.direct-post { background-color: #102040; }

bdi { color: red; }
.radio, .checkbox { color: #09bad4; 
    }
/* --------------------------- common Mokum-FreeFeed elements */ 
.col-md-9 {
    background-color: #102040;
    }
.col-md-3, .col-md-3.content   {
    background-color: #091832 ;
    }
input.search-input:focus {
    border-radius: 0;
    border: 0;
    border-bottom: 1px solid #eee;
    background-color: #102040;
    color: #dadada; /* LOL from Russian friends */
    }
/* --------------------------- end Mokum-FreeFeed elements 
                                                             
   --------------------------- Mokum proper elements */
#share-url-input.form-control { 
    border-radius: 0;
    border: 1px solid #eee;
    background-color: #102040;
    color: #dadada; /* LOL from Russian friends */
    }
.bem-post { width: 697px; margin-left: -11px; /* hides vertical post left bar */
            padding-left: 14px;               /* restores posts' vertical indent */
            background-color: #102040;
            font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
            font-size: 15px; color: #fafaaa; 
    }
.bem-phone-navigation__item, .bem-sidebar-menu__link  {
            font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
            font-size: 14px;
            color: #dada7a; 
    }
.bem-post__author-name {color: #a7a7d6; }
.bem-post__comment-text {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
            font-size: 14px; 
                      color: #caca7a; 
    }
.mthe-post__comment_subscribed { background-color: #091832; color: #fafaca; }
.mthe-post__comment_others { background-color: #162032;  color: #dadaba; }
.mthe-post__comment_your { background-color: #102040; color: #fafaaa; }
.bem-sidebar-menu__title, .bem-sidebar-userinfo__display-name { 
    color: #00F000 ; 
    }
.bem-page__post {
    border-bottom: 1px solid #9b9b9a;
    }
.content .box-body {
    background-color: #091832;
    }
.box-body td {
    background-color: #091832;
    }
#what ,
#wiki_page_status,
#user_show_display_names,
#select_change_status {
    background-color: #F0F0F0;
    color: #091832; 
    }
#wiki_page_slug {
    color: #F0F0F0;
    }
.dropdown-menu {
    background-color: #091832;
    color: #40f020; }
.modal-header, .modal-body, .modal-footer  {
    background-color: #091832;
    color: #40f020; }
/* --------------------------- end Mokum proper elements 
     */
h1, .site-logo, .site-logo-link, .name  {
color: silver !important; border-radius: 5px;
    }
h2, h3 { color: #00F000; }
.expand-button {
background-color: #102040;
color: #ffffff;
    }
.post {
background-color: #102040;
    }
span.Linkify,
span.Linkify span {
    color: #dada8a;
    background-color: #102040;
    }
.expand-panel {
    background-color: #102040;
    }
.Linkify {
color: gray;
    }
.col-md-3.sidebar,
.box-header-friends, 
.box-header-groups {
    color: #ffffff;
    }
.p-home a,
.p-direct-messages a,
.p-my-discussions a,
.p-my-discussions a,
.p-direct-messages a,
.p-best-of a {
     color: #ffffff;
     }
li.p-invites,
li.p-invites a,
div.box-footer a,
.box-header-search,
.box-header-timeline,
.user div a,
ul li a,
label {
     color: #ffffff;
     }
.search-button {
color: #3b179c;
}
.post-edit-more-trigger {
color: #9e660b;
}
.post-textarea,
.search-input {
color: #102040;
    }
.user-name-wrapper a span {
color: lime;
    }
div.box-footer {
    color: #ffffff;
    background-color: #606060;
    }
.post-author span { color: #5f5cfa; }
a { color: red; }
a.active {color: lime; }
a:hover { background-color: #a08000; }   /* era fff040; */
select { margin-left: 15px; }
.info a { color: #80FF00 !important; }
.post-action { color: #ff0080 !important; } 
.post-text a { color: #80FF00 !important; }
 /*                                toglie la barra a sx dei post */ 
.post.timeline-post { background-color: #102040; width: 697px; 
                      margin-left: -11px; 
                      padding-left: 11px;
    }
.invisibleSelect__label { color: #808080; }
.select.invisibleSelect__select { background-color: #000000; }
/* --------------------------- m.freefeed, Myfeed proper Style */
a.url2link-url, 
a.url2link-uname,
a.not-my-link {  color: #a7a7d6; }
a.my-link {  color: #ababda; }
p.description { color: silver !important; }
.post-cont {
    margin-bottom: 3px;
    color: #fafaaa;
    line-height: 20px;
    }
.sb-emt-content, .sb-emt-title, .sidebar-h, .sidebar-emt {
    background-color: #091832;
    color: #fafaaa;
    }
.cmt-content.long-text { color: #EaEaBa; }
.main, .splash {
    background-color: #091832;
    }
li.new-post-feed, input.input { color: #091832;
    }
ul.ft-list { color: #091832;
    background-color: #F0F0F0;
    }
.sidebar, .user-popup {
    border: 1px solid #5f5c7a;
    background-color: #091832;
    }
.privacy-popup {
    display: block; 
    width: 400px;
    height: 190px;
    text-align: center;
    margin-left: 10px;
    padding:10px;
    border: 1px solid #ff0080;
    background-color: #091832;
    }
                               /* FAQ phabricator indigo theme  */
.phui-document-container, .phui-document-toc-content {
    border: 1px solid #5f5c7a;
    background-color: #102040; 
    }
.phui-list-sidenav.phui-document-toc-list {
    border: 1px solid #5f5c7a;
    background-color: #2C1D4A; 
    }
/*  ----------------------209-- END m.freefeed, Myfeed elements */
}
