css:
@-moz-document domain("pandora.com")
{
/*
#######################################
***********PLEASE READ BELOW***********
#######################################
*/

  html
  {
    overflow: hidden;
  } 

/*
#######################################
The above code removes the ugly scroll
bar from the side of the page, but also 
removes your ability to scroll at all.
If you want to enable scrolling again, 
just comment out the above code, or you
can delete it altogether. Enjoy :)
#######################################
***************************************
#######################################
*/

/* The comments on this code are a mix
of my comments, and comments from the
style i used as a template. It really
could have been organized better, but
I wasn't originally planning on sharing
this with anyone. Sorry about that! */


/* 
 * `Now Playing` page
 */

#splash {
  background: none;
}

#playerBar {
  background-image: url(https://i.imgur.com/v8DGW00.png)!important;
}

.contentnav{
	border-top: solid #333 1px;
}

.clearfix {
  background: none;   
}

.still_listening_container .panel-top a {
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
  border: solid #49535C 1px;
  text-shadow: none;
  color: slategrey;
}

#body {
  background-color: black;
  background-image: url(https://i.imgur.com/v8DGW00.png)!important;
}

#playerBar .searchBox, .fxCol-cont-1 {
  background: none;   
}

.stationListItem .stationName li:hover {  
    background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
    border-bottom: solid black 1px;
    padding-top: 6px;
}

.stationListItem .inactiveHoverArrow li:hover {  
    background: none;
}

.stationListItem .stationName .stationNameText {
  text-shadow: none;
  color: slategrey;
}

.dd_container ul, .dropdownmenu ul {
  background-image: url(http://i.imgur.com/dAOTJ.jpg)!important;
  border: solid #374048 1px;
}

#brandingBar .rightcolumn .dd_container li a, .dropdownmenu li a {
  text-shadow: none;
  color: slategrey;
  padding: 6px 12px;
}

#brandingBar .rightcolumn .dd_container li a:hover, .dropdownmenu li a:hover {
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
}

#brandingBar .rightcolumn .user_activator a {
  background: none;
}

#playerBar .thumbUpButton.indicator a:link, #playerBar .thumbUpButton.indicator a:visited, #playerBar .thumbUpButton.indicator a:hover, #playerBar .thumbUpButton.indicator a:active {
    display: block;
    width: 36px;
    height: 35px;
    background-image: url(https://s9.postimg.org/ll5raxhy7/btn_up_indicator.png)!important;
}

.col-prop-1 {
  background: none;
}

.list-menu-1 .selected a, .list-menu-1 .on a {
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
  
}

.list-menu-1 .selected, .list-menu-1 .selected:hover, .list-menu-1 .on {
  border: solid slategrey 1px;
  background: none;
}

.list-menu-1 li:hover {
  background: none;
  background-color: none;
}

.genre_stations_lCol.col-b-1, .genre_stations_rCol.col-b-2, .listeners_lCol.col-b-1, .listeners_rCol.col-b-2, .music_feed_lCol.col-b-1, .music_feed_rCol.col-b-2, .search_lCol.col-b-1, .search_rCol.col-b-2, .track_detail_lCol.col-b-1, .track_detail_rCol.col-b-2, .music_feed_rCol .connect_promo {
  border: none;
}

.hed-1 {
  color:#243353
}

.music_feed_lCol.col-b-1 {
  padding-right: 4px;
}

.music_feed_rCol.col-b-2 {
  border-left: solid slategrey 1px;
}

.backstage h2 {
  border-bottom: solid slategrey 1px;   
}

.music_feed_rCol #connectSearchResults p {
  color: #2C3E63;
  font-weight: bold;
}

.music_feed_rCol .connect_promo .connect_promo_img, .space-b-2 {
  	background-color: none;
    opacity: 0.7;
    padding-left: 8px;
}

.music_feed_rCol .find_people input.find_field{
  all: initial;
  background-color: none;
  border: solid slategrey 1px;
  width: 360px;
  height: 28px;
  padding-left: 4px;
  color: slategrey;
}

.music_feed_rCol .find_people input.find_btn {
  all: initial;
  background-color: none;
  border: solid slategrey 1px;
  width: 44px;
  height: 28px;
  padding-left: 13px;
  margin-left: 10px;
  color: slategrey;
}

.music_feed_rCol .find_people .inputContainer .clear_input {
  margin-top: 2px;   
}

.backstage .col-right-profile-header {
  background: none;  
  border-left: solid slategrey 1px;
  margin-left: 194px;
  border-bottom: solid slategrey 1px;
}

.backstage .profile_rCol #user_info #user_info_header h1 {
  margin-left: -191px;
}

.backstage .col-right-profile-body {
  border: none;
  border-left: solid slategrey 1px;
  margin-left: 194px;
}

.backstage .profile_rCol .profile_permissions, .backstage .profile_rCol .user_message {
  color: slategrey;   
}

.backstage .profile_rCol #contentRight .comments h2 {
  padding-bottom: 13px;   
}

.backstage .comments .comment_form {
  all: initial;
}

.backstage .comments input, .backstage .comments textarea {
  width: 600px;
  border: solid slategrey 1px;
  color: slategrey;
  font-size: 13pt;  
  padding: 3px 0px 10px 4px;
}

.backstage .comments input:focus, .backstage .comments textarea:focus {
  color: slategrey; 
}

.backstage .comments .comment_controls {
    width: 740px;
    padding-left: 6px;
}

.backstage .comments .comment_privacy label {
  color: slategrey;
}

.backstage .comments .post_button, .backstage .comments .comment_privacy {
  opacity: 0.5;
  width: 200px;
}

.list-menu-1 .selected, .list-menu-1 .selected:hover, .list-menu-1 .on, .list-menu-1 .on:hover {
  background: none;   
}

.backstage .profile_rCol .btn_bg {
  background: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;   
  border: solid slategrey 1px;
}

#bookmarks_note {
  opacity: 0.55;  
}

#bookmarks_search{
  border: solid slategrey 1px;   
}


.backstage .profile_rCol #followers #search.empty{
  background: none;
  border: solid slategrey 1px;
  padding-left: 4px;
}

.backstage .profile_rCol #stations h2, .backstage .profile_rCol .like_section .title {
  border-bottom: solid slategrey 1px;     
}

.lightbox_body_no_header {
  background-image: url(https://i.imgur.com/v8DGW00.png)!important;
  border: solidslategrey 1px;  
}
    
/* Wrapper of playerBar, brandingBar */
#topnav {
  height: 0px !important;
}

/* Player bar */
#playerBar {
  background: #3a4a6a;
  border-bottom: 0px;
}

/* Bar with `Freemium` button and user menu */
#brandingBar {
  background: none;
  position: absolute !important;
  right: 0px !important;
  height: 25px !important;
  top: 60px !important;
  min-width: 200px !important;
  max-width: 200px !important;
}

/* Do branding bar items like navigation bar ones */
#brandingBar .rightcolumn,
#brandingBar .rightcolumn a {
  color: #d6deea;
}

.user_menu {
  padding-top: 5px;   
}

.user_activator {
  background: none !important;
}
.userName:hover,
a#freemium:hover {
  background: none !important;
  color: #fff !important;
  text-decoration: underline;
}

/* Hide unused items from branding bar */
#brandingBar .middlecolumn, .upgradeLink, 
.leftcolumn .logo,.user_menu + SPAN {
  display: none !important;
}

/* Search popup */
#searchPopupNavPosition {
  top: 50px;
}

#playerBar .searchBox {
  border: solid slategrey 1px;
  border-radius: 20px;
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;   
}

.searchPopupContainer .content {
  background-image: url(https://i.imgur.com/v8DGW00.png)!important;
  border: solid slategrey 1px;
}

.searchPopupContainer .footer {
  display: none;
}

.searchPopupContainer .searchInput, .searchPopupSection{
  color: slategrey;
}

.searchPopupContainer .arrow {
  background-image: url(https://s3.postimg.org/sjfru2ur7/arrow.png);
}

.searchPopupContainer .content .label {
  background: none;
  padding-top: 9px;
}

.searchPopupContainer .content .item.selected, #playerBar .searchInput{
  color: #e6e6e6;
  background: none;
}

#playerBar .searchInput.focus{
  color: #e6e6e6;
}

/* Main content */
#main {
  top: 55px !important;
  margin-top: 34px !important;
}

/* Reduce page height */
#mainContainer, .skinContainer {
  height: 100% !important;
}

/* Set full width of content */
#adLayout, #mainContentContainer,
.slidesBackground, .slidesBackground img {
  width: 100% !important;
}

/* Content navigation bar (Now playing, Music feed, ...) */
.contentnav {
  background-image: url(http://i.imgur.com/dAOTJ.jpg)!important;
  background-size: contain;
  position: fixed !important;
  top: 66px !important;
  left: 0px !important;
  width: 100% !Important;
  z-index: 102 !important;
}

.col-prop-1 {
    min-height: 550px;
}

/* Remove backbround except of log in screen */
#mainContent,
#mainContainer:not(.no_player_bar),
#trackInfo,
#trackInfoButtons.unselectable,
.stationContent
{
	background:none;
    border: none;
}

#adLayout, #mainContentContainer, .slidesBackground, .slidesBackground img{
    background-color: rgba(0,0,0,0.0);
}

#trackDetailContainer {
  	background: transparent;
    border: none;
}

#trackDetail .detail {
	background: transparent;
    border: none;
}

.detail .divider {
  display: none;    
}

.item.relatedArtists {
    display: none;  
    border: none;
}

#trackInfo {
  border-bottom: solid slategrey 1px;   
}

#trackInfoButtons .buttons {
  display: none;
  border: none;
}

#trackInfo .info a {
  all: initial;
  color: #0b448e;
}

.byText, .onText {
  all: initial;   
  font-size: 10pt;
  color: slategrey;
}

#currentSlideArrow {
    background-image: url(https://s14.postimg.org/65b484nwh/arrow3.png);
}

.stationListItem.selected{
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
  background-size: 200px 3000px;
  background-repeat: no-repeat;
  background-position: 0px -4px;
  border-top: solid slategrey 1px;
  border-bottom: solid slategrey 1px;
}

.stationListItem.selected .options {
  border: none;
  border-top: solid #374048 1px;
  margin-top: 2px;
}

.stationListItem .option.addVariety {
  border-right: solid #374048 1px; 
}

.options{
  background-image: url(http://i.imgur.com/dAOTJ.jpg)!important;
}

.stationContent {
    top: 0px;
    border-right: solid slategrey 1px;
    height: 100%;
}

#mainContent, #mainContainer:not(.no_player_bar), #trackInfo, #trackInfoButtons.unselectable, .stationContent {
  height: 100%;   
}

/* Empty element */
.top {
  display: none !important;
}

/* Artist info block */
.homeRightSide, .view-2 {
  background-color: transparent;
}

/* Dividers of artist info block */
.detail .divider {  
  border-bottom: 1px solid #bbc5d0;
}

/* Remove empty space after lyrics */
.detail .itemContent .lyricsText {
  
  margin-bottom: 0px;
}

/* Fix left border of artist info block */
.fxCol-cont-5 {
  background: none;
  border-left: solid slategrey 1px;
}


/* 
 * Station list
 */

/* Move station sort buttons up */
#stationSortButtons {
  position: absolute;
  top: 95px;
  width: 190px;
    
}
.stationListHolder {
  margin-top: 125px;
  border-top: solid #0b0d0e 1px;
}

#stationSortButtons {
  border-bottom: solid #0b0d0e 1px; 
  
}

#stationSortButtons div {
  background: none;   
  text-shadow: none;
  color: slategrey;
  border: none;
}

.btn_bg_sort.selected {
  background-image: url(http://www.pandora.com/img/content-area/albumart-bg-top.jpg)!important;
  border: none;
  background-position: right top;
}

/* Hide promo information */
#upgradeOffer, #promobox, .platformPromo {
  display: none !important;
}

.stationContent {
    height: 70%;
    position: fixed;
    
}

.jspContainer, .stationListHolder {
    height: 100%;
    border-right: solid slategrey 1px;
    overflow-y: hidden; 
}

.scroll-pane.vertical-only {
  overflow-y: hidden; 
  border-top: solid slategrey 1px;
  margin-top: 124px;
}

.stationListItem .stationName .stationNameText.promotedStation {
  background: url(https://s13.postimg.org/hdynslmvb/promoted_icon.png) no-repeat 0 8px;
}


/* 
 * Popup window 
 */

/* Fix window on screen */
.lightbox {
  position: fixed;
}
/* Remove border */
.lightbox_bg {
  background: none;
  padding: 0px;
  border: 1px solid #BBC1CD !important;
}
/* Use pretty close button */
.lightbox_bg .close {
  background: url(http://pandora.com/img/close1_btn.png) no-repeat 0 0;
  top: 3px;
  right: -12px;
}


/*
 * Misc
 */

/* Change font */
* {
  border-radius: 0px !important;
  font-family: "Segoe UI" !important;
}
/* Fix image flickering */
img {  
  backface-visibility: hidden;
}

/* Tob bar on `Restricted` page, Page footer */
.topbar, #footer {
  display: none !important;
}

/*  Hide ads and other useless stuff */
#ad_container, #accountMessageRibbon, #videoPageInfo,
.track_detail_close, .stationChangeSelectorNoMenu {
  display: none !important;
}

/* Remove borders at station page */
.section {
  border-bottom: 0px;
}

/* Unknown */
.tip_header {
  position: relative !important;
  top: -1px !Important;
  margin-top: -1px !Important;
}

/* Fix header width on user profile page */
.col-right-profile-header {
  width: 100%;
}

/* Freemium fixes */
.freemium-hide-ads.adSupported-layout #adLayout,
.freemium-hide-ads.adSupported-layout .contentContainer {
  width: 100% !important;
}
#freemium_settings {
  top: 20px !important;
}
}
