css:
@-moz-document regexp("(https|http)://informatics.letovo.ru.*"), regexp(".*ejudge.*/.*") {
input[type="submit"] {
    font-size: 12pt !important;
    min-width: 130px !important;
    padding: 6px 16px !important;
    text-align: center !important;
    cursor: pointer !important;
    user-select: none !important;
    border: 1px solid #000000 !important;
    background: #03a9f4 !important;
    color: #FFFFFF !important;
    overflow: hidden !important;
    font-weight: bold;
}

.nProbEmpty, .nProbBad, .nProbOk, .nProbCurrent, .nProbTrans {
    border: 1px solid #000000 !important;
    border-left: none !important;
    padding-top: 20px !important;
    border-radius: 0 !important;
    font-size: 14pt;
}

.nTopNavList li:first-child div, .nBottomNavList li:first-child div, .nTopNavList li.first-rad .nProbEmpty, .nTopNavList li.first-rad .nProbCurrent {
    border-left: 1px solid #000000 !important;
    border-radius: 0 !important;
}

.nProbEmpty {
    background: #03a9f4 !important;
}

.nProbBad {
    background: #d50000 !important;
}

.nProbCurrent {
    background: #0277bd !important;

}
.nProbCurrent a {
    font-size: 15pt;
}

.nProbOk {
    background: #00c853 !important;
}

.nProbTrans {
    background: #ffd740 !important;
}

.nTopNavList {
    margin-top: 30px !important;
}

.nTopNavList ul, .nBottomNavList ul {
    margin-left: 20px !important;
}

div.nProbEmpty a, div.nProbBad a, div.nProbOk a, div.nProbCurrent a, div.nProbTrans a {
    bottom: 10px !important;
    color: #ffffff !important;
    font-weight: bold !important;
}

.nProbTrans a {
    color: #000000 !important;
}

.probNav li {
    top: 0 !important;
    bottom: 0 !important;
    height: 32px !important;
}

#footer {
    display: none !important;
}

#l11 img {
    display: none !important;
}

#l11 {
    width: 0 !important;
    height: 0 !important;
}

.main_phrase {
    font-size: 15pt !important;
    margin-left: 20px !important;
    margin-bottom: 30px !important;
    padding-top: 30px !important;
}

#main-menu {
    margin: 0 !important;
    padding: 0 !important;
    background: #03a9f4 !important;
    border: none !important;
    min-width: 100% !important;
    height: 26px !important;
}

#main-menu ul li .current, #main-menu ul li.current {
    background: #0277bd !important;
    margin: 0 !important;
    border: none !important;
    display: inherit !important;
    height: inherit !important;
}

#main-menu a, #main-menu ul li .current div, #main-menu ul li.current div {
    color: #ffffff !important;
    height: 23px !important;
}

#main-menu ul {
    margin: 0 !important;
    padding: 0 !important;
}

#probNavTaskArea {
    border: none !important;
}

#container {
    position: relative !important;
    display: block !important;
    margin: 0 !important;
    padding: 0 !important;
    width: auto !important;
    min-width: 100% !important;
    border: none !important;
    background: #ffffff !important;
    min-height: 100vh !important;
}

#l12-col {
    background: none !important;
    height: auto !important;
}

#l12 {
    position: static !important;
    height: auto !important;
}

#main-cont {
    padding: 0 !important;
    width: 100% !important;
    min-height: 100vh !important;
}

body {
    margin: 0 !important;
    padding: 0 !important;
}

.h2-long h2 {
    font-size: 15px !important;
    background: none !important;
    color: #000000 !important;
    display: inline !important;
    border: none !important;
    padding: unset !important;
    margin: unset !important;
}

* {
    box-shadow: none !important;
    text-shadow: none !important;
    font-family: 'Roboto', 'Segoe UI', sans-serif !important;
}

.info-table-line tr, .line-table-wb tr {
    border: none !important;
}

#statusLine {
    height: 26px !important;
    font-size: 12pt !important;
    border: none !important;
    padding-left: 20px !important;
}

div.server_status_on {
    background: #00e676 !important;
}

div.server_status_error {
    background: #d84315 !important;
}

div.server_status_alarm {
    background: #afb42b !important;
}

.server_status_off {
    background: #9e9e9e !important;
}

.table th, .table .th, table.standings th {
    background: #1a237e !important;
    color: #ffffff !important;
    border: none !important;
}

select {
    background-color: #0563af !important;
    color: white !important;
    padding: 8px !important;
    width: 280px !important;
    border: none !important;
    font-size: 14pt !important;
    outline: none !important;
}

select option {
    padding: 30px !important;
}

div.user_action_item {
    font-size: 0 !important;
}

div.user_actions {
    background: rgb(102, 204, 255) !important;
    margin: unset !important;
    border: none !important;
    width: 100% !important;
}

div.user_actions table.menu {
    padding: 10px !important;
}


}
