css:
@-moz-document domain("userbenchmark.com") {
body {
    color:#dfdfdf;
    background-color: #202020;
}

div.pagecontainer, div.pagecontainer>div, div.pagecontainer>div>div, div.be-lb-page-top-banner {
    background-color: #202020 !important;
    background-image: none !important;
}

.nav-tabs>li>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:visited {
    color: #dfdfdf;
    background-color: #202020;
}
.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus , .nav-tabs>li>a:hover, .nav-tabs>li.active>a:visited {
    color: #efefef;
    background-color: #181818;
}

.vsstyle, a.stealthlink, .ptscore {
    color: #fff;
}

div.pagebounds, div.pagebounds>div, div.pagebounds>div>div {
    background-color: #181818 !important;
}

table.table-no-border>tbody>tr>td {
    border: 0px !important;
}

.top-menu .toptext, .top-menu a {
    color: #fff;
}

.headergrad {
    background-image: none;
}

.prcloc-sel {
    color: #111;
}

div.boxthumb, div.boxthumb>div, div.boxthumb>div>div {
    color: #fff;
    background-color: #000 !important;
    background-image: none !important;
}

.bsc-w {
    color: #f0f0f0;
}
.bsc-c, .lightblacktext {
    color: #b0b0b0;
}
.tabs-left>.nav-tabs {
    border-right: 1px solid transparent;
}
.comp-comparelabeldesc {
    color: #808080;
}

.mh-tc {
    color:#101010;
}

.well {
    background-color: #101010 !important;
}

table.table-topless>tbody>tr>td {
    color: #c0c0c0;
}

}
