css:
/* i really want this to be global */
article {
    background-color: #181a1d;
}
body {
    background: #181a1d;
    color: #fdfdfd;
}
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
nav,
section,
summary {
    background-color: #181a1d;
}
* {
    background-color: #181a1d;
}

#wrapper {
    background-color: #181a1d;
    border: 1px solid #181a1d;
}
.col-full-width .preamble {
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
    border-bottom: 1px solid #181a1d;
    border-bottom-color: rgb(24, 26, 29);
    border-left: 1px solid #181a1d;
    border-left-color: rgb(24, 26, 29);
    border-top: 1px solid #181a1d;
    border-top-color: rgb(24, 26, 29);
    background-image: linear-gradient(131deg, #181a1d 100%, hsl(216, 9%, 10%) 100%);
}
footer[role=contentinfo] {
    border-top: 3px solid #181a1d;
    border-top-color: rgb(24, 26, 29);
}
.small-feed .feed-container {
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
}
.small-feed {
    border-top: 10px solid #333;
    border-top-color: rgb(51, 51, 51);
    border-bottom: 10px solid #333;
    border-bottom-color: rgb(51, 51, 51);
    border-left: 1px solid #181a1d;
    border-left-color: rgb(24, 26, 29);
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
}
.col-full-width .preamble:first-of-type:after {
    border-left: 1px solid #181a1d;
    border-left-color: rgb(24, 26, 29);
}
.col-full-width .preamble h2 a {
    color: #fff;
}
.tek-frontpage .featured-feeds header {
    background-color: #181a1d;
    color: #aaa;
}
.col-full-width .preamble.commercial .source-info,
.col-full-width .preamble.commercial .text {
    background-color: #181a1d;
}
.col-full-width .preamble.commercial {
    background-image: linear-gradient(131deg, #181a1d 0%, #181a1d 100%);
}
.preamble.commercial .text,
.preamble.commercial .source-info {
    background-color: #181a1d;
}
.tek-frontpage .featured-feeds {
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
}
.tek-frontpage .featured-feeds {
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
}
.top-lists nav {
    border-bottom: 1px solid #181a1d;
    border-bottom-color: rgb(24, 26, 29);
    background-color: #181a1d;
}
.col-full-width .preamble:first-of-type:after {
    border-left: 1px solid #181a1d;
    border-left-color: rgb(24, 26, 29);
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
    border-top: 1px solid #181a1d;
    border-top-color: rgb(24, 26, 29);
    border-bottom: 1px solid #181a1d;
    border-bottom-color: rgb(24, 26, 29);
}
.col-full-width .feed-comics {
    border-left: 1px solid #181a1d;
    border-left-color: rgb(24, 26, 29);
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
    border-top: 1px solid #181a1d;
    border-top-color: rgb(24, 26, 29);
    border-bottom: 1px solid #181a1d;
    border-bottom-color: rgb(24, 26, 29);
}
.col-full-width .preamble {
    border-right: 1px solid #181a1d;
    border-right-color: rgb(24, 26, 29);
    border-bottom: 1px solid #181a1d;
    border-bottom-color: rgb(24, 26, 29);
    background-image: linear-gradient(131deg, #181a1d 0%, hsl(216, 9%, 10%) 100%);
}
.preamble.commercial {
    background: #181a1d;
    background-image: none;
    background-color: rgb(24, 26, 29);
}
.top-lists ul li .rank {
    background-color: #181a1d;
}
.prisjakt-header-link a {
    background-color: #181a1d;
    color: #dce0e0;
}
table {
    border-color: #181a1d;
}
.main-color,
.no-touch .main-color-on-hover:hover,
.mc,
.no-touch .mcoh:hover {
    color: #fff;
}
.comment-count:after {
    border-color: #002D32 transparent;
}
.comment-count {
    background-color: #002d32;
}
.score-badge .score-logo {
    background: #181a1d;
}
