css:
@-moz-document domain('myuni.adelaide.edu.au') {

body:not(.full-width):not(.outcomes) .ic-Layout-wrapper {
    max-width: none;
}

.ic-app-header__logomark-container {
    display: none;
}

.ic-app-header__menu-list {
    margin-top: 14px;
}

@media only screen and (min-width: 1140px) {
    body.course-menu-expanded .ic-app-main-content__secondary {
        width: 30%;
    }
}

.ToDoSidebarItem__Info {
    max-width: 100%;
}

.ToDoSidebarItem__Title span {
    white-space: pre-wrap !important;
}

body.course-menu-expanded .ic-Layout-wrapper {
    position: fixed;
    width: calc(100% - 84px);
}

.ic-app-header__secondary-navigation {
    display: none;
}

body.course-menu-expanded:not(.ic-no-flex-layout):not(.embedded) .ic-Layout-columns {
    height: calc(100vh - 74px);
    max-height: calc(100vh - 74px);
    overflow-y: scroll;
    overflow-x: visible;
    margin-left: 0 !important;
    padding-left: 192px;
}

.ic-app-course-menu nav {
    width: 156px;
}

#calendar-app {
    height: calc(100vh - 74px);
    max-height: calc(100vh - 74px);
    overflow-y: scroll;
}

.ic-app-course-menu {
    left: 84px;
    right: auto;
    top: 72px;
    bottom: 0;
    position: fixed;
    overflow-y: scroll !important;
}

#ag-list {
    padding-bottom: 0;
}

.tool_content_wrapper {
    height: calc(100vh - 74px) !important;
    overflow-y: hidden;
}

iframe {
    min-height: auto !important;
    overflow: hidden;
}

#doc_preview {
    height: calc(100vh - 300px) !important;
}

#doc_preview  div {
    overflow: hidden !important;
}

.item-group-container {
    padding-bottom: 0;
}

}
