css:
@-moz-document domain("drrr.com")
 {/* by Ludirazaqi@gmail.com */

body,
html,
.message_box_effect_wraper {
    background:rgba(0, 0, 0, 0.4);
    padding: 5px 0;
}

.game-room--night .message_box_effect_wraper {
    color: #fff;
    background: rgba(0, 0, 0, .4);
}
.message_box {
    color: #fff;
}
ul.menu > li > .icon, ul.menu > li > a, ul.menu > li > input {
    color: #fff;
    font-size: 15px;
}
.game-room--night ul.menu > li > .icon, .game-room--night ul.menu > li > a, .game-room--night ul.menu > li > input {
    color: #fff;
}
ul.menu > li > .icon:focus, ul.menu > li > .icon:hover, ul.menu > li > a:focus, ul.menu > li > a:hover, ul.menu > li > input:focus, ul.menu > li > input:hover {
    color: #01cdfe;
}
.game-room--night ul.menu > li > .icon:focus, .game-room--night ul.menu > li > .icon:hover, .game-room--night ul.menu > li > a:focus, .game-room--night ul.menu > li > a:hover, .game-room--night ul.menu > li > input:focus, .game-room--night ul.menu > li > input:hover {
    color: #01cdfe;
}
.to-whom {
    float: right;
    font-size: 12px;
    margin-right: 5px;
    transition: opacity .2s ease;
    opacity: 0;
    visibility: hidden;
}
.to-whom .symbol {
    display: inline-block;
    width: 10px;
    height: 10px;
    margin-right: 4px;
    margin-left: 3px;
    -webkit-box-shadow: 0 0 0 1px #fff;
    box-shadow: 0 0 1px 1px #fff;
}
.form-control {
    display: block;
    width: 100%;
    height: 26px;
    padding: 1px 1px;
    font-size: 13px;
    line-height: 1.42857143;
    color: #000;
    background-color: #fff;
    border: 1px solid #aaa;
    border-radius: 1px;
}
.room-input-wrap textarea {
    height: 5em;
    resize: none;
    border-radius: 5px;
}
.game-room--night .room-input-wrap textarea {
    color:#fff;
    background:#fff0;
    border-color:#01cdfe;
}
.game-room--night .room-input-wrap textarea.state-secret {
    background: rgb(81, 82, 82);
}
.room-input-wrap textarea.state-secret {
    background: rgb(81, 82, 82);
}
.room-submit-wrap .room-submit-btn:focus, .room-submit-wrap .room-submit-btn:hover {
    color: #b967ff;
    border-color: #01cdfe;
}
.room-submit-wrap .room-submit-btn {
    display: block;
    margin: 5px auto 0;
    width: 184px;
    height: auto;
    font-size: 12px;
    line-height: 1;
    padding-top: 3px;
    padding-bottom: 3px;
    color: #fff;
    font-weight: 400;
    background: #000;
    border-radius: 3px;
    border: 1px solid #fff;
    outline: 0;
}
.game-room--night .room-submit-wrap .room-submit-btn:focus, .game-room--night .room-submit-wrap .room-submit-btn:hover {
    color: #b967ff;
    border-color: #01cdfe;
}
.notification-bar.notice {
    color: #fff;
    background: #0006;
}
.notification-bar {
    display: none;
    width: 100%;
    z-index: 1050;
    font-size: 13px;
    text-align: center;
    font-weight: 700;
    padding: 1px 1px;
    border-radius: 0 0 1px 0;
}
#musicBox {
    bottom: 0;
    left: 0;
    right: 0;
    width: 100%;
    height: 40px;
    position: fixed;
    background: rgba(0, 0, 0, .78);
    color: #a7a7a7;
    font-size: 14px;
    z-index: 6000;
}
#musicShelf .musicList {
    padding: 5px 0;
}
#musicShelf .musicList .musicItem:focus, #musicShelf .musicList .musicItem:hover {
    color: #000;
    background: #01cdfe;
}
#musicBox .volume input[type="range"] {
    -webkit-appearance: none;
    height: 7px;
    padding: 0;
    background: rgba(52, 212, 255, .3);
    border-radius: 40px;
}
.progress-music {
    position: fixed;
    bottom: 40px;
    left: 0;
    height: 1px;
    width: 0;
    background: #01cdfe;
    opacity: 1;
}
.bubble .body {
    overflow: hidden;
    float: left;
    clear: left;
    padding: 15px 25px;
    margin: 0 15px 5px 15px;
    border-radius: 12px;
    border: 4px #fff solid;
    background: #000;
    font-size: 1.1em;
    line-height: 1.4;
    letter-spacing: 3px;
    color: #fff;
    position: relative;
    word-break: break-word;
    min-height: 60px;
}
.talk dt .avatar {
    width: 60px;
    height: 60px;
    background-size: 60px 60px;
    margin: 0px auto;
    text-indent: -99999px;
}
.system {
    margin: 10px 10px 20px -30px;
    letter-spacing: 3px;
    clear: left;
    word-break: break-word;
}
.dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover {
    color: #000;
    background-color: #01cdfe;
}
.input-group-sm > .form-control, .input-group-sm > .input-group-addon, .input-group-sm > .input-group-btn > .btn {
    height: 30px;
    padding: 3px 5px;
    font-size: 12px;
    line-height: 1.5;
    border-radius: 0px;
}
#setting_pannel .user-list > li.is-tripcode {
    color: #01cdfe;
}
.room-input-wrap .url-icon {
    height: 20px;
    width: 50px;
    background: rgba(255, 255, 255, .8);
    position: absolute;
    right: 0px;
    bottom: 0px;
    margin-right: 120px;
    border-top-left-radius: 0px;
    border-bottom-right-radius: 3px;
    border: 1px solid #aaa;
    border-right-color: transparent;
    border-bottom-color: transparent;
    font-size: 12px;
    font-weight: 700;
    text-align: center;
    cursor: pointer;
    opacity: 0;
    outline: 0;
    -webkit-transition: opacity ease .2s;
    transition: opacity ease .2s;
}
.sweet-alert {
    color: #fff;
    background-color: #000000b3;
    width: 480px;
    padding: 20px;
    border-radius: 10px;
    text-align: center;
    position: fixed;
    left: 50%;
    top: 50%;
    margin-left: -250px;
    margin-top: -200px;
    overflow: hidden;
    display: none;
    z-index: 99999;
    -webkit-box-shadow: inset 0 0 60px 0 rgba(0, 0, 0, .2), 0 10px 60px 0 rgba(0, 0, 0, .2);
    box-shadow: inset 0 0 60px 0 rgba(123, 123, 123, 0.2), 0 10px 60px 0 rgba(1, 205, 254, 0.3);
}
.notification-bar.warning {
    color: #fff;
    background: #f00;
}
#setting_pannel {
    clear: both;
    position: fixed;
    top: 122px;
    left: 0;
    color: #ff71ce;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 0 0px 10px 0;
}
.bubble .body .message-link {
    padding-left: 5px;
    padding-right: 5px;
    margin-left: 10px;
    font-weight: 700;
    color: #fff;
    background: #000;
    -webkit-box-shadow: inset 0 0 6px rgba(255, 255, 255, .8);
    box-shadow: inset 0 0 0px rgb(255, 255, 255);
    border: 1.5px #fff solid;
    border-radius: 5px;
    height: 1em;
    text-transform: uppercase;
}
.bubble .body .message-link:focus {
    color: #999;
    background: #d6d6d6;
    text-decoration: none;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
    box-shadow: inset 0 0 6px rgba(0, 0, 0, .3);
}
.img-thumbnail {
    padding: 4px;
    line-height: 1.42857143;
    background-color: #fff0;
    border: 1px solid #aaa;
    border-radius: 10px;
    -webkit-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
    display: inline-block;
    max-width: 100%;
    height: auto;
}
.image-mask:hover img {
    border-color: #ff71ce;
}
.row {
    margin-left: -20px;
    margin-right: -10px;
}
.dropdown-menu {
    border-radius: 3px;
    color: #fff;
    background-color: #000c;
    border-color: #aaa
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
    color: #fff;
    background-color: #000;
    border: 1px solid #ddd;
    border-bottom-color: transparent;
    cursor: default;
}
.name-wrap .symbol {
    display: inline-block;
    width: 8px;
    height: 8px;
    margin-right: 4px;
    margin-left: 1px;
    vertical-align: 1px;
    -webkit-box-shadow: 0 0 0 1px #fff;
    box-shadow: 0px 0px 1px 1px #fff;
}
.modal .modal-content .nav-tabs > li.active > a, .modal .modal-content .nav-tabs > li.active > a:focus, .modal .modal-content .nav-tabs > li.active > a:hover {
    color: #fff;
}
.modal .modal-content .nav-tabs > li > a:focus, .modal .modal-content .nav-tabs > li > a:hover {
    color: #fff;
    background: #ff71ce;
}
.wrap {
    background: #080808;
    padding: 16px 20px;
    margin-left: auto;
    margin-right: auto;
    color: #fff;
    border-radius: 4px;
    clear: both;
}
.modal .modal-content .input-group .btn {
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
    padding-top: 1px;
    padding-bottom: 1px;
}
.modal .modal-content .btn {
    background-color: rgba(0, 0, 0, .15);
    color: #fff;
    -webkit-box-shadow: none;
    box-shadow: none;
    font-size: 14px;
    font-weight: 494;
    border-radius: 10px;
    padding: 2px 32px;
    border: 2px solid #fff;
}
ul.rooms li.name .btn-link {
    color: #fff;
    font-size: 1em;
    text-align: left;
}
body {
    background-color: #000;
    color: #fff;
    font-size: 15px;
    -webkit-text-size-adjust: none;
}
.wrap a {
    color: #fff;
    text-decoration: none;
}
ul.rooms li.members .avatar-wrap {
    display: block;
    float: left;
    width: 20%;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    color: #fff;
}
.lounge-nav {
    margin: 0 -20px 0;
    padding: 6px 20px;
    font-size: 14px;
    background: #000;
}
.lounge-links {
    color: #fff;
    line-height: 28px;
}
.progress-bar {
    float: left;
    width: 0%;
    height: 100%;
    font-size: 12px;
    line-height: 20px;
    color: #fff;
    text-align: center;
    background-color: #b967ff;
}}
