/* -------------------------------------------- *//* main*/
.main {
	background:none !important;
	margin:0px !important;
	width:auto !important;
	max-width:1000px !important;
	min-width:700px !important;
}
/* -------------------------------------------- *//* główna kolumna */
 .newsList,
 .third,
 #content .third,
 .toolItem, .expandPage,
 #content {
	font-size:11px !important;
	width:auto !important;
	padding-left:5px !important;
	padding-right:5px !important;
	margin-top:0px !important;
}
/* -------------------------------------------- *//*okienko odpowiedzi*/
.komentarze .formularz textarea {
	font-size:11px !important;
	height:200px !important;
	width:150% !important;
}
#ctl00_phContent_Comments_tblForm{
    width: 70% !important;
}
/* -------------------------------------------- *//* różne drobne niedociągnięcia */
#header .top_img,
#header .top_4,
.categories,
.komentarze,
#content .third .news .mainBar,
#content .third .news .newsOptions,
#content .third.demo .itemOptions {
	width:auto !important;
}
.profile .pageContent {
	width:82% !important;
}

/* -------------------------------------------- *//*kolor czcionki dla relaksu*/
body, img, div, input, textarea,p,
#article, #content, .third, .news, .newsContent, .page{
	color:#505050 !important;
	background:#FFFFFF !important;
}
a:link { color: #5B95D9 !important }
a:visited { color: #ABBA84 !important }
a:hover, a:active { color: #FFA500 !important }


