css:
@-moz-document domain("forum.vivaldi.net") {
/* https://isabella-theme.github.io/ */
body {
    --colorHl: #be4318;
    --colorHlFaded: #b18879;
    --colorHlFg: var(--colorWhite);
    --colorBg: #333335;
    --colorBgDarker: #232325;
    --colorBgFaded: #444445e8;
    --colorFg: #d1d1d1;
    --colorFgBolded: white;
    --colorFgFaded: #87878c;
    --colorFgFadedMore: #68686c;
    --colorAc: #101013;
    color: var(--colorFg);
    background-color: var(--colorBg);
}
/* globals */
a:focus,a:hover {color: var(--colorHl);}
blockquote {
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
code {
    color: var(--colorFgBolded);
    background-color: var(--colorAc);
}
hr {border-color: var(--colorBgDarker);}
pre {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.h1 .small,.h1 small,.h2 .small,.h2 small,.h3 .small,.h3 small,.h4 .small,
.h4 small,.h5 .small,.h5 small,.h6 .small,.h6 small,h1 .small,h1 small,
h2 .small,h2 small,h3 .small,h3 small,h4 .small,h4 small,h5 .small,h5 small,
h6 .small,h6 small {
    color: var(--colorFg);
}
/* header */
#vivaldiheader {background: var(--colorAc);}
#vivaldiheader #vivaldilogo img,#vivaldimenu a .sub-arrow {
    filter: grayscale(1) invert(100%);
}
#vivaldiheader a,#vivaldiheader a:active,
#vivaldiheader a:focus,#vivaldiheader a:visited {
    color: var(--colorFg);
}
#vivaldimenu a.current,#vivaldimenu a:hover {color: var(--colorHl);}
/* header drop downs */
#vivaldimenu ul {
    color: var(--colorFg);
    background: var(--colorBgDarker);
}
#vivaldimenu ul,#vivaldimenu ul li {border-color: var(--colorBgDarker)}
#vivaldimenu ul a:hover {background: var(--colorBgFaded);}
/* forum navigation */
#submenu {
    color: var(--colorFg);
    background-color: var(--colorAc);
    border-color: var(--colorAc);
}
#submenu a:focus,#submenu a:hover,#submenu li.active>a {
    color: var(--colorHl);
    background-color: var(--colorBg);
}
.unread-count:after {
    color: var(--colorHlFg);
    background: var(--colorHl);
    border-color: var(--colorHl);
}
#submenu a:focus .unread-count:after,#submenu a:hover .unread-count:after,
#submenu li.active>a .unread-count:after {
    color: white; /* doesn't accept variables */
}
/* content */
/* forum path */
.breadcrumb>.active,.breadcrumb>li+li:before {color: var(--colorFg);}
.posts-list .posts-list-item .topic-title,a {color: var(--colorHl);}
.categories>p,.category>p,.subcategory>p {color: var(--colorFgFaded);}
.breadcrumb .fa {color: var(--colorFg);}
/* forum categories */
.categories>li:hover,.category>ul>li:hover {background-color: transparent;}
.categories-title {color: var(--colorFgFaded);}
.category>ul>li:not(.unread) h2 a {color: var(--colorFgFaded);}
.categories .description,.category .description,.subcategory .description {
    color: var(--colorFg);
}
.categories .category-children small,.category .category-children small,
.subcategory .category-children small {
    color: var(--colorFgFaded);
}
.category-children a small:hover {color: var(--colorHl);}
.category>ul>li .tag-list .tag {
    background: var(--colorBgDarker);
}
/* category stats */
.stats small {color: var(--colorFgFaded);}
/* last post teasers */
a.permalink,a.permalink:active,a.permalink:focus,a.permalink:hover {
    color: var(--colorFgFaded);
}
.categories>li .card,.category>ul>li .card {height: 60px;}
.categories>li .card p,.category>ul>li .card p {margin-bottom: 10px;}
/* tags page */
.tag-item {color: var(--colorFg);}
.tag-topic-count {color: var(--colorHl);}
/* users page */
.nav>li>a:focus,.nav>li>a:hover,.nav-pills>li.active>a,
.nav-pills>li.active>a:focus,.nav-pills>li.active>a:hover {
    color: var(--colorHlFg);
    background-color: var(--colorHl);
}
.status.offline {color: gray;}
/* groups page */
.panel {
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.panel-default>.panel-heading {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
/* groups profiles */
.posts-list .posts-list-item .post-body {border-right-color: var(--colorHl);}
.table-striped>tbody>tr:nth-of-type(odd) {background-color: var(--colorBg);}
.table>tbody>tr>td,.table>tbody>tr>th,.table>tfoot>tr>td,.table>tfoot>tr>th,
.table>thead>tr>td,.table>thead>tr>th {
    border-top-color: var(--colorBg);
}
.table-hover>tbody>tr:hover {background-color: var(--colorBgFaded);}
/* threads */
.topic .posts>li a {color: var(--colorHl);}
.topic .posts>li a:hover {color: var(--colorFg);}
.topic .posts .content .toggle {
    background-color: var(--colorBg);
    border-color: var(--colorBg);
}
.edit-icon {color: var(--colorHl);}
small.pull-right a {color: var(--colorHl) !important;}
.votes a {background-color: var(--colorBgDarker);}
.topic [component="post/downvote"].downvoted,
.topic [component="post/upvote"].upvoted {
    color: var(--colorHlFg) !important;
    background-color: var(--colorHl);
}
.topic .threaded-replies .replies-last {color: var(--colorFg);}
.topic [component="post/parent"] {background-color: var(--colorBgDarker);}
.topic .post-signature {
    color: var(--colorFgFaded);
    border-top-color: var(--colorAc);
}
/* user profiles */
.account .cover .avatar-wrapper {border-color: var(--colorAc);}
.account>.row .fullname {color: var(--colorFgBolded);}
.account .profile .account-stats .stat-label {color: var(--colorFgFaded);}
.account .profile .profile-meta {color: var(--colorFgFaded);}
.account .profile .profile-meta strong {color: var(--colorFg);}
/* user profile settings */
.well {
    background-color: var(--colorAc);
    border-color: var(--colorAc);
}
/* footer */
#vivaldifooter a {color: var(--colorFg);}
/* editor */
.composer {background: var(--colorBg);}
.composer .resizer .trigger {
    background: var(--colorAc);
    border-color: var(--colorAc);
}
.composer .formatting-bar .formatting-group li {color: var(--colorFg);}
.composer .formatting-bar .formatting-group li:focus,
.composer .formatting-bar .formatting-group li:hover {
    background-color: var(--colorBgFaded);
}
.composer .write,.composer .preview {
    background: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
div.preview.well a {color: var(--colorHl);}
.taskbar .navbar-nav li.taskbar-composer a,
.taskbar .navbar-nav li.taskbar-composer a:active,
.taskbar .navbar-nav li.taskbar-composer a:focus,
.taskbar .navbar-nav li.taskbar-composer a:hover {
    background: var(--colorHl);
}
.taskbar .navbar-nav li.taskbar-chat a i,
.taskbar .navbar-nav li.taskbar-composer a i {
    color: white;/* doesn't accept variables */
}
/* inputs,menus,buttons,and modals */
.alert-warning,.alert-info {
    color: var(--colorHlFg);
    background-color: var(--colorHl);
    border-color: var(--colorHl);
}
.alert-window .alert {background-color: var(--colorAc);}
.alert-window .alert.alert-info {
    color: var(--colorFg);
    border-color: var(--colorAc);
}
.btn-primary,.btn-default {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.btn-primary:hover,.btn-primary.active,.btn-primary.focus,.btn-primary:active,
.btn-primary:focus,.btn-primary:hover,.open>.dropdown-toggle.btn-primary,
.btn-default.active,.btn-default.focus,.btn-default:active,.btn-default:focus,
.btn-default:hover,.open>.dropdown-toggle.btn-default {
    color: var(--colorFg);
    background-color: var(--colorBgFaded);
    border-color: var(--colorBgFaded);
}
.btn-info {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.btn-info.active,.btn-info.focus,.btn-info:active,.btn-info:focus,
.btn-info:hover,.open>.dropdown-toggle.btn-info {
    color: var(--colorFg);
    background-color: var(--colorBgFaded);
    border-color: var(--colorBgFaded);
}
.fab {background-color: var(--colorHl);}
.btn-group.account-fab .fab {color: var(--colorFg);}
ul.dropdown-menu.dropdown-menu-right a,
ul.dropdown-menu.dropdown-menu-right a:hover {
    color: var(--colorFg);
}
a.list-group-item,.list-group-item {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
a.list-group-item:focus,a.list-group-item:hover {
    color: var(--colorFg);
    background-color: var(--colorBgFaded);
    border-color: var(--colorBgFaded);
}
.close {
    color: var(--colorFg);
    text-shadow: initial;
    opacity: 0.6;
}
.close:focus,.close:hover {color: var(--colorFg);}
.dropdown-menu {
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.dropdown-menu>li>a {color: var(--colorFg);}
.dropdown-menu>li>a:focus,.dropdown-menu>li>a:hover {color: var(--colorFg);}
.dropdown-menu .divider {background-color: var(--colorBg);}
.navbar-default .navbar-nav>.open>a,.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover {
    background-color: var(--colorBgDarker);
}
.header .notif-dropdown-link {border-color: var(--colorBg);}
.header .notification-list li.unread,
.slideout-menu .notification-list li.unread {
    background-color: var(--colorHl);
}
.dropdown-menu>li>a:focus,.dropdown-menu>li>a:hover {
    background-color: var(--colorBgFaded);
}
.header .chat-list,.slideout-menu .chat-list {color: var(--colorFg);}
.chats-list>li .avatar-placeholder {background: var(--colorBg);}
.chats-list,.header .chat-list>li:not(:last-child),
.slideout-menu .chat-list>li:not(:last-child) {
    border-color: var(--colorBg);
}
.header .chat-list>li:hover,.slideout-menu .chat-list>li:hover {
    background: var(--colorBgFaded);
}
.chats-list>li {border-color: var(--colorBg);}
.pagination>li>a,.pagination>li>span,.pagination>.disabled>a,
.pagination>.disabled>a:focus,.pagination>.disabled>a:hover,
.pagination>.disabled>span,.pagination>.disabled>span:focus,
.pagination>.disabled>span:hover {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.pagination>li>a:focus,.pagination>li>a:hover,.pagination>li>span:focus,
.pagination>li>span:hover {
    color: var(--colorFg);
    background-color: var(--colorBgFaded);
    border-color: var(--colorBgFaded);
}
.pagination>.active>a,.pagination>.active>a:focus,.pagination>.active>a:hover,
.pagination>.active>span,.pagination>.active>span:focus,
.pagination>.active>span:hover {
    color: var(--colorHlFg);
    background-color: var(--colorHl);
    border-color: var(--colorHl);
}
.form-control {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.form-control:focus {
    border-color: var(--colorBgDarker);
    box-shadow: 0 0 4px var(--colorAc);
}
.bootstrap-tagsinput {
    color: var(--colorFg);
    background-color: var(--colorAc);
    border-color: var(--colorAc);
    box-shadow: 0 1px 1px var(--colorAc);
}
.input-group-addon {
    color: var(--colorFg);
    background-color: var(--colorBgDarker);
    border-color: var(--colorBgDarker);
}
.modal-content {background-color: var(--colorBgDarker);}
.modal-header {border-color: var(--colorAc);}
}
