css:
@-moz-document domain(weibo.com)
{
/* 2019-05-18: version 0.13 */
:root {
    --lightcolor: #FFFFEF;
    --bgcolor: #F9F9E9;
    --darkcolor: #E9E9D9;
    --blackcolor: #DFDFCF;
}

body,
.WB_global_nav,
.S_bg2,
.W_btn_b,
.W_input,
.SW_fun_bg,
.W_layer .content,
.W_tips.tips_warn,
.WB_global_nav .gn_topmenulist,
.WB_editor_sidebar,
.WB_editor_iframe,
.WB_editor_iframe_new {
    background-color: var(--bgcolor);
}

blockquote,
.S_bg1,
.SW_fun_bg:hover,
.SW_fun_bg_active,
.main_editor .preface {
    background-color: var(--darkcolor);
}

.send_weibo .input .W_input,
.send_weibo .input_simple .W_input,
.W_layer .W_input,
.W_layer .send_weibo .inpu,
.WB_global_nav .gn_search_v2 .placeholder,
.WB_global_nav .gn_search_v2 .W_input,
.WB_editor_main .main_box {
    background-color: var(--lightcolor);
}

.S_bg3 {
    background-color: var(--blackcolor);
}
}
