css:
@-moz-document url-prefix("https://bbs.feng.com/") {
.wrap, .wp { width:1200px!important; position:relative!important;}
body { background:#ececec!important;}
.logo img { max-width:80px!important; }
.xin_global_topbar { height:50px; text-align:left!important;}
.xin_global_topbar .topbarBox { height:50px; line-height:50px; font-weight: 400!important;}
.xin_global_topbar .topbarBox .server .sign_btn li a {font-size:12px!important;}
.xin_global_nav { position:absolute!important; top:0px!important; background:none!important; box-shadow:none!important; border-bottom:none!important; border-top:none!important; width:756px!important; left:50%!important; margin-left:-378px!important; margin-top:2px!important;}
.xin_global_nav.fixed { background:#ffffff!important; width:100%!important; left:0px!important; margin-left:0px!important; box-shadow: 0 2px 4px 0 rgba(0,0,0,.15)!important;}
.xin_global_nav .nav_list { margin:0px!important;}
.xin_global_nav .nav_list .drop_li a { font-weight:400!important; font-size:12px!important;}

.quick_services {display:none!important;}
div[style="display: block;background:#fff;box-shadow: 0 1px 3px #BCBCBC;margin: 0 auto 10px;padding: 10px 0 5px;overflow: hidden;text-align: center;"]  {display:none!important;}
.bbs_info {display:none!important;}
.fl_g .last_post {display:none!important;}
div[style="width: 72px;"] { width:50px!important;}
.fl_icn_g img { width:50px!important; height:50px!important;}
.fl_g dl {margin-left:50px!important;}
.bm_h { background:#74889b; border-radius:0px!important;}
.fl_tb .fl_icn a img { width:50px; height:50px!important;}
td[style="width: 72px;"] { width:50px!important;}
.fl_tb .xg2, .fl_by {display:none!important;}
.fl_tb h2 {font-size:12px!important;}
.fl_tb .count { font-weight:normal!important;}
a[style="color: #FF0000;"] { color:#2d6aef!important;}
.fl_i { font-size:12px!important;}
.switch_btn_d { display:none!important;}
.footer_box .footer_inner .footerLeft img { width:50%!important; top:0px!important;}
.footer_box .footer_inner .footerLeft { height:60%!important; top:0px!important;}
.footer_box .footer_inner .footerRight .ours_links { margin:5px 0!important;}
.footer_box .footer_inner { display:none!important;}
.footer_box .touch a, .lk { display:none!important;}
.footer_box .touch p { float:inherit!important;}

.forum_info, .forum_rules .s_title { display:none!important;}
.new_thread, .new_thread span, .new_thread i { background:#74889b; height:36px!important;}
.new_thread { height:36px!important; line-height:36px!important; font-size:14px!important; padding:0px!important; overflow:hidden!important; border-radius:4px!important; text-shadow:none!important; margin-top:6px!important;}
.pg a, .pg strong, .pgb a, .pg label { border-radius:2px!important;}
.ttp a, .ttp span, .ttp strong { background:none!important; text-shadow:none!important; font-size:12px!important;}
.ttp li { padding-right:0px!important;}
.xi2, .xi2 a, .xi3 a { font-size:12px!important;}
.tl th, .tl td.fn { font-weight:300!important;}
a[style="color: #EE1B2E;"] { font-weight:300!important; color:#74889b!important; font-size:12px!important;}
a[style="font-weight: bold;color: #EE1B2E;"] { font-weight:300!important; color:#74889b!important; font-size:12px!important;}
a[style="font-weight: bold;color: #2897C5;"] { font-weight:300!important; color:#74889b!important; font-size:12px!important;}
a[style="color: #EC1282;"] { font-weight:200!important; color:#74889b!important; font-size:12px!important;}
.tl th img, .tl td img {opacity:0.3!important;}
.tl th em, .tl th em a, .tl th em a:visited { color:#74889b!important; font-size:12px!important;}
.tl .bm_c th {font-size:12px!important;}
.pn em, .pn span, .pn strong {background:#fff!important; color:#74889b!important; font-size:12px!important; border:0px solid #74889b!important;}
.t_f, .t_f td { color:#666!important; font-size:12px!important;}
#postlist img {opacity:0.3!important; display:none;}
.pls p, .pls .pil {display:none!important;}
.pls .favatar { padding-bottom:0!important;}
.pls_panel .user_props { display:none!important;}
.t_fsz { min-height:0px!important;}
.pls_panel .authi { background:#fff!important; padding:5px 15px!important; border-radius:4px; border:1px solid #eee!important; position:relative!important; top:-8px!important; box-shadow:2px 2px 0 #e6e6e6;}
.pls .pi a { font-size:12px!important;}
li[style="text-indent:2px;width:135px;"] {display:none!important;}
.pls .o .pm2 { background-position: 2px 0px!important; text-indent:22px!important;}
.pct, #p_btn { padding:0px!important;}
.mbw { margin:0px!important;}
.cy_btn { width:60px!important; height:60px!important;}
.cy_btn .circle { width:56px!important; height:56px!important;}
.cy_btn .icon { width:34px!important; height:34px!important; background-position:-4px -698px!important; margin-top:-4px!important;}
.post_reply {padding-left:40px!important;}
.avtm img { opacity:0.3!important;}
}
