css:
@-moz-document url-prefix("https://www.tumblr.com/") {
.post-typography-update .post.is_conversation .post_content .post_title, .post-typography-update .post.is_regular .post_content .post_title, .post-typography-update .post .post-content .title-field *, .reblog-title 

{ font-family: \"Helvetica Neue\", Helvetica, sans-serif !important; font-size: 23px !important; font-weight: bold !important; line-height: normal !important; min-height: 25px !important; } 

.post_title { font-size: 23px !important; line-height: normal !important; font-weight: bold !important; font-family: \"Helvetica Neue\", Helvetica, sans-serif !important; }
    
.post .post_content h2 { font-size: 18px !important; line-height: normal !important; font-weight: bold !important; font-family: \"Helvetica Neue\", Helvetica, sans-serif !important; }
}
