css:
@-moz-document url-prefix("https://cs50.me") {
/* CS50 ME */
html,
body {
	background: #222;
	color: #ddd
}

.sidebar-title {
	color: #ddd
}

.sidebar-title:focus,
.sidebar-title:hover {
	color: #fff
}

.nav>li>a:focus,
.nav>li>a:hover {
	background: none;
}

.problem-link-disabled {
	color: #ddd
}

.well {
	background: #111;
}

.form-control,
.form-control:focus,
.form-control[disabled] {
	background: #111;
	color: #ddd;
	border-color: #1ca8dd;
}

select.submission-select,
a.btn.btn-xs.btn-pill.btn-default {
	background: #000;
	color: #ddd;
	border-color: #1CA8DD;
}
}

@-moz-document url-prefix("https://docs.cs50.net") {
/* CS50 PSETS */
html, body {
	background: #222;
	color: #ddd;
}

a {
	color: #42b883;
}

a:hover, a:focus {
	color: #3c763d;
}

#toctitle {
	color: #ba3925;
}

.highlight {
	color: #222222;
}

.listingblock .pygments,
.listingblock .pygments code {
	background-color: #000000;
	color: #dee2e6;
}
}

@-moz-document url-prefix("https://lab.cs50.io") {
/* CS50 LAB */
html,
.markdown-body {
	color: #ddd;
	background-color: #222;
}

.markdown-body .highlight pre,
.markdown-body pre {
	background-color: #000000;
	color: #dee2e6;
}

.highlight .o {
	color: #f06d6d;
}

.Popover-message.Popover-message--top-left.p-4.Box.box-shadow-extra-large.bg-gray-light {
	background-color: #000000 !important;
	color: #dee2e6;
}

.markdown-body table th,
.markdown-body table td {
	color: #222;
}

a {
	color: #42b883;
}
}

@-moz-document url-prefix("https://cs50.readthedocs.io") {
/* CS50 DOCS */
.wy-nav-content, .wy-nav-content-wrap {
	background: #404040 !important;
	color: #dddddd
}

pre {
	color: #ddd;
	background: #343131;
}

.highlight .cp {
	color: #11ad3e;
}

.highlight .nf {
	color: #338cff;
}

.highlight .s {
	color: #42aed0;
}

.highlight .k {
	color: #27ae60;
	font-weight: bold;
}

.highlight .mi {
	color: #e67e22;
}

.highlight .se {
	color: #d3d655;
	font-weight: bold;
}

.highlight .o {
	color: #c807ff;
}

.highlight .kt {
	color: #ff4009;
}

.admonition-title {
	background: #3e805f !important;
}

.wy-alert.wy-alert-info, .rst-content .note, .rst-content .wy-alert-info.attention, .rst-content .wy-alert-info.caution, .rst-content .wy-alert-info.danger, .rst-content .wy-alert-info.error, .rst-content .wy-alert-info.hint, .rst-content .wy-alert-info.important, .rst-content .wy-alert-info.tip, .rst-content .wy-alert-info.warning, .rst-content .seealso, .rst-content .wy-alert-info.admonition-todo, .rst-content .wy-alert-info.admonition {
	background: #343131;
}

.highlight .s2 {
	color: #61e1ff;
}

.wy-menu-vertical li.current a:hover {
	background: #404040;
	color: #ddd;
}

.wy-menu-vertical li.toctree-l2 a, .wy-menu-vertical li.toctree-l3 a, .wy-menu-vertical li.toctree-l4 a {
	color: #e7f2fa;
}

.wy-menu-vertical li.current {
	background: rgba(231, 76, 60, 0.35);
}

.wy-menu-vertical li.toctree-l2.current>a {
	background: #2980b9;
	padding: .4045em 2.427em;
}

.wy-menu-vertical li.toctree-l1.current>a {
	background: #999;
}

.wy-menu-vertical li.toctree-l2.current li.toctree-l3>a {
	background: rgb(115, 59, 53);	
}
}
