css:
@-moz-document domain(web.telegram.org) {
html {
    background-color: #1d2733;
}

.im_dialogs_col_wrap {
    background-color: #1d2733;
}

.dialogMessage.peerID,
.im_dialog_peer {
    color: #ffffff;
}


/* Site header "Telegram" */
.tg_head_split {
    background-color: #222e3c;
}

.dropdown.open .tg_head_btn,
.tg_head_btn:hover {
    background-color: #222e3c;
}


/* Chatlist - chat hover */
.im_dialog_wrap:hover,
.im_dialog_wrap:active {
    background-color: #151e27;
}

.im_dialogs_scrollable_wrap a.im_dialog:hover,
.im_dialogs_scrollable_wrap a.im_dialog_selected {
    border-radius: 0;
    background: #151e27;
}


/* Unread messages bubble text color */
.im_dialog_badge,
.badge,
.im_dialog_badge_muted {
    background-color: #59a8e1;
    color: #ffffff;
}

.im_dialog_badge_muted {
    background-color: #7d8e98;
    color: #000000;
}

.im_page_wrap {
    background-color: #151e27;
    border-color: #151e27;
    box-shadow: none;
    border-left: 1px solid #151e27;
    border-right: 1px solid #151e27;
    border-bottom: 1px solid #151e27;
}

.im_dialogs_col_wrap {
    border-right: 1px solid #151e27;
}

.im_history_pinned_wrap {
    border-bottom: 1px solid #0e141a;
}

.im_message_text,
.im_message_webpage_description {
    color: #ffffff;
}

.im_message_reply_body {
    color: #ffffff;
}


/* Caption text color */
.im_message_document_caption,
.im_message_photo_caption,
.im_message_video_caption {
    color: #ffffff;
}

/* Media */

.im_message_body_media .im_message_document,
.im_message_body_media .im_message_audio {
    background-color: #1d2733;
}

/* Scrollbar */
.im_history_col .nano > .nano-pane > .nano-slider {
    background-color: #1b6189;
}

.im_history_col .nano > .nano-pane {
    background-color: #124763;
}


/* Search bar color */
.im_dialogs_search {
    background-color: #1d2733;
}

.btn-md-primary:focus,
.btn-md-primary:hover,
.btn-md:hover {
    background-color: #124763;
}

.im_dialogs_search_field,
.im_dialogs_search,
.im_dialogs_search_field:focus,
.im_dialogs_search:focus,
.im_dialogs_modal_col_wrap .im_dialogs_search_field {
    border-color: #1d2733 !important;
    border: 1px solid #1d2733;
    border-radius: 2px;
    padding: 6px 26px 6px 30px;
    margin: 0;
    background-color: #1d2733;
    color: #ffffff;
}


/* Mobile version text field */

.composer_rich_textarea,
.composer_rich_textarea:empty,
.composer_rich_textarea:active,
.composer_rich_textarea:focus {
    background-color: #1d2733;
    border-color: #151e27;
    color: #ffffff;
}

/* Chat list chat item */
.im_dialogs_scrollable_wrap a.im_dialog {
    border-color: #151e27;
}

/* Mobile version header color */
.tg_page_head .navbar-inverse {
    background-color: #222e3c;
}

/* User profile color */
.md_modal_head {
    background-color: #222e3c;
}

.md_modal_footer,
.md_modal_footer_empty {
    background-color: #222e3c;
}

.md_modal_body {
    color: #ffffff;
    background-color: #1d2733;
}

.md_modal_section_splitter,
.divider {
    box-shadow: inset 0 1px 1px #151e27;
    border-top: 1px solid #151e27;
    background-color: #151e27;
}

.md_modal_iconed_section_wrap {
    border-bottom: 1px solid #151e27;
}

.md_modal_split_action {
    background-color: #59a8e1;
    color: #ffffff;
}


/* Icons */
.md_modal_split_action > i {
    filter: invert(33%);
}

.tg_checkbox_label {
    color: #ffffff;
}

.tg_radio > span {
    color: #ffffff;
}

.dropdown-menu,
.tg_head_logo_dropdown .dropdown-menu {
    background-color: #222e3c;
    border-left-color: #151e27;
    border-right-color: #151e27;
}

.dropdown-menu > li > a,
.dropdown-menu > li > a:hover {
    color: #3a6d99;
}

.tg_head_peer_dropdown .dropdown-menu > li > a {
    background-color: #222e3c;
    color: #e0e0e0;
}

.tg_head_peer_dropdown .dropdown-menu > li > a:hover {
    background-color: #151e27;
    color: #e0e0e0;
}

.nav > li > a:hover,
.nav > li > a:focus,
.nav .open > a,
.nav .open > a:hover,
.nav .open > a:focus,
.nav-pills > li.active > a,
.nav-pills > li.active > a:hover,
.nav-pills > li.active > a:focus,
.dropdown-menu > li:hover,
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus,
.dropdown-menu > li.active > a {
    background-color: #151e27;
    color: #e0e0e0;
}

.dropdown-menu > li > a, .dropdown-menu > li > a:hover {
    color: #e0e0e0;
}


/* Select messages */
.im_message_selected > div.im_message_outer_wrap,
.im_message_selected > div.im_message_outer_wrap:hover,
.im_history_select_active .im_message_outer_wrap:hover {
    background-color: #131a22;
}

.im_history_select_active .im_message_outer_wrap:hover .icon-document,
.im_message_selected .icon-document {
    background-color: #131a22;
}

.im_message_document_thumb_wrap {
    background-color: #131a22;
}

.im_message_unread_split {
    background-color: #131a22;
}


/* Textfield */
.composer_rich_textarea {
    color: #ffffff;
}

.im_send_dropbox_wrap {
    background-color: #151e27;
}

/* Voice recording button background */
.im_record_bg {
    background-color: transparent;
}


/* Emoji Panel */
.composer_emoji_tooltip_content,
.composer_emoji_tooltip_content_emoji,
.composer_emoji_tooltip_tabs,
.composer_emoji_tooltip_tabs_wrap {
    background-color: #131a22;
}

.composer_emoji_tooltip {
    background-color: #131a22;
    border: none;
}

.composer_dropdown_wrap {
    background-color: #151e27;
}

[class="composer_emoji_tooltip_category"] i {
    filter: invert(100%);
}

a.composer_emoji_btn:hover {
    background-color: #0e141a;
}

.composer_dropdown li a:hover,
.composer_dropdown li.composer_autocomplete_option_active a {
    background-color: #0e141a;
}

.footer_wrap {
    color: #1d2733;
}

.im_history_pinned_panel {
    border-bottom: 1px solid #151e27;
}


/* Modal window (e.g. Edit group name) */
.md_simple_modal_wrap,
.modal_simple_form > h4,
.md_simple_modal_body > h4,
.md_simple_modal_body .tg_checkbox > span,
.md-input {
    background-color: #1d2733;
    color: #ffffff;
}

.tgme_footer,
.tgme_page_embed_code,
.tgme_page_web_action a.tgme_action_button,
.fixed_actions .tgme_page_widget_actions,
.tgme_widget_message_bubble {
    border-color: #151e27 !important;
}

.im_edit_panel_border {
    border-bottom: 1px solid #0e141a;
}

.composer_stickerset_title {
    color: #ececec;
}

.composer_sticker_btn:hover {
    background-color: #0e141a;
}

.reply_markup_button {
    background-color: #1d2733;
    color: #ffffff;
}


/*-------------------- fixes --------------------*/
.im_page_wrap,
.composer_rich_textarea:empty,
.form-control {
    overflow: hidden;
}

.mobile_scrollable_wrap {
    overflow-y: hidden;
}

.im_dialogs_search_field {
    background-position: -6px -204px;
}

.page_wrap {
    background-size: cover!important;
}

.im_message_body {
    padding: 7px 10px;
}

.im_message_views {
    right: 7px;
}
.im_history_col .nano > .nano-pane {
    background-color: #124763;
}

.im_history_col .nano > .nano-pane > .nano-slider {
    background-color: #1b6189;
}

body {
    color: #999;
}

code {
	background-color: #11171e;
	color: #a3a3a3;
}

pre {
    margin-bottom: 0;
    overflow: auto;
    padding: 3px;
    border: 1px solid #11171e;
    max-height: none;
    font-size: inherit;
}

pre {
    display: block;
    color: #a3a3a3;
    background-color: #11171e;
    border-radius: 4px;
}
}
