/*		THEME DEEZER Deep Dark 'v1.0
/*
/*		by Senka
/*
/************************ GLOBAL ***********************/
/* i really want this to be global */

.naboo {background-color:black;}

/******************** HEADER **************************/
/* search bar */
.page-topbar .topbar-search-input {background-color:#303030;height:30px;}
.topbar-search-form {background:#303030;border-radius:40px;padding:2px;}
.topbar-search-form:hover {background: linear-gradient(45deg, rgb(0,143,104), rgb(250,224,66));}
.topbar-search-form:hover input {background:black;}
.page-topbar .topbar-search-input:hover {background-color:black;}


.page-topbar .popper-search .search-heading {color:#7d7d87;}
.page-topbar .popper-search .search-history-label {color:#7d7d87;}
.page-topbar .popper-search .search-history-icon {fill:#303030;}

.page-topbar .popper-search .search-history-link:hover {background-color:#303030;}
.page-topbar .popper-search .search-history-link:hover span {color:lightgray;}
.page-topbar .popper-search .search-history-link:hover svg {fill:lightgray;}
.popper-search {background-color:black;box-shadow:0 2px 20px 0 rgba(137,215,187,.25);}

.topbar-search-form.is-active {background: linear-gradient(to right, #008f68 0%,#fae042 90%);}
.topbar-search-form.topbar-search-active {color:red;background:linear-gradient(to right, #f1341a 0%,#ffb400 90%);}
.topbar-search-form.topbar-search-active.is-active {color:blue;background:linear-gradient(to right, #008f68 0%,#fae042 90%);}

.topbar-search-form.is-active .topbar-search-input {background-color:black;color:white;}
.topbar-search-form.topbar-search-active .topbar-search-input {background-color:black; color:#f26629}
.topbar-search-form.topbar-search-active.is-active .topbar-search-input {background-color:black; color:white;}
.page-topbar .topbar-search-clear, .page-topbar .topbar-search-submit {height:30px;margin-top:2px;margin-right:0.12em;}
.page-topbar .topbar-search-active .topbar-search-submit {background-color:#303030;}

.topbar-search-form.topbar-search-active.is-active .topbar-search-submit:hover {background-color:#fae042;}
.topbar-search-form.topbar-search-active .topbar-search-submit:hover {background-color:#ffb400;}
.page-topbar .topbar-search-active .topbar-search-submit:hover svg {fill:black;}

/*------*/

.topbar-notification svg {fill:gray;}
.page-topbar .topbar-notification:hover {background:#303030;}
.page-topbar .topbar-notification:hover svg {fill:gray;}
.page-topbar {background-color:black;border-bottom:1px solid #292929;}

/* COMPTE */
.page-topbar .popper-account .account-heading {color:lightgray}
.page-topbar .popper-account .account-header:hover {background-color:#303030;}
.page-topbar .popper-account .account-header:hover .account-name {color:white;}

.popper-content.popper-account.popper-lg {background:black;box-shadow:0 2px 20px 0 rgba(0,127,235,.4);}
.page-topbar .popper-account .account-item.is-divider, .page-topbar .popper-account .account-menu {border-top:1px solid #303030;}
.page-topbar .popper-account .account-link:hover {background-color:#303030;}
.page-topbar .popper-account .account-link.is-main {color:gray}
.page-topbar .popper-account .account-link.is-main:hover {color:lightgray;}
.page-topbar .popper-account .account-link {color:lightgray}
.page-topbar .popper-account .account-link:hover {color:white;}
.popper[x-placement=bottom] .popper-arrow {border-bottom-color:black;}


/******************** CONTENT *************************/

/* Titre */
a.link-animated {color:grey;}
.heading-1 {color:#7d7d87;}
.heading-2 {color:grey;}
.heading-4, .heading-4 a {color:white;}
.catalog-header .header-info .heading-link {color:gray;}

/* Carousel */
.slide.slide-cut {border:1px solid #303030;}
.slide.slide-cut .slide-background {background-color:black;}
.slide.slide-cut .slide-covers:after {background-color:black;border-left:1px solid black}
.flow-logo-svg path {fill:#7d7d87;}
.slide.slide-cut .slide-text .title {color:#7d7d87;}
.slide.slide-cut .slide-play-icon .action-item-btn {background-color:rgba(40,40,40,0.89);color:lightgray}

.carousel-control {background-color:black;border:1px solid #303030;}
.carousel-control:hover:not(.disabled) {background-color:#303030;}

/* TOPS */
.slide-text .title, .slide-text .subtitle.ellipsis {color:#7d7d87;}
.slide .slide-btn {border:1px solid #7d7d87;color:#7d7d87;}
.large-card .track .track-number {color:#7d7d87;}
.tracklist > li .heading-5 > a {color:lightgray;}
.tracklist > li .heading-5 > .heading-5-sub a {color:#007feb}

/* Radio */
.thumbnail-half-rounded .picture-img {background-color:#303030;}

/* Page Artiste */
.heading-1.ellipsis {color:lightgray;}

.navbar {background-color:black;}
.navbar-item.active .navbar-link {color:lightgray;}
.navbar-link:hover {color:white;}

    /* Discographie */
    #page_naboo_artist .content-left {background-color:rgba(255,255,255,0);box-shadow:0 0 6px rgba(255,255,255,0.2);}

    .btn-view-more {background-color:#303030;} /* grand bouton */
    .btn-view-more:hover {background:gray;}
    .btn-view-more:hover .label {color:black;}
    .btn-view-more:hover:after, .link-animated:hover:after {color:black;}
    .catalog-tab-discography .heading .cell-line .line {background-color:#303030;} /* Ligne */

    /* top titre */
    .datagrid-header .datagrid-row {background-color:#303030;}
    .datagrid-header .datagrid-row:after, .datagrid-header .datagrid-row:before, .datagrid-row:after, .datagrid-row:before {background-color:#303030;}
    .datagrid-header .datagrid-row:hover {/*background:gray;*/background-color:#303030;border:2px solid gray;}
    /*.datagrid-header > .datagrid-row:hover:before {background:gray;}
    .datagrid-header > .datagrid-row:hover:after {background:gray;}*/
    .datagrid-header .datagrid-row:hover .datagrid-label {color:lightgray;}
    .datagrid-header .datagrid-row:hover .datagrid-header .is-sortable .icon, .datagrid-header .is-sortable .svg-icon {fill:lightgray;}
    .datagrid-note {background-color:#32323d;}
    .datagrid-note.active {background-color:#c2c2ca;}

    /* Com */
    .catalog-tab-comment .post-comment .form-control {background-color:black;border:2px solid gray;}
    div.comment-message {background-color:black!important;border:1px solid #303030;}
    .catalog-tab-comment .message-author {color:gray;}

    /* search */
.datagrid-toolbar .toolbar-search .form-control {background-color:#303030;border:none;}

/* Page MA MUSIQUE */
#page_profile {background-color:black;}
#page_profile .profile-header .header-main .list-actions {border-left:1px solid #303030;}
.thumbnail-grid-responsive .thumbnail-col .thumbnail-viewmore {background-color:black;border:1px solid #303030;}
.thumbnail-grid-responsive .thumbnail-col .thumbnail-viewmore .thumbnail-viewmore-label {color:gray;}
.thumbnail .picture {background-color:#303030}
img[src="https://e-cdns-images.dzcdn.net/images/user/d41d8cd98f00b204e9800998ecf8427e/264x264-000000-80-0-0.jpg"],img[src="https://e-cdns-images.dzcdn.net/images/artist/d41d8cd98f00b204e9800998ecf8427e/264x264-000000-80-0-0.jpg"] {content: url("https://i.ibb.co/0Y9GGjP/264x264-000000-80-0-0.jpg");}

img[src="https://e-cdns-images.dzcdn.net/images/cover//264x264-000000-80-0-0.jpg"], img[src="https://e-cdns-images.dzcdn.net/images/cover/d41d8cd98f00b204e9800998ecf8427e/264x264-000000-80-0-0.jpg"], img[src="https://e-cdns-images.dzcdn.net/images/artist/d41d8cd98f00b204e9800998ecf8427e/86x86-000000-80-0-0.jpg"] {content: url("https://i.ibb.co/0Y9GGjP/264x264-000000-80-0-0.jpg");}

.create-assistant-container:hover {background-color:#303030;border: 1px dashed white;}
#page_profile .user-search .form-control {background-color:#303030;border:none;}
.dropdown-menu, .dropdown-item a, .dropdown-item button {background-color:#303030;border:none;}
.dropdown-item a, .dropdown-item button:hover {background-color:gray;color:black;border:none;}

.thumbnail-caption .heading-4 a:hover {color:#007feb;text-decoration:none;}

.svg-icon-love-outline.is-active .svg-path {fill:#9c00ff;stroke:#9c00ff;}

.popover {background-color:#303030;border:1px solid black;box-shadow:0 2px 20px 0 rgba(0,0,0,1);}
.popover-left:before {border-left-color:black} .popover-left:after {border-left-color:#303030;} /* petite fleche qui m'a fait grave chier */
.popover:before {border-right-color:black} .popover-right:after {border-right-color:#303030;}
.popover .popover-header {border-bottom:1px solid gray;}
.popover .popover-header .heading-4 a:hover {color:#007FEB;text-decoration:none;}
.page-contextmenu .menu-item {background:transparent;color:gray}
.page-contextmenu .menu-item:hover {background:rgba(255,255,255,0.1);color:lightgray}
.page-contextmenu .menu-item:hover .icon {color:lightgray;}
.page-contextmenu .menu-item.link {background-color:rgba(0,0,0,0.3);}
.page-contextmenu .menu-item.link:hover {background-color:rgba(255,255,255,0.2);}

.form-control {background-color:rgba(0,0,0,0.4);border:none;}



/******************** SIDEBAR LEFT ********************/
.page-sidebar {background-color:black;border-right:1px solid #292929}
.logo.logo-deezer-black {background-image:url(https://i.ibb.co/sj3wB2d/deezer-white.png)}
.sidebar-nav-link:hover {background-color:#303030;}
.sidebar-nav-link.is-main:hover {background-color:#303030;}
.page-sidebar .sidebar-nav-link:hover {background-color:#303030;}
/********************* PLAYER *************************/
.page-player .player-bottom {background-color:black;border-top:1px solid #292929;}

/* Button */
.svg-icon.svg-icon-next, .svg-icon.svg-icon-pause, svg-icon.svg-icon-play, .svg-icon.svg-icon-prev {fill:lightgray;} /* Prev + Play + Pause + Next */
.svg-icon-group-btn[disabled], .svg-icon-group-btn[disabled] .svg-icon.svg-icon-prev {fill:#303030;opacity:1} /* Prev quand d√©sactiv√© */
.svg-icon.svg-icon-add, .svg-icon.svg-icon-love-outline {stroke:lightgray;}
.svg-icon-group-btn:hover {background:#303030;}
.svg-icon {fill:lightgray;stroke:lightgray;}

/* text */
.marquee-content, .track-link {color:grey} /* Titre de la musique */
.page-player .player-options .queuelist-label {color:grey;} /* File d'attente */
.page-player .player-track .track-seekbar .slider-counter {color:grey;} /* Timer track */

/* bar */
.slider-track-default {background-color:#303030;}
.slider-track-active {background:linear-gradient(to right, #fa8914 1%,#c22d65 25%,#2349c1 50%,#2349c1 50%,#469cdd 75%,#1baa58 100%);}
.slider-track-input::-webkit-slider-thumb {background-color:black;border:1px solid #1eaa59;}

.page-player .player-options .option-item+.option-item:before {background-color:#303030;}
.page-player .player-options .queuelist.is-active {background-color:#303030;}
.page-player .player-options .queuelist.is-available:hover {background-color:black;box-shadow:0 0px 4px rgba(255,255,255,.5);}
.page-player .player-options .queuelist.is-available:hover .queuelist-label {color:white;}

/* volume */
.popper-content.popper-player-volume {background-color:#303030;}
.popper[x-placement=top] .popper-arrow {border-top-color:#303030;}
.popper[x-placement=top] .slider-track-active {background:gray;}
.popper[x-placement=top] .slider-track-input::-webkit-slider-thumb {background-color:#303030;border:1px solid gray;}
.popper-content.popper-player-volume .slider-track-default {background-color:black;}
.svg-icon-group-btn.is-active {background-color:#303030;}

/* hover next */
.popper-content {background-color:#303030;box-shadow:0 1px 6px rgba(0,0,0,1)}.page-player .popper-player-preview .preview-link {color:gray;}

/* setting */
/*.popper-content {background-color:#303030;}*/
.page-player .popper-player-settings .setting-label {color:lightgray}
.input-radio {background-color:#303030;border:1px solid lightgray;}
.input-radio:after {background-color:lightgray;}
.page-player .popper-player-settings .setting-menu+.setting-menu {border-top:1px solid;} /* Ligne s√©paratrice*/
.input-switch.is-checked {background-color:black;}
.input-switch:after {background-color:lightgray;}

/********************* File d'attente *************************/
.player-wrapper {background-color:black;}
.datagrid {background-color:black;}

.page-player .queuelist-title {color:gray;}
.page-player .queuelist-header {border-bottom:1px solid #303030;}

.c011.c0110.c0123.dropdown-toggle {background-color:#191919;border-color:#303030;color:lightgray;}
.c011.c0110.c0123.dropdown-toggle:hover {background-color:#303030;border-color:gray;color:white;}
.c0110 {background-color:#303030;color:gray;border:none;}
.c011.c0110.dropdown-toggle:hover {background-color:gray;color:lightgray;border:none;}
.c011.c0110:hover {background-color:gray;color:lightgray;border:none;}
.c0110:not(.c0124).c0123 {background-color:rgba(255,255,255,0.3);}

.datagrid-row {border-left:2px solid transparent;border-right:2px solid transparent;}
.datagrid-row:before {background-color:#303030;}
.datagrid-row.has-contextmenu, .datagrid-row:hover {background-color:#303030;}
.nano>.nano-pane>.nano-slider {background-color:#303030;}

.thumbnail {box-shadow:0 1px 26px rgba(255,255,255,.16);}
.page-player .queuelist-cover-link {color:lightgray;}
.datagrid-label-main {color:lightgray;}

div.datagrid div div.datagrid-cell.datagrid-cell-action.cell-love button svg {stroke:gray;}
div.datagrid div div.datagrid-cell.datagrid-cell-action.cell-love button:hover svg {stroke:#9c00ff;}
.datagrid-action.is-active, .datagrid-action:hover {background:black;}
.page-player .queuelist-upnext-heading {border-bottom:1px solid #303030;}

.input-switch {background-color:#303030;}


/* ADS */
.abp-banner {background-color:black;}

.abp-banner {display:none;}
.conversion-banner {display:none;}
