css:
@-moz-document domain("grommr.com") {
body {
    background-color: #333;
    background-image: none !important;
    color: #FFF;
}

#main {
    background-color: #555;
}

.navt {
    background-image: none !important;
    background-color: #555;
}

.ui-tabs-panel {
    background-color: #777 !important;
    color: #fff;
}

.post.comment {
    background-color: #333;
}

.post-add {
    background-color: #777;
}

img[src="/Content/Tenants/Grommr/Images/banners/premium-alt.png"] {
    border-radius: 15px;
}

#ajax-messages-inbox,
table.messages {
    background-color: #777 !important;
}

.msg-photo,
.msg-from,
.msg-icon,
.msg-subject,
.msg-options {
    background-color: #777;
    color: #FFF !important;
}

#ajax-messages-inbox,
table.messages {
    background-color: #777 !important;
}

#tabs,
.msg-photo,
.msg-from,
.msg-icon,
.msg-subject,
.msg-options {
    background-color: #777;
    color: #FFF !important;
}

a {
    color: #FFF !important;
}

ul[role="tablist"] {
    background-color: #333 !important;
    background-image: none;
}

.popular-list div a {
    color: #FFF;
    background-color: rgba(127, 127, 127, 0.8);
}

.nav-list * {
    color: #000 !important;
}

#newsfeed-updates-bar {
    background-color: #333;
}

#ajax-messages-inbox,
table.messages {
    background-color: #777 !important;
}

#tabs,
.msg-photo,
.msg-from,
.msg-icon,
.msg-subject,
.msg-options {
    background-color: #777;
    color: #FFF !important;
}

#tabs a {
    background-color: #777;
    color: #FFF !important;
}

.t_Tooltip_light * {
    background-color: #555 !important;
    color: #FFF;
}

.slider {
    background-color: #777;
}

.jslider-value {
    background-color: #333 !important;
}

.picker-control input {
    background-color: #777;
    color: #FFF;
    border: 1px solid white;
}

.picker-control button {
    background-color: #777;
    border: 1px solid white;
    color: #FFF;
}

.picker-control .ui-multiselect-menu {
    background-color: #777;
}

.post.sticky {
    background-color: #333 !important;
    background-image: none;
}

.ui-state-default a {
    background-color: #333;
}

.miniprofile {
    background-color: #333 !important;
    background-image: none !important;
}

.photo-thumbnail-sm img:hover {
    transform: scale(3);
    border: 1px solid white;
    z-index: 9999 !important;
    position: absolute;
}
}
