css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://zfilm"), url-prefix("http://zfilm"), domain("zhd.life")
{
.content-video1 {height: 100px !important; margin:0px auto 3px; padding-top: 20px !important;}
  .poster-main1 img {height:70px !important; width:50px !important; }
.filters {
    width: 50%;
    display: flex;
    float: right;
    margin: 0;
}

  .main .main2 noindex {display:none;}
  .header .lo {height: 23px;}

.header,
.header * {
	box-sizing: border-box;
    position: static;
    line-height: normal;
}
.header {
	position: fixed;
	z-index: 999;
	background: #57698b;
	font-family: 'Roboto', sans-serif;
	width: 100%;
	left: 0;
	right: 0;
	top: 0;
	display: flex;
	padding: 0 30px;
    align-items: center;
    justify-content: space-between;
	height: auto;
}
.header .lo{
	order: 1;
    height: 40px;
    position: static;
}
.header .menu{
	order: 2;
	display: flex;
	left: 0;
    margin: 0 auto;
}
.header .menu li{
	display: flex;
    align-items: center;
}
.header .menu>li>a{
	padding: 12px 25px;
}
.header .menu ul{
	top: 100%;
    min-width: 100%;
    border: none;
    width: auto!important;
	padding: 10px 0;
}
.header .menu ul li{
	width: 100%;
}
.header .menu ul li a{
    width: 100%;
    white-space: nowrap;
    padding: 5px 25px 5px 20px;
    position: relative;
}
.header .menu ul li a:after{
	content: '';
	position: absolute;
    left: 0;
    bottom: 0;
    width: 0;
    background: #d06070;
    height: 1px;
    transition: all .1s linear 0.05s;
}
.header .menu ul li a:hover:after{
    width: 100%;
    transition: all .3s linear 0s;
}
.header #search input[type=text]{
	border:none;
    padding: 0;
}
.header #search{
    order: 3;
    padding: 5px 10px;
    position: relative;
    display: flex;
    align-items: center;
    background: #fff;
}
.header .fastreg{
	order: 4;
	padding: 0;
    margin: 0 0 0 auto;
	position: relative;
}
.header .dropdsds{
	order: 5;
	padding: 0;
	position: relative;
}
  #search, .menu, .lo {line-height: 21px;}
  .sub_top{top: 3px;}
  .menu ul{top:21px;}
  .podelitca, .pl1{display: none;}
  .poster-video > em > span, .poster-video > i > span, .poster-video > strong > span {background: RGBA(0, 0, 0, 0.15) !important;;}
  .poster-video>b>a, .poster-video em span, .poster-video i span, .poster-video > strong > span{text-shadow:-1px -1px 0 #000,1px -1px 0 #000,-1px 1px 0 #000,1px 1px 0 #000; color: rgba(255, 255, 255, 1)!important; font-size: 10px; font-weight: normal;}
.poster-video>b>a { background: rgba(89, 184, 131, 0.15);  text-shadow:-1px -1px 0 #000,1px -1px 0 #000,-1px 1px 0 #000,1px 1px 0 #000; color: rgba(255, 255, 255, 1)!important; }

  body {background: #3e424d !important;}
.filter_1>a>.filter_mov, .filter_1>a>.filter_anim, .filter_1>a>.filter_ser { display: none; }
.filter_1 > a > b { font-weight: lighter; }
.ngeres {
	margin: 0;
	left: 0;
    display: flex;
    height: 29px;
    line-height: normal;
    width: 50%;
    float: left;
}
.ngeres>li {
    float: none;
    margin: auto;
}
.dropdown > button { padding: 3px 10px 3px 11px; }
.season-list table tr:hover { background: #5c687e; color: white; }


.favpage-head { background-color: rgb(149, 156, 167); height: 35px; height: 25px;}
body { background: #232323 !important; }
.favpage-foto img { border-radius: 50%; border: 1px solid white; } .favpage-foto > p {display:none;}
#comment { background-color: #c9ccce!important; }
.zcomm { background-color: #d8d8d8!important; }
#dle-comments-list>div:nth-child(even) .zcomm { background-color: #dfdfdf!important; }
.text_spoiler { background-color: #ffffff!important; }

/*filters*/
@media (min-width: 1480px) {
    #xSearchBlockForm { width: 240px; background-color: #d0d0d0; position: absolute; margin-left: -237px; z-index: 995; border-radius: 5px;}
    .openClose .open{border: 4px double black;} .xFieldSearchSubmit input[type=submit] { width: 190px!important; }
}
@media (min-width: 1050px) and (max-width: 1489px) {
    #xSearchBlockForm { width: 540px; background-color: #d0d0d0; border-radius: 5px; padding-left: 220px; }
    .openClose .open{border: 4px double black;} .xFieldSearchSubmit input[type=submit] { width: 490px!important; }
}

.main2 > .favpage-foto > p { content: ""; }
.great_btn { padding: 5px 0; background-color: rgb(119, 119, 119); }

.xFieldSearchContent>.list { background-color: #a6b1be; }

.favpage-head { padding: 1px 5px 5px 60px; } .favpage-menu li a { line-height: 30px; text-transform: none; height: 30px; }
.dropdsds{ padding: 0px 5px 5px;} .fastreg { padding: 0px 5px 6px 6px; } .dropdsds > button, a.fastreg-trig { height: 25px; }
.content {
	margin-top: 15px;
}
.main>.main2>div>table{display:none !important;}
.conteiner>.right-material>#M374500Composite627384{
position: absolute; top: -9999px; left: -9999px;
}
.content-maintop{
	margin-top: 40px;
	width: 100%;
    display: flex;
    padding: 0;
}
.content-maintop:after{
	display: none;
}
.content-maintop li{
	width: 100%;
}
.block-top-maintop{
	width: 100%;
    display: flex;
}
.content-maintop li a{
	padding: 0;
    position: relative;
}
.content-maintop-poster{
	margin: 0;
}
.content-maintop li a h3{
    padding: 10px;
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
    background: rgba(0,0,0,0);
}
.content-maintop li a:hover h3{
    background: rgba(0,0,0,1);
}

.main2{
	width: 100%;
	padding: 0 2%;
    box-sizing: border-box;
}
.filters+.clear{
	display: none;
}
.main{
	width: 100%;
}
.content-video{
	width: 100%;
    display: flex;
	flex-flow: row wrap;
}
.title-h2+ .content-video{
	width: auto;
    margin: -30px -12px 0 -12px;
}
.content-video>li {
    margin: 25px auto auto auto;
    height: auto;
    width: 15%;
}
.poster-main>a>img{
	width: 100%;
    height: auto;
}
.xFieldSearchSubmit{
	padding: 0;
}
#xSearchBlockForm{
	width: 90%;
    box-sizing: border-box;
    padding: 0;
	position: static;
	margin: 0;
}
.xFieldSearchContent>.list>.title{
	display: flex;
    align-items: center;
    justify-content: space-between;
    font-size: 12px;
}

.xFieldSearchContent>.list .pols ul{
    display: flex;
    flex-flow: row wrap;
    margin: 0;
    padding: 0 30px;
}
.xFieldSearchContent .openClose *,
.xFieldSearchContent .openClose{
    box-sizing: border-box;
}
.xFieldSearchContent>.list .pols>ul li{
	display: flex;
    margin: 4px 2px 0px 0px;
}
.xFieldSearchContent>.list .pols label.XSearchfield:hover span,
.xFieldSearchContent>.list .pols label.XSearchfield:hover{
    background: #25405d;
    color: #fff;
}

.xFieldSearchContent>.list .pols label.XSearchfield>input:checked+span:after{
	content: '';
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
    border: 2px solid #25405d;
}
.xFieldSearchContent>.list .pols label.XSearchfield>input:checked+span{
	background: rgba(255,255,255,0);
}
.xFieldSearchContent>.list .pols label.XSearchfield{
	display: flex;
    align-items: center;
    padding: 5px 0px 5px 10px;
    background: #fff;
	position: relative;
}
.xFieldSearchContent>.list .pols label.XSearchfield>span{
    border: none;
    padding: 0 0 0 5px;
    display: flex;
    margin: 0;
}
.xFieldSearchContent>.list .pols label.XSearchfield>input[type=checkbox], .xFieldSearchContent>.list .pols label.XSearchfield>input[type=radio] {
    position: relative;
    margin: 0;
}
[itemtype="http://schema.org/Movie"]{
	width: 100%;
}
.right-material{
	width: calc(100% - 300px)
}

#djasss_pl,
#djasss_pl *{
    box-sizing: border-box;
    display: flex;
}
#djasss_pl{
	width: 100%;
    background: none;
}
.conteiner{
	width: 100%;
}
.title-full, .title-h2,
.h1-span{
	width: 100%;
}
.djasss_pl>iframe, .djasss_pl>object{
	min-width: 100%;
	min-height: 100%;
    margin: 0;
	height: 89vh;
}
.content-video4{
    width: 100%;
    display: flex;
    flex-flow: row wrap;
}
.content-video4>li:nth-of-type(4n),
.content-video4>li{
    margin: 25px auto auto auto;
    width: 21%;
    height: auto;
}
.left-material>div:nth-child(3){
	display:none;
}
.poster-video>img{
	border-radius: 0;
}
.poster-video{
	margin: 0;
}
.playlist-add-area{
	padding: 0 0 25px 0;
}
.favbutts li a{
	width: 100%;
}
.favbutts li{
	padding: 0;
}
hr{
    margin: 10px 0;
}
.favbutts:nth-of-type(2)+hr,
.favbutts:nth-of-type(2){
	display: none;
}
.content-video3{
	display: flex;
    width: 100%;
    flex-flow: row wrap;
}
.info-poster2{
	display: none;
}
.content-video3>li{
    min-height: auto;
    margin: 0;
    width: 50%;
}
.poster-main2>a>img{
	width: 100%;
	height: 100%;
}
.poster-main2{
	display: flex;
}
.poster-main2>em {
    position: absolute;
    top: 10px;
    left: 0;
    display: flex;
    pointer-events: none;
}
.poster-main2>em>span {
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    padding: 3px 10px;
    pointer-events: none;
}
.view-info-content{
	font-size: 11px;
}

.season-area{
	padding: 0;
}

.season-h{
    padding: 5px 15px;
    font-size: 12px;
    height: auto;
    line-height: normal;
    border-bottom: 1px solid;
    display: flex;
    position: relative;
}
.season-h a[data-short]{
	display:none;
}
.season-h a {
    text-align: right;
    min-width: 100px;
    width: 100%;
    margin: 0;
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    bottom: 0;
    display: flex;
    justify-content: flex-end;
    align-items: center;
    padding: 0 20px 0 0;
    box-sizing: border-box;
}
.filter_1{
	width: auto;
	height: auto;
    margin: auto;
    padding: 5px 10px;
}
.filter_1:hover{
    text-decoration: underline;
}
.filter_1 > a > b{
	width: auto;
}
.header .fastreg-area{
	position: absolute;
	top: 100%;
	margin-top: 7px;
    right: 0;
}
.fastreg-area input{
    width: 100%;
    height: auto;
    padding: 5px;
    border-radius: 0;
    border: 0;
    line-height: 1.3;
    font-size: 14px;
}
.header .dropdsds form{
	position: absolute;
	right: 0;
	top: 100%;
	margin-top: 7px;
}
.header>.open>form>.dropdsds-menu{
	display: flex;
	flex-flow: column;
    padding: 10px 20px;
    width: auto;
    min-width: 300px;
}
.dropdsds-menu>li, .dropdsds-menu>li>a{
	width: 100%;
	text-align: center;
    line-height: normal;
    padding: 5px 0;
    display: flex;
    align-items: center;
    justify-content: center;
}
.dropdsds-menu>li label{
	cursor: pointer;
    min-width: 30%;
    text-align: center;
}
.dropdsds-menu>li input[type="password"],
.dropdsds-menu>li input[type="text"]{
	width: 100%!important;
    border-radius: 0!important;

}
.dropdsds-menu>li>a{
	font-size: 10px;
}

.h1-main{
	width: 100%;
	margin: 25px auto 15px auto;
	font-size: 18px;
}
.main_info_j {
    z-index: 20;
    right: 0;
    bottom: 10px;
    width: 20px;
    height: 20px;
    background-size: 80%;
    background-color: #000;
    padding: 5px;
    background-position: center;
    border-radius: 50%;
    opacity: .7;
}
.main_info_j:hover {
    opacity: 1;
}
}
