css:
@-moz-document domain(loj.ac) {
	body {
    background: url("http://file.xdf.cn/uploads/180730/1217_20180730142226.jpg");
    background-size: cover;
}
.ui {
    opacity: 0.95;
}
.ui .label {
    transition: transform 0.1s;
}
.ui .label:hover {
    transform: scale(1.1, 1.1);
    box-shadow: 0 1px 1px #888888;
}
.padding > .ui.very.basic.center.aligned.table {
    border: solid 5px #F2F2F2;
    border-radius: 10px;
    background-color: white;
}
.padding > .ui.very.basic.center.aligned.table > thead {
    background-color: #F2F2F2;
}
.ui.comments{
	border: solid 5px white;
	border-radius: 4px;
	background-color: white;
}
}
