	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.bookings-direction-widget,
.bizlocs .content-view .content-view-hero .bizloc-details .bizloc-view-link,
.embed-header .adr,
.infosheet-header .adr,
.leaflet-control-attribution,
