.bento>a:active,
.bento>div:active,
.bento>button:active,
.route-info-bar .fuel-cost-estimate .fuel-cost-info button.fuel-cost-button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

