.puree-button-primary:active,
a.button.highlight:active,
button.highlight:active,
generic-dialog .button-row .confirm-button:active,
input.orko-button-secondary:active,
input.puree-button-primary:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

