*,::placeholder
  {
    color: #fff!important;
  }

  #filters label:hover,#filters .filter-selection:hover,#backup-buttons .dropdown-content,div#message-box-title,#message-box.center>div,#message-box.danger #message-box-title,#confirm>div,button,input,select,input[type=checkbox]:not(.slider),#notes,body,#search,#manage\.newUI\.sort,#stylus-popup,#help-popup,#message-box>div,#message-box-buttons,#message-box-title,.newUI .entry,.search-result,.CodeMirror,.CodeMirror-gutters,.Codemirror-hints,#header,.search-result-meta,.active #filters-stats,#hotkey-info div,.colorpicker-sliders,.colorpicker-input-container,.CodeMirror-lint-tooltip,.entry.usercss .style-name-link::after,#search-replace-dialog [data-type="status"],#search-replace-dialog>*,.menu-items-wrapper
  {
    background: #000!important;
  }

  .newUI .entry .style-name:hover:before
  {
    background: #0000!important;
  }

  .newUI .entry .style-name:hover:before,.CodeMirror-activeline-background,#backup-buttons .dropdown-content a:hover,.CodeMirror-activeline,.CodeMirror-line::selection,.CodeMirror-line>span::selection,.CodeMirror-line>span>span::selection,:selection
  {
    background: #2b2b2b!important;
  }

  #backup-buttons .dropdown-content a:hover
  {
    background-color: #333!important;
  }

  .newUI .entry:hover,button:not(:disabled):hover
  {
    background: #333!important;
  }

  select,input[type="checkbox"]:not(.slider),input:not([type]),input[type="number"],#backup-buttons .dropdown-content,#message-box.center>div,#confirm>div,button,.search-result,.search-result:hover,.disabled h2::after,.CodeMirror,.newUI .entry,.active,#filters-stats,.Codemirror-hints,#search,#manage\.newUI\.sort,#message-box>div,.CodeMirror-lint-tooltip,body#stylus-options,.entry.usercss .style-name-link::after,#message-box-buttons button,button:not(:disabled),#help-popup,.menu-items-wrapper
  {
    border: 1px solid #FFF!important;
  }
body#stylus-popup {
    border: 2px solid #fff!important;
    border-bottom: 3px solid #fff!important;
}
  .block
  {
    border-bottom: 1px solid #fff!important;
  }

  #search-replace-dialog
  {
    border-radius: 3px!important;
  }

  :focus
  {
    border-color: #fff!important;
  }
.filter-selection select{
    border-color:#000!important;
}
.CodeMirror-focused,[data-focused-via-click] :focus, [data-focused-via-click]:focus
  {
    outline: none!important;
  }

  textarea:focus{
    outline: 1px solid #fff!important;
  }
  #header,#search-replace-dialog,.applies-to
  {
    border-color: #fff!important;
  }

  .svg-icon,.svg-icon.select-arrow,.newUI .entry:hover .svg-icon:hover,.svg-icon:hover,#reset-filters,.svg-icon.checked
  {
    fill: #FFF!important;
  }

  body.dirty #save-button
  {
    filter: grayscale(100%) brightness(40%) sepia(100%) hue-rotate(-50deg) saturate(600%) contrast(.8)!important;
  }

  .CodeMirror-selected
  {
    background: #d9d9d9!important;
  }

  .CodeMirror-focused .CodeMirror-selected
  {
    background: #393939!important;
  }

  .CodeMirror-line::selection,.CodeMirror-line>span::selection,.CodeMirror-line>span>span::selection
  {
    background: #5e5e5e!important;
  }

  .CodeMirror-line::-moz-selection,.CodeMirror-line>span::-moz-selection,.CodeMirror-line>span>span::-moz-selection
  {
    background: #444!important;
  }

  .CodeMirror-activeline-background
  {
    background: #313131!important;
  }

  #manage-text>:not(:last-child):after
  {
    content: ""!important;
  }

  #search-replace-dialog:not(:focus-within):not(:hover)
  {
    opacity: 1!important;
  }

  ::-webkit-scrollbar,::-webkit-scrollbar:horizontal,::-webkit-scrollbar:vertical
  {
    width: 10px!important;
    height: 10px!important;
    background-color: #000!important;
  }

  ::-webkit-scrollbar-corner
  {
    background-color: #000!important;
  }

  ::-webkit-scrollbar-thumb
  {
    background-color: #fff!important;
    border-radius: 5px!important;
    border: 3px solid #000!important;
  }

  ::-webkit-scrollbar-track-piece
  {
    background-color: #000!important;
    box-shadow: inset 0 0 0 #ccc!important;
    border: 0 solid #fff!important;
    border-radius: 5.5px!important;
  }

  ::-webkit-media-controls-enclosure
  {
    filter: invert(1)!important;
    border: 1px solid #000!important;
  }
}
