css:
@-moz-document domain("www.dokuwiki.org") {
:root {
    --highlight-fg-color: #9C8989;
    --main-fg-color: #684B4B;
    --highlight-bg-color: #321212;
    --main-bg-color: #1A0202;
    --dark: #020000;
}
html, body {
    background-color: var(--dark);
    color: var(--main-fg-color);
}

#dokuwiki__site {
    background-color: var(--highlight-bg-color);
}

.dokuwiki div.page,
.dokuwiki .pageId span {
    background-color: var(--main-bg-color);
    border: 1px solid var(--main-bg-color);
    box-shadow: 0 0 0.5em var(--dark);
}

.dokuwiki div.breadcrumbs,
.dokuwiki div.breadcrumbs div:first-child {
    border-top: 1px solid var(--main-fg-color);
}

.dokuwiki div.breadcrumbs,
.dokuwiki div.breadcrumbs div:last-child {
    border-bottom: 1px solid var(--main-fg-color);
}

th {
    background-color: var(--highlight-bg-color);
}

th, td {
    border: 1px solid var(--main-fg-color)
}

.dokuwiki table.inline tr:hover th {
    background-color: var(--dark);
}

.dokuwiki table.inline tr:hover td {
    background-color: var(--dark);
}

.dokuwiki div.section_highlight {
    border: solid var(--main-fg-color);
    border-width: 0 0.5em;
}

.dokuwiki a.wikilink1 {
    color: var(--highlight-fg-color);
}

#dokuwiki__header h1 a {
    color: var(--highlight-fg-color);
}

.cryptor {
    background-color: var(--highlight-bg-color);
}

pre, code {
    background-color: var(--main-bg-color);
    color: var(--main-fg-color);
    border: 1px solid var(--main-fg-color);
    box-shadow: inset 0 0 0.5em var(--dark);
}

input, textarea, select, keygen {
    border: 1px solid var(--main-fg-color);
    box-shadow: inset 0 0 1px var(--dark);
}

input, textarea, button, select, optgroup, option, keygen, output, meter, progress {
    background-color: var(--highlight-bg-color);
    color: var(--highlight-fg-color);
}

input[type=submit], input[type=button], input[type=reset],
input.button,
a.button, button,
.qq-upload-button {
    color: var(--main-fg-color);
    background-color: var(--main-bg-color);
    background-image: linear-gradient(to bottom,var(--highlight-bg-color) 0,var(--main-bg-color) 30%,var(--highlight-bg-color) 99%,var(--main-bg-color) 99%);
    border: 1px solid var(--main-fg-color);
}

input[type=submit]:hover, input[type=submit]:active, input[type=submit]:focus,
input[type=button]:hover, input[type=button]:active, input[type=button]:focus,
input[type=reset]:hover, input[type=reset]:active, input[type=reset]:focus,
input.button:hover, input.button:active, input.button:focus,
a.button:hover, a.button:active, a.button:focus,
button:hover, button:active, button:focus,
.qq-upload-button:hover, .qq-upload-button:active, .qq-upload-button:focus
{
    color: var(--highlight-fg-color);
    background-color: var(--highlight-bg-color);
    background-image: linear-gradient(to bottom,var(--main-fg-color) 0,var(--highlight-bg-color) 30%,var(--main-fg-color) 99%,var(--highlight-bg-color) 99%);
    border: 1px solid var(--highlight-fg-color);
}

#dw__toc {
    background: var(--main-bg-color);
    border-left: 1px solid var(--main-fg-color);
}

#dokuwiki__pagetools div.tools ul li a svg {
    fill: var(--main-fg-color);
}

#dokuwiki__pagetools:hover div.tools ul {
    background-color: var(--main-bg-color);
    border-color: var(--main-fg-color);
    box-shadow: 2px 2px 2px var(--dark);
}

#dokuwiki__pagetools div.tools ul li a:active,
#dokuwiki__pagetools div.tools ul li a:focus,
#dokuwiki__pagetools div.tools ul li a:hover {
    background-color: var(--highlight-bg-color);
}

#vcWin {
    background-color: var(--main-bg-color) !important;
    color: var(--main-fg-color) !important;
}

#vcWin #vcInWin {
    border: 5px solid var(--main-fg-color) !important;
}

#vcWin #vcPrompt,
#vcWin #vcInWin div input
{
    color: var(--main-fg-color) !important;
}
}
