css:
@-moz-document domain("kotaku.com"), domain("io9.com"), domain("deadspin.com"), domain("gawker.com"), domain("gizmodo.com"), domain("jalopnik.com"), domain("jezebel.com"), domain("lifehacker.com"), domain("kinja.com"), domain("gawker-labs.com"), domain("theroot.com"), domain("fusion.net"), domain("theonion.com"), domain("avclub.com"), domain("earther.com"), domain("splinternews.com") {
/* Remove Ads and Junk */
aside,
.sidebar,
.sc-1jotqfp-0,
.ad-container,
.dfp,
.dfp-slot-PROMOTION_NATIVE,
.ad-promotion-native,
.sharingfooter,
.share-toolbar,
.sidebar,
.pe_newlayout .sidebar,
.curation-main-container,
.ad-promotions-container,
.js_ad-promotion,
.section-sponsorship,
.ad-wide,
.ad-top,
.js_ad-promotion-desktop,
.ad-mobile,
.js_ad-mobile,
.ad-bottom,
.splashy-ad-container,
.ad-unit,
.js_ad-unit,
#sharingfooter,
.site-footer,
.sponsored,
.trc-content-sponsored,
.meta__label--sponsored,
.commerce-inset,
.commerce-inset--v2,
article[data-model*="authorBlogId%22%3A765%2C%22"],
article[data-model*="authorBlogId%22%3A1635548096%2C%22"],
article[data-model*="authorBlogId%22%3A455808697%2C%22"],
article[data-model*="authorBlogId%22%3A1636137831%2C%22"],
.js_newsletter-btn--subscribe,
.instream-permalink
{
    display: none !important;
}


/* Article Listing */
.main,
main,
article,
.storytype-boilerplate,
.main__content,
.discussion-header
{
    width: 100% !important;
    max-width: 100% !important;
    border-left: 0 !important;
    border-right: 0 !important;
    margin: auto !important;
    right: auto !important;
    left: auto !important;
}

.large-8,
.jeEUKg,
.post__content-wrapper,
.share-toolbar__container,
.related-module,
.author-bio__wrapper,
.replies-wrapper
{
    /* Change width and max width as you desire */
    width: 80% !important;
    max-width: 80% !important;
    float: none !important;
    margin: auto !important;
}

.meta--pe,
.post__content-wrapper,
.share-toolbar__container,
.related-module,
.author-bio__wrapper,
.replies-wrapper
{
    max-width: 1068.8px !important;
}

.postlist__item .excerpt iframe
{
    max-width: 100% !important;
}

.postlist__item
{
    margin-right: -60px !important;
}

.postlist__item:after
{
    margin-right: 60px !important;
}

@media only screen and (min-width: 37.5em)
{
    .postlist__item .item__content--thumb .excerpt
{
        max-width: 66.5% !important;
        float: right !important;
    }
    .postlist__item .excerpt
{
        width: 100% !important;
    }
}

.postlist__item .excerpt
{
    width: 100% !important;
}

.align--bleed
{
    margin-left: 0 !important;
}

.kinja-logo-divider hr
{
    border-top: 1px solid transparent !important;
}

/* Article View Maximized Width */
.editor .editor-inner .google-adx,
.editor .editor-inner .inset--story,
.editor .editor-inner .instream-permalink,
.editor .editor-inner .pullquote,
.editor .editor-inner .referenced-fullwidth,
.editor .editor-inner>span,
.editor .editor-inner blockquote,
.editor .editor-inner figure,
.editor .editor-inner h1,
.editor .editor-inner h2,
.editor .editor-inner h3,
.editor .editor-inner h4,
.editor .editor-inner h5,
.editor .editor-inner h6,
.editor .editor-inner hr,
.editor .editor-inner ol,
.editor .editor-inner p,
.editor .editor-inner pre,
.editor .editor-inner ul,
.post-content .google-adx,
.post-content .inset--story,
.post-content .instream-permalink,
.post-content .pullquote,
.post-content .referenced-fullwidth,
.post-content>span,
.post-content blockquote,
.post-content figure,
.post-content h1,
.post-content h2,
.post-content h3,
.post-content h4,
.post-content h5,
.post-content h6,
.post-content hr,
.post-content ol,
.post-content p,
.post-content pre,
.post-content ul
{
    max-width: 100% !important;
}

/* Blog Article Content */
.recirculation-unit
{
    display: none !important;
}

.permalink.featured-post.f_featured_post_more_space_on .post .post-content,
.permalink.featured-post .post .post-content
{
    padding-left: 0px;
    padding-right: 0px;
    width: 80%;
    margin: auto;
}

/* Small-view Content */
@media only screen and (max-width: 50em)
{
    .postlist__item
{
        margin-right: 0px !important;
    }
    .postlist__item:after
{
        margin-right: 0px !important;
    }
    .liveblog-wrapper:before,
    .qanda-wrapper:before,
    .replies-wrapper:before
{
        background-image: none !important;
    }
}

}
