css:
@-moz-document domain("pkmnclassic.net") {
body {
    background-color: #666666;
}

#gtsMain {
    background-color: #333;
    color: #c0c0c0;
}

.pfFormValue {
    background-color: #222;
}

.gtsPokemonSummary .portrait, .gtsPokemonSummary li.portrait {
    background-color: #34373c;
}

#gtsSubNav {
    background-color: #222;
    background-image: -moz-linear-gradient(top, #333 0%, #222 100%);
    background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#333), color-stop(100%,#222));
    background-image: -webkit-linear-gradient(top, #333 0%,#222 100%);
    background-image: -o-linear-gradient(top, #333 0%,#222 100%);
    background-image: -ms-linear-gradient(top, #333 0%,#222 100%);
    background-image: linear-gradient(to bottom, #333 0%,#222 100%);    
    border: 1px solid #252525;
}

.home #gtsHeader .gtsNav li.home a, .gts #gtsHeader .gtsNav li.gts a, .bv #gtsHeader .gtsNav li.bv a, .dw #gtsHeader .gtsNav li.dw a, .dex #gtsHeader .gtsNav li.dex a, .stat #gtsHeader .gtsNav li.stat a {
    background-color: #333;
}

#gtsHeader .gtsNav li a {
    border: 1px solid #252525;
    background-color: #333;
}

#gtsHeader .gtsNav li a:hover {
    background-color: #252525;
}

input[type="text"], input[type="password"], select, textarea {
    border: 1px solid #252525;
    background-color: #34373c;
    color: #c0c0c0;
}

.pfLookup .results {
    background-color: #34373c;
    border: 1px solid #252525;
}

.pfLookup .results .result.default, .pfLookup .results:hover .result:hover, .pfLookup .results:hover .result.default:hover {
    background-color: #222;
}

#gtsFooter:after {  
    text-align: justify;
    text-justify: initial;
    content: 'Poké Classic Network Dark v1.0.0 by sks316' !important;
}
}
