css:
@-moz-document url-prefix("http://www.mouse.latercera.com") {
	body { color: #231f20; background-color: #3e3e3e;}
    body.single { background-color: #252525; }
    .single-container { background-color: #171717; color: #b1b1b1; }
    .single .rank article, .single .rank article h1 { background-color: #3e3e3e; color: #000; }
    .rank article { background-color: #f6f6f6; border-bottom: 1px solid #f6f6f6; }
    .bordered .col-lg-4 { border-color: #3e3e3e; background-color: #1f1f1f; }
    .mid-article, .mid-article h1 { background-color: #1f1f1f; color: #000000; }
    .mid-article .bottom-data { border-top: 1px solid #000000; }
    .bordered { border-color: #000000; }
    .rank article { background-color: #212121; border-bottom: 1px solid #000000; }
    .rank article, .rank article h1 { background-color: #212121; border-bottom: none; color: #777; }
    .main-header .hdr-relative:before { background-color: #131313; }
    .main-header { background-color: #231f20; }
    .tags-container a { color: #ffffff; text-transform: uppercase; padding: 1px 10px; font-size: 1em; white-space: nowrap; }
    .main-header .mouse { background-image: url(http://mouse.latercera.com/wp-content/themes/mousetodon/img/mouse-static-inverse.png); }
    .tags-container ul li { font-size: 0.875em; color: #ff0000; position: relative; }
}
