css:
@-moz-document domain("bitcoinmagazine.com") {
body {
    background-color: #111 !important;
    color: #999;
}

body.gr {
    background-color: #111 !important;
}

.category-list--keywords .keyword-tags {
    background-color: #1a1a1a;
    color: #888;
    border-bottom: 2px solid #ff9f00;
}

.navbar {
    background: #222;
    color: #aaa;
    border-bottom: 1px solid #666;
}
.navbar-main a {
    color: #aaa;
}

.navbar-main a:active,
.navbar-main a:focus,
.navbar-main a:hover {
    color: #ff9f00;
}

.submenu-item {
    background-color: #333;
    color: #aaa;
}

.submenu-item div.subnav-col > a {
    color: #aaa;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    color: #74859b !important;
}

.about-template p, .about-template ul, .about-template ol, .about-template li {
	color: #888;
}

.archive-list--title, .archive-list--author, .archive-list--date {
	color: #888;
}

.category-description p {
    color: #888;
}

.main-news-home {
    background-color: #222;
    border: 1px solid #444;
}

.main-news-home .author {
    color: #888;
}

figcaption,
.author-sticky-sidebar .article--time {
    color: #777;
}

.sidebar-list .list-group-item .time {
    color: #888;
}

.bm-card .author,
.bm-card .time {
    color: #888;
}

div[style*="float:right"] {
    background-color: #222 !important;
}

a.navbar-logo {
    display: inline-block;
    padding: 0 !important;
    margin: 5px 0 0 !important;
    width: 220px;
    -webkit-filter: invert(30%);
}

.category-list--keywords .keyword-tags {
    background-color: #1a1a1a;
    color: #888;
    border-bottom: 2px solid #ff9f00;
}

.page-item.disabled .page-link {
    color: #888;
    background-color: #222;
    border-color: #444;
}

.page-link {
    color: #888;
    background-color: #222;
    border: 1px solid #444;
}

.page-link:focus,
.page-link:hover {
    color: #ff9f00;
    text-decoration: none;
    background-color: #444;
    border-color: #444;
}
}
