css:
@-moz-document domain("lioden.com") {
/*@namespace url(http://www.w3.org/1999/xhtml);*/
body {
    background: url('http://www.goodwp.com/pic/201107/1600x1200/goodwp.com-19171.jpg')!important;
    background-attachment: fixed!important;
    background-size: 100%!important;
}
h1,
h2,
h3{
    color: #baf9ff!important;
}

a,
div{
color:white;
}

.hidden-xs,
.xlarge,
b{
color:#a9d5d8!important;
}

p{
color:#87b3b7;
}

.feature,
.feature.b,
table.table td {
color:white;
    background: rgba(0,0,0, .5);
}

td{
color: white;
}

td.right.center,
td.right,
.sub_menu,
.right_odd.xsmall,
.right_locked,
td.right_odd,
.customiser-sortable li:nth-child(2n+1), 
ol.breadcrumb{
     background: rgba(0,0,0, .50)!important;
    color: #bfe5ff!important;
}

td.right_odd.center{
color:#e8f5ff!important;
}

#interface.feature,
.item-surround,
.auto.feature.center,
div.left {
    background: rgba(0, 91, 150, 0.3)!important;
    color: #bfe5ff!important;
}

.page-description.center.auto{
background: rgba(0, 91, 150, 0.3)!important;
}

th,
td,
.sub_menu_item_selected {
    background: rgba(0,0,0, 0.3)!important;
    color: white!important;
    transition: all ease 0.5s;
}
a:link,
a:visited {
    color: #83e6ef;
}
a:hover,
a:active {
    color: #ff5a6b!important;
    transition: all ease 0.5s;
}
.sidebar,
.navbar {
   background: rgba(0, 91, 150, 0.3)!important;
    color: white!important;
}
.navbar {
    border-top-right-radius: 15px;
}
.sidebar {
    border-bottom-right-radius: 15px;
   
}
.navbar-default .navbar-nav > li > a {
    color: white!important;
    transition: all ease 0.5s;
}
.navbar-default .navbar-nav > li > a:hover {
    background: rgba(179, 205, 224, 0.3)!important;
    transition: all ease 0.5s;
}
.xsmall {
    background: none!important;
    color: white!important;
}
.panel,
.sub_menu,
.sub_sub_menu,
.pagination span {
    box-shadow: none!important;
    border: 1px solid black!important;
    background: #011f4b!important;
    border-radius: 15px;
    color: white!important;
}
.s-chat-message_content{
color: black!important;
}
.s-chat__username{
color: #207aa3!important;
}
.chat_light {
     background: rgba(100, 151, 177, .5)!important;
    color: black!important;
}
.chat_dark {
    background: #011f4b!important;
    color: white!important;
}
.topbar,
.breadcrumb {
    background: rgba(100, 151, 177, .5)!important;
    color: black!important;
}
.breadcrumb > .active {
    color: white!important;
}
input{
color:black!important;
}
input[type="submit"],
input[type="button"] {
    color: #b3cde0!important;
    background: #011f4b!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    transition: all ease 1s;
    border-radius: 15px!important;
}
input[type="submit"]:hover,
input[type="button"]:hover {
    color: #005b96!important;
    background: #011f4b!important;
    border: 2px solid black!important;
    box-shadow: none!important;
    text-shadow: none!important;
    border-radius: 15px!important;
}
.alert.alert-success{
background: rgba(43, 137, 98, .75)!important;
color: white!important;
}

.alert.alert-danger{
background: rgba(205, 85, 85, .75)!important;
color: white!important;
}

.progress-bar-warning,
.progress-bar-danger,
.progress-bar-success,
.progress-bar {
    background: rgba(100, 151, 177, .5)!important;
}
.progress {
    background: #b3cde0!important;
    border: 1px solid black;
    border-radius: 15px;
    color: #011f4b!important;
}
.progress div {
    color: #011f4b;
}
div#lion_image>div {
    background: url('https://dl.dropbox.com/s/er1v1hqfe8b2w7c/Simple%20Blue%20Lion%20BG.png')!important;
}
table.table,
div.table {
    border: 1px solid black!important;
    color:white!important;
}
input[type="text"] {
    color: black;
}
input[type="password"] {
    color: black;
}
textarea {
    color: black;
}
select {
    color: black;
}
div.container.main {
    background: none!important;
    box-shadow: none!important;
    border: none!important;
    border-top-right-radius: 15px!important;
    border-bottom-right-radius: 15px!important;
    border-bottom-left-radius: 15px!important;
}
div.col-md-9.col-xs-12 {
    background: none!important;
    border: none!important;
}
.item {
    border: none!important;
     background: rgba(0, 91, 150, 0.3)
}
.item-footer,
.item-header {
background: rgba(69, 153, 209, 0.8);
color: white!important;
}

.floatleft,
.floatright{
color: white!important;
}

.badge { 
  background:#005b96!important;
color:black!important;
}

li,
div.abandoned-count
{
color: black!important;
}

div.ui-tooltip-content{
background:none!important;
color:#011f4b!important;
}
.ui-widget
{
background:rgba(255, 255, 255, .75)!important;
}
}

@-moz-document url("https://www.lioden.com/crafting.php") {
b,
span.small{
color: black!important;
}
}
