css:
@-moz-document regexp("^(?=.*cardboardtranslations\\.com)(?=.*jingai-musume).*$") {
/* Code to Style the Text and Scrollbar */
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
hr {
    border-color: #555555 !important;
}
select {
 	background-color: #555555 !important;
    color: #eeeeee;
}
a {
    color: #c69c6d !important;
}
.nav-links > * a {
 	background-color: #555555 !important;
}
blockquote {
 	background-color: #555555;
    color: #eeeeee;
    letter-spacing: unset;
    text-align: unset !important;
    border: unset;
}
b, strong {
    font-weight: unset !important;   
}
p,del {
    color: #eeeeee;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
.site-content,.entry-content,.entry-header, body, #main, div[id="page"] {
    background: #333333 !important;
}
html {
    scrollbar-color: #555555 #333333;
}
.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}
.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}
/* Code to center Novel text */
.entry-header {
	margin-top: 50px;
}
.site, #wrapper, #content  {
    max-width: 700px !important;
    margin: 0 auto !important;
    padding: unset !important;
}
div[id="content"], .site-main {
    margin: 0 auto !important;
    padding: unset !important;
}
.hentry {
    margin: unset !important;
    padding: unset !important;
}
header[class="entry-header"] {
    margin: unset !important;
    padding: unset !important;
    padding-top: 30px !important;
    max-width: unset !important;
    hyphens: none;
}
div[class="entry-content"] {
    margin: unset !important;
    padding: unset !important;
    max-width: unset !important;
    hyphens: none;
}
.content-area{
    margin: unset !important;
    padding: unset !important;
}
/* Code to remove content from webpage except for the Novels text and navigation. */
div[class="pea_cook_wrapper pea_cook_topcenter"], a[class="skip-link screen-reader-text"], header[id="masthead"], footer[id="colophon"], div[id="secondary"], div[id="comments"], footer[class="entry-footer"], div[class="wpcnt"], div[id="jp-post-flair"], div[id="actionbar"] { display: none;}
div[class="code-block code-block-1"] { display: none !important;}
}
