css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix(http://zip.4chan.org/v/)
{

/*Colors tables  bg etc*/

* {
font-family:"Mona", sans-serif !important;
font-size:14px !important;
-moz-appearance: none !important
}

.delform {
color:#FFFFFF !important;
}

.postarea {
padding: 0px !important; 
}

.pages {
background-color:transparent !important;
}

span.plus {
color:#FFFFFF !important;
}

.hr {
color:#FFFFFF !important;
}

.omittedposts{
color:#FFFFFF !important;
}

blockquote {
color:#FFFFFF !important;
max-width:870px !important;
}

table {
color:#FFFFFF !important;
}

.logo 
  { margin-bottom: 25px !important; 
    text-align: right !important;
    width: auto !important;
    margin: 0 30px 0 0 !important; }

.logo img 
  { float: left !important;
    clear: left !important; }

.logo font { margin: 0 !important; }

.logo font b span, html > body > table > tbody > tr > th > font
  { color: #000000;
    font: 200 100%/1.4 Helvetica, Arial, sans-serif !important; }

.logo font b span 
  { color: #000000 !important;
    font-weight: 700 !important;
    font-size: 150% !important; }

html > body > table > tbody > tr > td,
html > body > table > tbody > tr > th
  { opacity: 0.50 !important;
    -moz-border-radius: 10px !important; }


html > body > table > tbody > tr > td { padding: 0.25em 0 !important; }

html > body > table > tbody > tr > td:hover, 
html > body > table > tbody > tr > td:focus,
html > body > table > tbody > tr > th:hover
  { opacity: 1.00 !important; }

.doubledash { display: none; }

.postarea > form > table > tbody > tr { opacity: 0.5 !important; }
.postarea > form > table > tbody > tr:hover { opacity: 1.00 !important; }

.postblock 
  { background-color: #d4cf69!important;
    border-color: #000000!important;
    color: #000000 !important; 
    padding-top: 0.1850em !important;
    -moz-border-radius: 10px 0 0 10px !important;
    vertical-align: top !important;
    text-align: right !important; }

.inputtext, input[name="upfile"]
  { width: 100% !important;
    -moz-border-radius: 0 10px 10px 0 !important; }

textarea.inputtext
  { height: 200px !important; }

#twi_table_div{
border:none !important
}

.filetitle {
color:#FFFFFF !important;
}

.spoiler { 
background-color: #FFF !important; 
color: #FFF !important; 
}

textarea{
height:10em !important;
width:45em !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid 2px #FFFFFF !important;
-moz-border-radius:2px !important;
opacity:.99 !important;
z-index:3 !important;
}

html,body {
font-size:20px !important;
background-color:#e9b786 !important;
background-image:url('http://i43.tinypic.com/nzoy9j.png') !important;
background-position: right top !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
color:#FFFFFF !important;
}

.replymode {
background-color:transparent !important;
color:#FFFFFF !important;
}

th {
background-color:#a11e28 !important;
opacity:.90 !important;
-moz-border-radius:10px !important;
}


tr {
color:#FFFFFF !important;
}

*[id="footer"]  {
-moz-border-radius:5px !important;opacity:.90 !important;background-color:#a11e28 !important;color:#FFFFFF !important;
border:solid 2.5px #FFFFFF !important;padding: 10px 20px 16px 20px !important
}

.unkfunc{
color:#FFFFFF !important;
}

#bod1 {
background-color:transparent !important;
}

td input, td textarea{
-moz-border-radius:10px !important;
}

[class*="postername"] {
background-color:transparent !important; 
color:#FFFFFF !important
}

[class*="postertrip"] {
color:rgb(0,160,100) !important
}

[class*="abbr"]{
color:#FFFFFF !important
}

div {
color:#FFFFFF !important;
}

* [class="boxcontent"]{
opacity:.90 !important;background-color:#a11e28 !important;color:#FFFFFF !important;
}

* [id="ft"]{
display:none !important;
}

* [class="boxbar"]{
background-color:#a11e28 !important;
}

* [id="filter-container"]{
display:none !important;
}

* [id="options-container"]{
display:none !important;
}

input[type="file"] {
background-color:transparent !important;
}

span [title='']{
color:#FFFFFF !important;
}

sup{
color:#FFFFFF !important;
}

.postarea .postblock{
-moz-border-radius:15px /*2px 0 0 2px*/ !important;
border:solid 0px #FFFFFF  !important;
border-right-style: dashed !important;
border:solid 2px #FFFFFF !important;
text-align:center !important;
background-color:#a11e28 !important;
color:#FFFFFF !important;
}

.postarea:hover .postblock:hover{font-weight:bold !important;color:#FFFFFF !important;}


/* hr */

hr{display:none !important;}

form hr{display:block !important;}

hr{height:0px !important;border:none !important;background:#999 !important;}


/* rounded corners */

td.reply {-moz-border-radius:5px !important;opacity:.90 !important;background-color:#a11e28 !important;color:#FFFFFF !important;
border:solid 2.5px #FFFFFF !important;padding: 0 20px 16px 0 !important}

td.replyhl {-moz-border-radius:5px !important;opacity:.90 !important;background-color:#de6761 !important;color:#FFFFFF !important;
border:solid 2.5px #FFFFFF !important;padding: 0 20px 16px 0 !important}


/* links */

a {text-decoration:none !important;color:#FFFFFF !important;}

*[class*="poster"],*[class*="linkmail"],*[class*="replytitle"],*[class*="filetitle"],*[class*="omittedposts"],*[class*="oldpost"] { color: #FFFFFF !important; border-width: 0px !important; }

*[class*="doubledash"] { color: #FFFFFF !important; }

*[class*="unkfunc"],*[class*="quotelink"],*[class*="reflink"],*[class*="quotejs"] { color: #FFFFFF !important; }

*[target="_blank"]{ color: #FFFFFF !important; }

*[class*="abbr"] { color: #FFFFFF !important; }

a.quotejs:hover, a:hover{font-weight:bold !important;color:#FFFFFF !important;}

b {color:#FFFFFF !important;}

b.quotejs:hover, b:hover{font-weight:bold !important;color:#FFFFFF !important;}

/* removes ugly borders on page overview [0,1,2...] */

table,td{border:none !important;}

td.rules{display:none !important;}



/* blurry thumbnails fix */

img[md5] {

height: auto !important;

width: auto !important;

}
}

/* 4chan extension */

DIV[class="4chan_ext_hiddenthreadinfo"] .reply
  { padding: 0 !important; 
    border: 0 none !important;
    -moz-border-radius: 0 !important;
    background-color: transparent !important; }

.reply .postblock { text-align: center !important; }

SPAN[class="4chan_win_header_buttons"]
  { right: 34px !important; }

SPAN[style="border: thin solid black; padding: 3px; z-index: 100; position: absolute; right: 10px; top: 0px;"][class="reply"],
SPAN[style="border: thin solid black; padding: 3px; position: absolute; right: 10px;"][class="reply"] 
{ padding: 0 !important;
  -moz-border-radius: 0 5px !important; }

#twi_table_div 
  { border: 0 none !important; 
    opacity: 0.90 !important; }

#twi_table_div .postername,
#twi_table_div .postertrip,
#twi_table_div .filetitle
  { font-size: 100% !important; }

#twi_table_div > .postblock 
  { border-color: #000000!important;
    border-width: 1px 1px 0 1px !important;
    -moz-border-radius: 10px 10px 0 0 !important;
    text-align: center !important; }

#twi_table_div table .postblock 
  { -moz-border-radius: 0 !important;
    background-color: #f2cc5d!important; 
    margin: 0 !important;
    color: #000000 !important;
    text-align: center !important; }

#twi_table_div > #twi_table
  { border-width: 0 1px 1px 1px !important;
    -moz-border-radius: 0 0 10px 10px !important; 
    padding: 0 0 5px 0 !important;
    opacity: 1.00 !important; }
