css:
@-moz-document regexp("^(?=.*isekailunatic)(?=.*chapter).*$") {
/* Code to Style the Text and Scrollbar */
 h1, h2, h3, h4, h5, h6, h7, h8, h9, figcaption {
    color: #eeeeee !important;
    font-family: Helvetica, Arial, sans-serif !important;
    font-size: 22px;
}
hr {
    display: none;
}
a {
    color: #c69c6d !important;
}
p, del, span, li {
    color: #eeeeee !important;
    font-size: 22px;
    margin-bottom: unset;
    letter-spacing: unset;
    text-align: unset !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
.site-content, .entry-content, .entry-header, body {
    background: #333333 !important;
}
html {
    scrollbar-color: #555555 #333333;
}
.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}
.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}
/* Code to center Novel text */
 .site, .site-main {
    max-width: 700px !important;
    margin: 0 auto !important;
    float: unset;
    width: unset;
    font-family: Helvetica, Arial, sans-serif !important;
}
div[id="primary"] {
    margin: 0 auto !important;
    padding: unset !important;
    font-family: Helvetica, Arial, sans-serif !important;
}
.site-content {
    border-top: unset;
    margin: unset;
    padding: unset;
}
.hentry {
    margin: unset !important;
    padding: unset !important;
}
#page {
    margin: unset !important;
    padding: unset !important;
    padding-top: 30px !important;
    max-width: unset !important;
    hyphens: none;
}
/* Code to remove content from webpage except for the Novels text and navigation. */
 div[class="widget widget_eu_cookie_law_widget"] {
    display: none !important;
}
div[id="actionbar"], a[class="skip-link screen-reader-text"], header[id="masthead"], footer[id="colophon"], div[class="featured-header-image"], div[id="secondary"], div[id="tertiary"], div[id="comments"], nav[class="navigation post-navigation"], footer[class="entry-footer clear"], div[class="wpcnt"], div[id="atatags-977445815-5cd99b07bf4c0"], div[id="jp-post-flair"] {
    display: none;
}
}
