css:
@-moz-document regexp(".*:8000\\/en-US.*") {
body {
    background: #171d21;
    color: #FFFFFF;
}
.search-initialhelp .help-column {
    background: #2b3033;
    box-shadow: none;
}
a, a:hover {
    color: #70e670;
}
.dropdown-menu ul {
    background-color: #171d21
}
h3 {
    color: #fff
}
h2 {
    color: #fff
}
p {
    color: #FFF
}
.search-history {
    background-color: #171d21;
    box-shadow: 0 1px 1px #2b3033;
}
.dataset-tablecontainer-results {
    background: #171d21;
}
.shared-datasettable .dataset-table-head {
    background: #171d21;
}
.shared-datasettable-resultsbody-row {
    background: #171d21;
}
.real-text, .name, .row-num, .shared-datasettable-resultsbody-row, .shared-datasettable td.row-num, .col-header, .link-label, .shared-jobstatus .status {
    color: #FFF;
}
.icon-string:before {
    color:#FFF
}
.search-bar .search-field-wrapper .ace_editor {
    border-color: #3c444d;
}
.shared-datasettable td.value.null-cell {
    font-family: inherit;
    color: #929292;
    background: #171d21;
    font-style: italic;
}
.table-caption-inner.affix-top {
    background-color: #3c444d;
}
.header-table-docked {
    background: none;
}
.app, .content-area:before {
    background-color: #171d21;
    color: #FFF;
}
.main-section-body {
    color: #dadada;
}
.text-filter-control-wrapper .text-filter-control.affix-top {
    background-color: #171d21;
}
.results-control-bar-inner.affix-top {
    background-color: #171d21;
}
.modal-header {
    background: #353c45;
}
.modal-footer {
    background: #353c45;
}
.modal-body-scrolling {
    background-color: #353c45;
}
.home-gettingstarted h1 {
    color: #FFF;
}
.home-gettingstarted {
    background-color: #3c444d;
}
.home-gettingstarted-shared-item .hoverable-area {
    color: #f3f3f3;
}
.home-gettingstarted-shared-item .hoverable-area:hover {
    background-color: #5e656d;
}
.section-home .home-dashboard {
    background-color: #3c444d;
}
.home-gettingstarted .toggle:before {
    background-color: #3c444d;
}
.home-gettingstarted .toggle {
    background: #3c444d;
    color: #ffffff;
}
.table-chrome>thead>tr>th {
    background-color: #3c444d;
}
.table-striped.table-chrome>tbody>tr.odd>td, .table-striped.table-chrome>tbody>tr:nth-child(2n).odd>td, .table-striped.table-chrome>tbody>tr:nth-child(odd)>td {
    background-color: #535d69;
}
.table-striped.table-chrome>tbody>tr.even>td, .table-striped.table-chrome>tbody>tr:nth-child(2n)>td, .table-striped.table-chrome>tbody>tr:nth-child(odd).even>td {
    background-color: #5c6773;
}
.datasets-results-table>.table, .reports-table>.table, .alerts-table>.table, .dashboards-table>.table {
    background-color: #fff;
    box-shadow: 0 1px 1px #585858;
    margin-bottom: 20px;
}
.table-chrome>thead>tr>th {
    border-right: 1px solid #535d69;
}
.table-row-expanding th.col-info {
    border-right: 1px solid #535d69;
}
.table-row-expanding>tbody>tr>td.expands {
    border-right: 1px solid #6b7177;
}
.view---pages-enterprise---7-2-6---1dFdk {
    background-color: #171d21;
}
.body---pages-enterprise---7-2-6---1ME5z {
    background-color: #3c444d;
}
.heading---pages-enterprise---7-2-6---2KvxI {
    color: #f5f5f5;
}
.link---pages-enterprise---7-2-6---1ArAk {
    color: #ffffff;
}
.link---pages-enterprise---7-2-6---1ArAk:hover {
    color: #ffffff;
    text-decoration: underline;
}
.view---pages-enterprise---7-2-6---3Zsef {
    border: 1px solid #2b3033;
}
.btn-pill.active, .btn-pill:active {
    color: #5c6773;
    background: #5cc05c;
}
.accordion-body, .accordion-heading {
    background-color: #171d21;
    color: #FFF;
}
.accordion-heading .accordion-toggle {
    background-color: #171d21;
    color: #FFFFFF;
}
.accordion-group.active .accordion-toggle {
    background: #3c444d;
}
.popdown-dialog {
    border: 1px solid #3c444d;
}
.btn {
    background-color: #000000;
    color:#FFFFFF;
}
.report-statemessage, .results-wrapper {
    background-color: #171d21;
}
.alert {
    color: #FFF;
}
}
