css:
@-moz-document domain("52pojie.cn") {
.fl .bm_h {
	background:#eeeeee;
	background:-moz-linear-gradient(top,#eeeeee 0%,#cccccc 100%);
	background:-webkit-linear-gradient(top,#eeeeee 0%,#cccccc 100%);
	background:linear-gradient(to bottom,#eeeeee 0%,#cccccc 100%);
    }
	#wp a,.xi2 {
	color:#4fb1ba;
	border-color:rgba(79,177,186,0.2);
	border-top-color:rgba(79,177,186,0.2);
	border-right-color:rgba(79,177,186,0.2);
	border-bottom-color:rgba(79,177,186,0.2);
	border-left-color:rgba(79,177,186,0.2);
}
#wp a:visited {
	color:#759598!important;
}
body {
	color:#333;
	background:#dddddd;
	font-weight:400;
	font-weight:700;
	font-weight:400;
	text-muted:#888;
	gray-bg:rgba(0,0,0,0.025);
	gray-text:#666;
	menu-text:#bbb;
	-webkit-transition:background .6s !important;
	transition:background .6s !important;
	-webkit-transition-timing-function:cubic-bezier(.2,1,.3,1) !important;
	transition-timing-function:cubic-bezier(.2,1,.3,1);
}
#wp {
	padding:3px 15px;
	background:#e9e9e9;
	box-sizing:border-box;
	-webkit-box-shadow:0 0.5em 1em 0.5em rgba(0,0,0,0.19);
	box-shadow:0 0.5em 1em 0.5em rgba(0,0,0,0.19);
	border-bottom-left-radius:0.3em;
	border-bottom-right-radius:0.3em;
}
i.pstatus {
	color:#888;
}
table.plhin {
	-webkit-transition:box-shadow .6s !important;
	transition:box-shadow .6s !important;
	-webkit-transition-timing-function:cubic-bezier(.2,1,.3,1) !important;
	transition-timing-function:cubic-bezier(.2,1,.3,1);
}
table.plhin:hover {
	-webkit-box-shadow:0 0 0.2em 0.1em rgba(0,0,0,0.19);
	box-shadow:0 0 0.2em 0.1em rgba(0,0,0,0.19);
}
.bm {
	margin-bottom:10px;
}

.xi2 .notabs {
	color:#888!important;
}
.xi2 a,.xi3 a {
	color:#fff!important;
}
.xg2 {
	color:rgba(255,255,255,0.75)!important;
}
.xg2,.poston {
	color:#888!important;
}
td.plc {
	background:whitesmoke;
}
.pi strong a {
	border:1px solid #e5e5e5;
}
.quote {
	color:#8f8f8f;
}
html:not(.widthauto) .ct2_a .mn {
	width:760px;
}
form#postform {
	margin:5px;
}
.ct2_a,.ct3_a {
	background:#e9e9e9;
	background-color:#e9e9e9;
}
.avtm img {
	-webkit-box-shadow:0 0.1em 1.3em -0.2em rgba(255,255,255,0.52);
	box-shadow:0 0.1em 1.3em -0.2em rgba(255,255,255,0.52);
	background:transparent!important;
}
table.ratl {
	height:200px;
	overflow-y:auto;
	display:inline-block;
}
table.ratl th:nth-child(4) {
	width:100%;
}
.pattl img,.sign img,.t_f img:not([smilieid]):not(.vm) {
        -webkit-box-shadow: 0 0.5em 1em 0.3em rgba(0, 0, 0, 0.19);
        box-shadow: 0 0.5em 1em 0.3em rgba(0, 0, 0, 0.19);
        border-bottom-left-radius: 0.3em;
        border-bottom-right-radius: 0.3em;
    }

    /* 不想下载图片附件 */
    .aimg_tip {
    display: none!important;
}
    /* 隐藏财富信息 */
    .pil.cl span {
    display: none;
}
    /* 不显示分享按钮 */
    .bdsharebuttonbox,
.followp {
    display: none!important;
}
    /* 不要在签名档提示我了 */
    .plm .sign > span {
    display: none!important;
}
    /* 不要在回复框提示我了 */
    #fastpostmessage {
    background: none!important;
}
  /*20181108更新*/
  
    #fastpostmessage {
    background: none!important;
}
    /* 不显示版规 */
    .ptn.xg2 {
    display: none!important;
}
    /* 评分高度 */
    table.ratl {
    height: 200px;
    overflow-y: auto;
    display: inline-block;
}
    /* 隐藏广告 */
    .bdsharebuttonbox,
.dnch_eo_pr,
.dnch_eo_pt,
div[class*="_eo_"] {
    display: none!important;
}
    /* 顶部的几个链接 */
    #toptb .wp > div > a:not(.switchwidth):not(#sslct) {
    display: none!important;
}
/*         ************* 以下更改 ****************                 */
    /* 回复框高度 */
.plc{
    padding : 0px 20px 0px 20px;
}
    /*用户框隐藏部件*/
.plm,.tns,.favatar p{
    display: none!important;
}
.avatar a img{
    width : 60px!important;
    height : auto!important;
    padding : 0px 0px 0px 13px!important;
}
.avtm img{
    width : 60px!important;
    height : auto;
    padding : 0px 0px 0px 15px!important;
}

.pi{
    padding : 5px 0px 5px 15px!important;
    margin : 0px 0px 0px 0px!important;
}
.pct{
    padding : 0px 0px 0px 0px!important;
}
/*评论高度*/
.t_fsz{
    min-height : 30px!important;
}

/* ==============================以下去头像栏勋章什么的==========================================  */
/*收听栏*/
.xl2,.favatar p{
    display: none!important;
}
.plm,.tns,.avatar{
    display: none!important;
}

}
