css:
@-moz-document domain(anidb.net) {
  /* Quick'n'Dirty AniDB Dark template - Lowlow 2019.05.14 
   * Works with the website's AniGoogle template
   * I just throw stuff as I encounter them while
   * surfing, dirty writing though ;-)
   *
   * Feel free to improve it ^^ 
   */

  :root {
      --color-Black: #000000;
      --color-DarkBG: #272727;
      --color-Grey: #444444;
      --color-GWhite: #D4D4D4;
      --color-White: #FFFFFF;

      --color-DGBlue: #3a3f51;  
      --color-LGBlue: #4f566f;  

      --color-DBlue: #7192b9;  
      --color-LBlue: #83a7d0;
  }

  #layout-search:not(.main_search) {
      background-image: none;
  }

  #layout-nav div.main_search {
      background-image: none;
      background-repeat: no-repeat;
  }

  #layout-nav {
      background-color: var(--color-DarkBG);
      color: var(--color-GWhite);
  }

  #layout-menu > li ul {
      background-color: var(--color-DGBlue);
      border-color: #C9D7F1 #97AACC #97AACC #C9D7F1;
  }

  #layout-main div.forum div.g_bubble.threads {
      background-color: var(--color-LGBlue);
  }

  a, span.link, th a {
      font-weight: bold;
      color: var(--color-GWhite);
  }

  a:hover {
      color: var(--color-White);
  }

  h1 a:not(.i_icon), h4 a:not(.i_icon), th a:not(.i_icon) {
      text-decoration: underline;
      text-decoration-style: solid;
  }

  th, #layout-main .g_bubble.graph div.header, #layout-main div.info div.extra div h2, #layout-main div.main_all div.g_section > h2, #layout-main table tr.header {
      background-color: var(--color-LGBlue);
      color: var(--color-GWhite);
  }

  .fancybox-container div.g_msg.note, #layout-main div.g_msg.note {
      border: 1px solid var(--color-LGBlue);
      background-color: var(--color-DGBlue);
  }

  .g_bubble .g_odd, html, #layout-main div.forum ul.topics li.row, #layout-main div.search_all div.suggestions, #layout-main div.tabbed_pane ul.tabs li, #layout-tabs ul.main-tabs > li.selected, #layout-tabs ul.main-tabs > li:hover {
      background-color: var(--color-DarkBG);
      color: var(--color-GWhite);
  }

  .g_bubble, .g_bubble .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble, .g_button a:hover, .tooltip > span.text.g_bubble, button:hover, div.g_odd, input[type="button"]:hover, input[type="submit"]:hover, li.g_odd, span.g_odd, tr.g_odd, #layout-main > h1, #layout-main div.addtagentityrel_all div.suggestions > span.focus, #layout-main div.addtagentityrel_all div.suggestions > span:hover, #layout-main div.edit_actions span > *:hover, #layout-main div.forum ul.topics li.g_odd.row, #layout-main div.g_timedentry h4, #layout-main div.search_all div.suggestions span:hover, #layout-main div.tabbed_pane ul.tabs li.selected, #layout-main div.tabbed_pane ul.tabs li:hover, #layout-main div.tabbed_pane_cfg div.body, #layout-main div.tabbed_pane_cfg ul.tabs li.selected, #layout-main div.tabbed_pane_cfg ul.tabs li:hover, #layout-main ul.g_list li.g_odd.selected, #layout-main ul.g_list li.g_odd:hover, #layout-main ul.g_list li.selected, #layout-main ul.g_list li:hover, #layout-search ul.quicksearch li.focus, #layout-search ul.quicksearch li:hover, #layout-tabs ul.main-tabs > li {
      background-color: var(--color-DGBlue);
      color: var(--color-GWhite);
  }

  .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble, .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble .g_bubble, .g_button a, .g_odd .g_bubble, button, #layout-main div.edit_actions span > *, #layout-main div.main_all div.account ul.tabs li, #layout-main ul.g_list li, #layout-main ul.g_list li.g_odd {
      background-color: var(--color-DGBlue);
  }

  input.text, input[type="date"], input[type="email"], input[type="password"], input[type="text"], input[type="url"], select, textarea, #layout-main .g_section > form > h2, #layout-main .g_section > h2, #layout-main div.search_all div.suggestions, #layout-main div.tabbed_pane ul.tabs li, #layout-search input.text, #layout-search select, #layout-tabs ul.main-tabs > li {
      border-color: var(--color-GWhite);
      color: var(--color-GWhite);
  }

  input, select {
      background-color: var(--color-DGBlue);
  }
  
  .select2-container--default .select2-selection--multiple, .select2-container--default .select2-selection--single, .select2-dropdown {
    background-color: var(--color-DGBlue);
    color: var(--color-GWhite);
  }

  .g_bubble .name-colored, .g_bubble div.name, .g_bubble.box > div.data div.date, .select2-container--classic .select2-selection--multiple .select2-selection__choice__remove, .select2-container--classic .select2-selection--single .select2-selection__clear, .select2-container--default .select2-results__group, .select2-container--default .select2-selection--multiple .select2-selection__choice__remove, .select2-container--default .select2-selection--multiple .select2-selection__clear, .select2-container--default .select2-selection--single .select2-selection__clear, .v_high, .v_med, caption, div.g_bubble.quotebody div.quoteof, div.g_bubble.showhidecontent div.desc, div.relations .g_bubble.stripe div.type, h1, h2, h3, h4, h6, span.diff, #layout-main div.accounting_all .estimate .row .field, #layout-main div.addsimilaranime_all div.addsimilaranime_entries a.name-colored, #layout-main div.addtagentityrel_all div.addbox div.tag-column h5, #layout-main div.anime_all div.groups td.lastupdate.new, #layout-main div.anime_all div.groups td.state.complete a, #layout-main div.anime_all div.groups td.state.finished a, #layout-main div.cmt_entries a.username-colored, #layout-main div.creq_all .state.new, #layout-main div.creq_all .state.return, #layout-main div.creq_all span.type.del, #layout-main div.forum .g_bubble.infobox div.bar, #layout-main div.forum .g_bubble.infobox div.left, #layout-main div.forum a.username-colored, #layout-main div.forum div.add-box div.field, #layout-main div.forum div.navbar span.breadcrumbs, #layout-main div.forum div.options-box div.field, #layout-main div.forum div.posting-box div.field, #layout-main div.forum li.header div.segment, #layout-main div.forumsearch_form div.field, #layout-main div.group_released td.state.complete a, #layout-main div.group_released td.state.finished a, #layout-main div.main_all div.account ul.tabs li, #layout-main div.msg_all div.header div.field, #layout-main div.msg_all tr td.type, #layout-main div.msg_all tr.new td.state, #layout-main div.mylist_list tr.all_watched td.stats, #layout-main div.mylist_list tr.complete td.stats, #layout-main div.myvotes_all tr td.myvote.high, #layout-main div.search_all div.search_results strong, #layout-main div.tag-column .parent, #layout-main div.userpage_all div.discussions tr.unread, #layout-main table.filelist tr.undumped td.id a, #preferences div.account tr th {
      font-weight: bold;
      color: var(--color-GWhite) !important;
  }

  .g_segmentlist tr.g_odd + tr:not(.g_odd) td, .g_segmentlist tr:not(.g_odd) + tr.g_odd td, li + li.g_odd, li.g_odd + li, tr + tr td, tr + tr th, #layout-main #tagtree div.tag-column div.tag, #layout-main .g_bubble.graph div.group + div.group_odd, #layout-main .g_bubble.graph div.group_odd + div.group, #layout-main .g_bubble.graph.eps div.row + div.row, #layout-main > h1, #layout-main div div.explanation div.description dl + dl, #layout-main div.addtagentityrel_all #tag-list div.tag + div.tag, #layout-main div.g_definitionlist tr th, #layout-main div.g_timedentry h4, #layout-main div.info div.extra div table, #layout-main div.report_all div.body ul li + li, #layout-nav div.menu_hr, #layout-search ul.quicksearch li + li {
      border-top: 1px solid;
  }

  #layout-main div.animelist_all tr td + td, #layout-main div.characterlist_all tr td + td, #layout-main div.clublist_all tr td + td, #layout-main div.collectionlist_all tr td + td, #layout-main div.creatorlist_all tr td + td, #layout-main div.grouplist_all tr td + td, #layout-main div.latest2_all table tr td + td, #layout-main div.latest2_all table tr.rowspan td:first-child, #layout-main div.modlatest_all table tr td + td, #layout-main div.mydb_all table tr td + td, #layout-main div.mylist_all td+td, #layout-main div.mynotifies_all tr td+td, #layout-main div.myvotes_all td+td, #layout-main div.mywishlist_all tr td+td, #layout-main div.songlist_all tr td + td, #layout-main div.tag-column .tag-block + .tag-block, #layout-main div.tag_all tr td + td, #layout-main div.taglist_all tr td + td, #layout-main div.userlist_all tr td + td {
      border-left-style: none;
  }

  #layout-main .episodes tr.newtype td, #layout-main .staffblock + .staffblock table {
      border-top: 1px groove;
  }

  #groupfilter .i_icon {
      background-color: var(--color-DGBlue);
  }
}
