css:
@-moz-document domain("whippet.telecom-lille.fr") {
.navbar-inner {
	background: #000;
	border: none;
}

.navbar .brand,
.navbar .nav > li > a {
	color: #fff;
	text-shadow: none;
}

.navbar .logininfo {
	font-size: 0;
}
.navbar .logininfo a {
	font-size: 14px;
}
.navbar .logininfo a:first-child {
	margin-right:1em;
}

.breadcrumb {
	background: #222;
}
.breadcrumb > li {
	text-shadow: none;
}	

#page-header .logo {
	display: none;
}

.block {
	background: #222;
	border: none;
}

body {
	background: #333;
}

#page {
	background: #333;
	color: #fff;
}

input[type='text'],
input[type='password']{
	background: #000;
	color: #fff;
	border-color: #555;
}

button[type='submit'],
input[type='submit'],
.navbar .btn-navbar {
	background: #000 !important;
	color: #ccc !important;
	border-color: #555 !important;
	text-shadow: none !important;
	font-weight: 600 !important;
	box-shadow: none !important;
	transition: 0.2s ease !important;
}

button[type='submit']:hover,
input[type='submit']:hover,
.navbar .btn-navbar:hover {
	background: #000 !important;
	color: #fff !important;
	border-color: rgba(82, 168, 236, .8) !important;
	box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(82, 168, 236, .6) !important;
}

.select {
	background: #111;
	border-color: #555;
}

.course_category_tree .category > .content {
	margin-left: 1em;
	padding-left: 0;
}
.course_category_tree .category > .content > .courses {
	border-radius: 5px;
	margin: -3px;
}


.course_category_tree .category > .info {
	background-color: #111;
	border-color: #555;
}

.content .courses .even {
	background-color: #333;
}
.content .courses .odd {
	background-color: #222;
}

.content .courses .coursebox {
	border: none;
	border-radius: 0;
}

.label {
	font-size: 14px;
	font-weight: 400;
	padding: 0;
}

.topics span {
	color: #ccc !important;
	background: none !important;
}

a:link span,
a:link img+span {
	color: #00b8de !important;
}

#page-footer {
	border-top-color: #666;
}

footer .logininfo,
.homelink {
	display: none;
}
}
