css:
@-moz-document url-prefix("https://instaling.pl/") {
body {
    background-image:none !important;
    background-color:black !important;
    color: #0970f7;
}
::-webkit-scrollbar {
    display: none;
}
#answer {
    border-color: #0970f7;
    background-color: #0a0a0a;
    color: #0970f7;
}
.special_character_button {
    border-color: #0970f7;
    background-color: #0a0a0a;
    color: #0970f7;
}
#check {
    border-color: #0970f7;
    background-color: #0a0a0a;
    color: #0970f7;
}
.center {
    background-color:white !important;
    border-radius: 5px;
    position:absolute;
    margin-left:-40vw !important;
    border: 50vw solid black;
}
.alert {
    border-color: #0970f7!important;
    background-color: #0a0a0a !important;
    color: #0970f7!important;
}
#start_session_page {
    color: #0970f7;
}
#footer::after {
    margin-bottom: 10vw;
    position: absolute;
    content:' Wypróbuj też inny dodatek świetnie współgrający z tym - pakiet usprawnień!  Link:   bit.ly/instalingcss ';
    width: 2500px;
    margin-left: -10vw !important;
}
.left {
    display:none;
}
.right {
    display:none;
}
}
