css:
@-moz-document domain("foxnews.com") {
a {
    color: #c0d0ff;
}

/*Second top banner*/
.site-header .nav-row-lower {
    background-color: #303030;
}
.site-header .nav-row-lower nav li a {
    color: #c0c0ff;
}

/*Main background*/
.row, sticky-columns {
    background-color: #1b1b1b;
}

/*Top left and top right panels*/
.promo, promo-image, js-promo {
    background-color: #303030;
    border-color: black;
}

/*Top middle*/
.info {
    background-color: #303030;
}
/*Below top middle*/
.collection {
    background-color: #333;
    border: 1px solid #000;
}
/*Link above story links*/
.article .eyebrow {
    color: #dce5ff;
}

/*Big page header*/
h1, h2, h3, h4, h5, h6 {
    color: #ccc;
}

/*Side-panel header*/
.collection .heading .title {
    color: #ccc;
}
/*Story author*/
.article-header {
    color: #ccc;
}
/*Story sub-header border*/
.article-header .article-meta-lower {
    border-top: 1px solid #7c838a;
    border-top-color: rgb(124, 131, 138);
    border-bottom: 1px solid #7c838a;
    border-bottom-color: rgb(124, 131, 138);
}

/*Summary under title under video*/
.featured .info .caption p {
    color: #ccc;
}

.article-body {
    color: #ccc;
}
.article-body p a {
    color: #bfcffd;
    border-bottom: 2px solid #a7c6e5;
    border-bottom-color: rgb(167, 198, 229);
}

/*Comment entry*/
.sppre_rich-editor .sppre_rich-editor-container .sppre_input-wrapper {
    background-color: #020202;
}

/*Comment text seems to keep going dark, rather than #ccc.  Don't know why.*/
body {
    color: #ccc;
}

.eyebrow {
    color: #a7c6e5;
    font-size: 12px;
    line-height: 16px;
    font-weight: 600;
    text-transform: uppercase;
}

/*Related topics*/
.topic-preview-v2__TopicPreviewContainer-jQjAjM, jyfpaw {
    background-color: #303030;
}

/*footer above blue breadcrumbs section*/
.wrapper {
    background-color: #1b1b1b;
}

/*Top advertisement panel*/
.pre-content .ad-container {
    background-color: #303030;
}

/*BEGIN 2019-05-27*/
/*Large text quote in stories*/
.pull-quote .quote-text {
    color: #57b869;
}
/*END 2019-05-27*/
}
