/* i really want this to be global */
@font-face {
	font-family: Streamster;
	src: url(https://vaborgabe.github.io/web/fonts/Streamster.ttf) format('truetype');
}
@font-face {
	font-family: VCROSDMono;
	src: url(https://vaborgabe.github.io/web/fonts/VCROSDMono.ttf) format('truetype');
}
@font-face {
	font-family: SF-AlienIntrudersSolid;
	src: url(https://vaborgabe.github.io/web/fonts/SFAlienEncountersSolid.ttf) format('truetype');
}
@font-face {
	font-family: Vogue;
	src: url(https://vaborgabe.github.io/wb/fonts/Vogue.ttf) format('truetype');
}
@font-face {
	font-family: FortySeven;
	src: url(https://vaborgabe.github.io/web/fonts/47-BoldItalic.ttf) format('truetype');
}
@font-face {
	font-family: Lazer84;
	src: url(https://vaborgabe.github.io/web/fonts/Lazer84.ttf) format('truetype');
}

@keyframes hover1 {
	from {
			color: #856084;
	}
	to {
			color: #297373;
	}
}
@keyframes unhover1 {
	from {
			color: #297373;
	}
	to {
			color: #856084;
	}
}

._1YMukI_FrE7QRuUVSvq_Qe::after {
    content: "";
    background: url("https://wallpapertag.com/wallpaper/full/5/e/0/133956-cool-vaporwave-wallpaper-1920x1080-1920x1080-cell-phone.jpg")!important;
    background-repeat: no-repeat;
    background-size: cover!important;
    opacity: 0.5;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    z-index: -1;
}

.Root__nav-bar {
	background-color: #FFA1F1;
	box-shadow: 0px 0px 10px 5px #FFA1F1;
}

.navBar-link--active {
	font-family: Streamster;
	color: white;
}

[dir=ltr] .navBar-item--with-icon-left .navbar-link__text {
	animation-name: unhover1;
	animation-duration: .2s;
	font-family: Streamster;
	color: #856084;
	font-size: 20px;
}

[dir=ltr] .navBar-item--with-icon-left:hover .navbar-link__text:hover {
	animation-name: hover1;
	animation-duration: .2s;
	color: #297373;
}

::-webkit-scrollbar {
	width: 1%;
}

::-webkit-scrollbar-thumb {
	background-color: hsl(309, 100%, 82%);	
}

[dir=ltr] .navBar-link--active:after {
	width: 100%;
	z-index: -1;
	background: linear-gradient(90deg, rgba(159, 255, 203,1) 20.5625%,rgba(159, 255, 203,1) 52.1875%,rgba(159, 255, 203,0) 75.3125%,rgba(159, 255, 203,0) 80.5625%);
}

html:not([lang=ar]) .navBar-group-header {
	font-family: Vogue;
	color: #258EA6;
	font-size: 100%;
}

.navBar-item.navBar-item--small.navBar-item--with-icon-right {
	font-family: VCROSDMono;
	color: #004E64;
}

.react-contextmenu {
	box-shadow: 0px 0px 15px 0px rgba(50, 50, 50, 0.75);
	background-color: #9fffcb;
	font-family: SF-AlienIntrudersSolid;
	color: #004E64;
}

.react-contextmenu-item.react-contextmenu-item--selected {
	background-color: #004E64!important;
}

.navBar-header .logo {
	width: 80%;
	margin-bottom: 20px;
	color: rgba(0,0,0,0);
	background: url(https://vaborgabe.github.io/web/spotify/LogoVaporwave.png);
}

.navBar-item--with-icon-left .navBar-link-text-with-icon-wrapper, .navBar-item--with-icon-right .navBar-link-text-with-icon-wrapper {
	color: #004E64;
}

.Root__now-playing-bar, .now-playing-bar-container {
	z-index: 1;
	background-color: #9FFFCB;
	border-top-color: #258EA6;
}

.ConnectBar, .ConnectBar__device-icon.spoticon-spotify-connect-16 {
	background-color: rgba(0,0,0,0);
	color: rgba(0,0,0,0);
}

a {
	color: #258EA6;
}

.track-info__name {
	font-family: Vogue;
	color: #258EA6 ! important;
}

.track-info__name.ellipsis-one-line {
	font-family: Vogue;
	color: #258EA6 ! important;
}

._3v-H5s52KoSlx1rvzcnTvM {
	font-family: VCROSDMono;
}

.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:before {
	background-color: #FF6978;
}

.EYVkZXmTImKH4iH48N7Uj._1-aXXBO8bQU1WTFY-7B0m-:hover:before {
	background-color: #E85F5C;
}

._1Fj-rlIZXTSNgYOCuLh7xo {
	font-family: FortySeven;
	color: #97D2FB;
	letter-spacing: .2em;
}

.mo-info-name {
	font-family: Vogue;
	color: #C2F9BB;
}

.control-button, .control-button--circled:after {
	color: #258EA6;
	border-color: #258EA6;
}

.playback-bar__progress-time {
	font-family: Lazer84;
	font-size: 12pt;
	color: #247BA0;
}

.progress-bar__bg {
	background-color: #ffa1f1;
}

.progress-bar__fg {
	background-color: #84DCC6;
}

.progress-bar--is-active .progress-bar__fg {
	background-color: #258EA6;
}

.middle-align.progress-bar__slider {
	background-color: #F1FAEE;
}
