css:
@-moz-document url-prefix("https://forum.ge/") {
div.tableborder:nth-child(13) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(11) {
display: none
}
div.tableborder:nth-child(17) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(11) {
display: none
}

/*charchoebi blokebis irgvliv*/
.tableborder {
    background-color: #151515;;
    border: 2px solid #C2CFDF;
}

/*yvela linki unda iyos yviteli feris*/
a:link, a:visited, a:active {
    text-decoration: none;
    color: #d5c729;
}
.normalname a:link, .normalname a:visited, .normalname a:active {
    font-size: 109%;
    text-decoration: none;
    padding-bottom: 2px;
    color: #d5c729;
}

/*labelebi tetri feris*/
span.normalname:nth-child(1) {
    font-weight: bold;
    color: #fff;
}

div.tableborder:nth-child(17) > form:nth-child(7) > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > font:nth-child(5) {
    color: #f00 !important;
}

/*postebis blokebis ferebi*/
.postcolor {
    font-size: 105%;
    line-height: 150%;
    color: #fff;
    background-color: #333;
}
.postlinksbar {
    padding: 7px;
    margin-top: 1px;
    font-size: 91%;
    color: #3A4F6C;
    background-color: #545659;
}
.postlinksbar a:link, .postlinksbar a:visited, .postlinksbar a:active {
    padding: 7px;
    margin-top: 1px;
    font-size: 91%;
    color: #d5c729;
    background-color: #545659;

}
/*aqtiuri userebis veli*/
.activeuserstrip {
    padding: 6px;
    background-color: #545659;
}

/*mtliani ferebi*/
TABLE, TR, TD {
    font-family: Sylfaen, Arial Unicode MS;
    font-size: 10pt;
    color: #f00;
    background-color: #333 !important;
}
.maintitle {
    vertical-align: middle;
    font-weight: bold;
    padding: 8px 0px 8px 5px;
    color: #FFF;
    background-color: #151515;
}
.desc {
    font-size: 91%;
    color: #fff;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > a:nth-child(1) > img:nth-child(1) {
    display: none;
}
body > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) > a:nth-child(1) > img:nth-child(1) {
    display: none;
}

/*quotes forma, ferebi da a.sh.*/
#QUOTE {
    font-family: Sylfaen, Arial Unicode MS;
    font-size: 100%;
    padding-top: 5px;
    padding-right: 5px;
    padding-bottom: 5px;
    padding-left: 5px;
    color: #B6B6B6;
    /*background-color: #151515 !important;*/
    background-color: #333 !important;
    border: 1px solid #000;
    border-width: 3px 3px 3px 3px;
    border-color: #444 #444 #444 #444;
    border-style: solid;
}
#arrowchat_buddy_list_tab {
    width: 189px;
    color: white;    
}
#navstrip > img:nth-child(1) {
    display: none;
}
.titlemedium {
    font-weight: bold;
    padding: 7px;
    margin: 0px;
    color: #FFF;
    background-color: #333;
}
.darkrow2 {
    color: #FFF;
    background-color: #545659;
}
.darkrow1 {
    color: #fff;
    background-color: #151515 !important;
}
div.row2 {
    background-color: #000 !important;
}

/*input veli*/
.textinput, .radiobutton, .checkbox {
    font-size: 109%;
    font-family: Sylfaen, Arial Unicode MS;
    vertical-align: middle;
    background-color: #151515;
    color: white;
}
.pformstrip {
    font-weight: bold;
    padding: 7px;
    margin-top: 1px;
    color: #f00;
    background-color: #545659;
}
.tablepad {
    padding: 6px;
    background-color: #545659;
}

/*pop up image*/
.codebuttons {
    margin: 10px 0;
    background-color: forestgreen;
    border-radius: 15px;
    height: 30px;
}
#fgModalTitle {
    color: #fff;
    float: left;
    font-weight: bold;
}
#fgModalIMG {
    display: block;
    color: white;
}
.fgModalContent {
    position: relative;
    background-color: #545659;
    margin: auto;
    padding: 0;
    border: 1px solid #888;
    width: 700px;
    max-width: 95%;
}

/*shesvlis gilaki da menu*/
