.ant-dropdown-menu-dark .ant-dropdown-menu {
    background: #000 !important
}

.ant-layout-header {
    background: #000 !important
}

.ant-layout-sider {
    background: #000 !important;
}

.ant-layout-sider-zero-width-trigger {
