css:
@-moz-document domain("savieo.com") {
.header{
background:#141D26;
box-shadow:inset 0 -1px 0 #131313;
}

a{
color:#8798A5;
}
.hero__input{
color:#8798A5;
background:#11131a;
}
.nav__search-input{
border:1px solid #131313;
box-shadow:none;
}
.sites{
background:#141d26;
border-bottom:1px solid #131313;
}
.sites__site-label{
color:#8798A5;
}
body{
color:#8798A5;
}
.thumbnails{
background:#141d26;
}
.download__heading, .download__subheading, .footer__heading, .heading{
color:#8798A5;
}

.thumbnail__title{
color:#8798A5;
}

.footer{
color:#8798A5;
background:#11131a;
}

.nav__search-input{
color:#8798A5;
background:#141d26;
}

.nav__dropdown{
border-top:1px solid #131313;
border-left:1px solid #131313;
border-right:1px solid #131313;
border-bottom:1px solid #131313;
background:#141d26;
}
.nav__dropdown__label{
color:#8798A5;
}

.page--border{
border-bottom:1px solid #131313;
}
.page__header{
border-bottom:1px solid #161b21;
}

.page.page--border{
background:#1B2836;
}

.nav__search-input::placeholder {
    color: #8798A5;
}


.download{
background:#11131a;
}
.download__select{
background:#1B2836;
}
.download__heading{
border-bottom:1px solid #131313;
}
.tabs__buttons{
border-top:1px solid #131313;
border-bottom:1px solid #131313;
background:#141d26;
}
.tabs__button:first-child{
border-left:1px solid #131313;
}
.tabs__button{
color:#8798A5;
border-right:1px solid #131313;
}
.info__title{
color:#8798A5;
}
.info__link{
color:#8798A5;
border:1px solid #11131a;
border:none;
background:#11131a;
}

.info{
background:#141d26;
}
.tabs{
background:#141d26;
}

#root, body {
    background: #141d26;
}

.tabs__info{
background:#11131a;
border-bottom:1px solid #131313;
}
.tabs__button.is-active{
background:#11131a;
border-bottom:1px solid #131313;
}
.tabs__link{
color:#8798A5;
border:1px solid #131314;
background:#1B2836;
}

.site{
background:#141d26;
border-bottom:1px solid #131313;
}
.site__title{
color:#8798A5;
}
.site__header{
border-bottom:1px solid #131313;
}
.site__code{
color:#8798A5;
text-shadow:none;
background:#11131a;
}

.hero__input{
background:#11131a;
}

.hero__button{
color:#8798A5;
background:#702321;
border-radius:0;
}

.bookmarklet-button, .load-more-button{
color:#8798A5;
background:#702321;
border-radius:0;
}

.nav__search-button{
width:49px;
height:37px;
color:#8798A5;
background-color:#702321;
border-bottom-right-radius:0;
border-top-right-radius:0;
}

.hero__title{
color:#8798A5;
}
.hero__text{
color:#8798A5;
}

.nav__search-button:hover, .hero__button:hover, .bookmarklet-button:hover, .load-more-button:hover {
    background: #de1219;
    color: #11131A;
}
}
