css:
@-moz-document domain("nu.nl") {
* {
	box-shadow: none !important;
	--background: rgba(0, 0, 0, 1);
	--opahigh: .9;	/*.9*/
	--opamid: .75;	/*.75*/
	--opalow: .65;	/*.65*/
	--color: white;
	--text-shadow-block: 0px 0px 1px black;
	--text-shadow: var(--text-shadow-block), var(--text-shadow-block), var(--text-shadow-block), var(--text-shadow-block), var(--text-shadow-block), var(--text-shadow-block), var(--text-shadow-block);
	font-size-adjust: .67 !important;
}

body {
/* 	background: url("https://www.hdwallpaper.nu/wp-content/uploads/2015/07/beautiful_Wallpaper_083.jpg") no-repeat fixed center 30% / 180% auto; */
/* 	background: url("https://wallpaperstock.net/wallpapers/thumbs1/37002hd.jpg") no-repeat fixed center 10% / cover; */
/* 	background: url("https://www.pixel4k.com/wp-content/uploads/2018/09/miranda-kerr-2018-photoshoot-4k_1536860190.jpg") no-repeat fixed center / cover; */
	background: url("http://papers.co/wallpaper/papers.co-hf76-melanie-iglesias-sexy-model-bed-40-wallpaper.jpg") no-repeat fixed center 55%/ 170%;
	background-color: black;
}

/*Header*/
	.block.topmenu {
		position: relative;
	}

	/*Logo */ .block.topmenu .top-logo {
		height: 60px;
		width: 60px;
		transform: scale(1.9,1.9) !important;
		transform-origin: 45px 16px !important;
	}


	.block.topmenu .main-menu-wrapper, .block.topmenu .sub-menu-wrapper, .block.topbar2 {
		background-color: var(--background);
		border: 0;
		padding: 0;
	}

	.block.topmenu .main-menu-wrapper .block-content, .block.topmenu .block-content {
		background: transparent ;
	}

	.main-menu-wrapper, .sub-menu-wrapper {
		border: 0 !important;
	}

	[class="block topmenu dynamic-height small"] {
		visibility: hidden;
	}

	/*Top menu*/ .block.topmenu .main-menu > li:not([class="nav_item nav_more both-mobile-desktop"]) > a:hover {
		background-color: rgba(255, 255, 255, var(--opalow)) !important;
		text-shadow: none;
	}

	/*dropdown-menu*/
		.block.topmenu .dropdown-menu-wrapper .block-content {
			background-color: rgba(0, 0, 0, var(--opahigh));
			border-color: #fff0
		}

		.dropdown-menu > ul:nth-child(1)> [class="nav_item item_default"] *, .dropdown-menu-sidebar-links * {
			color: var(--color) !important;
		}
	/*dropdown-menu*/
/*Header*/

/*Content*/
	.contentlist.mostrecent-page li, .three-columns #main .column.first .contentlist.featured li, .block.talk .block-content {
		border-color: transparent
	}

	#container {
		max-width: unset;
		padding: 0 10px;
		margin: 0;
		background-color: var(--background);
	}

	/*Left side-bar*/.nu-main-menu {
		background: #FFF0;
		padding-left: 8px !important
	}


	/*Right-side*/
		/*Widget*/
			.widgetlinks > ul:nth-child(1) > li:nth-child(1) {
				border-radius: 50px 0 0 50px;
			}
			.widgetlinks > ul:nth-child(1) > li:nth-last-child(1) {
				border-radius: 0 50px 50px 0;
			}

			.widgetlinks > ul:nth-child(1) > li {
				padding-bottom: 5px;
			}

			.particle.widgetlinks.tiles li {
				background-color: rgba(255, 255, 255, var(--opamid));
				border-radius: 0;
			}

			.particle.widgetlinks.tiles li:not(:hover) {
				border: 1px solid transparent !important;
			}
		/*Widget*/

		/*More*/ .block.nujij, .link .extension .molecule-link .item-content, .highlight .compact .block-content {
			border-radius: 0;
			background-color: rgba(255, 255, 255, var(--opalow));
			border-color: transparent;
		}
	/*Right-side*/

	/*More*/ .block .block-footer .load-more {
		border-radius: 50px;
		background-color: rgba(255, 255, 255, var(--opalow));
		border-color: transparent;
	}
/*Content*/

/* Footer */
	.block.footer {
		background-color: var(--background);
		margin: 0;
		border: 0;
	}

	div.zone:nth-child(6) {
		display: none;
	}
/* Footer */

/*Images All Global*/
	 .block.image img, .slideshow .royalSlider, .highlight .royalSlider , .molecule-video-list-item .media
, [class=" section-nusport source-normal"] li > a > div > div > img 
{
		border-radius: 0;
		opacity: var(--opalow);
	}

	.particle.headerimage {
		border-radius: 0
	}

	.particle.headerimage img {
		opacity: var(--opalow);
	}

	.particle.headerimage:hover img, .block.image img:hover, .slideshow .royalSlider:hover, .highlight .royalSlider:hover, [class="video-item molecule-video-list-item item"]:hover .media, [class=" section-nusport source-normal"] li:hover img {
		opacity: var(--opahigh);
	}
/*Images All Global*/

/*Title block backgroung-color*/
	.block .block-title {
		border-radius: 16px;
	}

 	/**/ .block .block-title, .block .block-title.section-bgcolored, body.nu .block-title {
		background-color: rgba(1, 0, 80, var(--opamid));
	}

	/*Economie*/ body.nuzakelijk .block-title, .section-nuzakelijk .section-bgcolored, .section-nuzakelijk.section-bgcolored {
		background-color: rgba(0, 108, 222, var(--opamid)) !important;
	}

	/*Sport*/ body.nusport .block-title, .section-nusport .section-bgcolored {
		background-color: rgba(224, 6, 19, var(--opamid)) !important; 
	}

	/*Tech*/ body.nutech .block-title, .section-nutech .section-bgcolored, .section-nutech.section-bgcolored {
		background-color: rgba(81, 0, 175, var(--opamid)) !important;
	}

	/*Entertainment*/ body.nuentertainment .block-title, .section-nuentertainment .section-bgcolored, .section-nuentertainment.section-bgcolored {
		background-color: rgba(182, 15, 128, var(--opamid)) !important; 
	}

	/*Uit*/ body.nuuit .block-title, .section-nuuit .section-bgcolored, .section-nuuit.section-bgcolored {
		background-color: rgba(237, 137, 60, var(--opamid)) !important;
	}

	/*Overige*/ .section-nuoverig .section-bgcolored, .section-nuoverig.section-bgcolored {
		background-color: rgba(42, 145, 152, var(--opamid)) !important;
	}

	/**/ .section-nu .section-bgcolored, .section-nu.section-bgcolored {
		background-color: rgba(1, 0, 80, var(--opamid)) !important;
	}
/*Title block backgroung-color*/

/*Text*/
	.nu-main-menu a.nav-item.nav-main:not(.current), .topmenu ul.main-menu > li > a, .block.mostrecent .block-content *, .most-read > div:nth-child(1) > div:nth-child(2) *, [class="block-wrapper"] > [class="block-content clearfix"] *, .topbar .slogan .block-content, .sub-menu > li > a, .media-footer >.title-wrapper > .title, [title="meer navigatie opties"] > span, [class="column-content clearfix"] > .column.last div.block-footer > a, [class="footer_menu navigation"] ul li a, .footer_info > .social h6, .footer_info .copyright .copyright_text, .footer_info .copyright a, [class="block article-body"] > .block-wrapper > .block-content > *, .nav-group > .nav-children , .metadata > span, .block.article-tags *, [data-type="articlelist"] [class="block-wrapper"] > [class="block-footer"]  a:not(.ajax), .attribution > span
{
		color: var(--color);
/* 		text-shadow: var(--text-shadow); */
	}

	.contentlist.plain li .list-content::after, .contentlist li .list-content::after, .block .block-footer a.footer-link::after, .topmenu ul.main-menu > li > a:not(:hover), .sub-menu > li > a, [class="block-wrapper"] > [class="block-content clearfix"] *, [class="footer_menu navigation"] ul li a, [class="block article-body"] *, .block.article-tags *, .block.article-tags *, .nav-group > .nav-children * {
		color: var(--color) !important;
	}
/*Text*/
}
