css:
@-moz-document domain("coolors.co") {
/* Loading Screen */
.spinner-dark
{
    border-color: #0FACF3 transparent transparent;
}
#loader
{
    background: #222;
}

/* Remove Header Ad*/
.coolors-ad-2, header #skillshare-wrapper
{
    visibility: hidden !important;
}

/* Recolor Headers*/
header
{
    background: #222;
}
#sub-header
{
    background: #222;
}

/* Button Colors */
a
{
    color: #999;
}
a.disabled
{
    color: #444;
}

/* Divider Colors*/
#sub-header .divider, #main-nav .divider
{
    border-right: 1px solid #444;
}

/* Resizing */
@media only screen and (max-width: 975px)
{
    #sub-header nav li>a
    {
        font-size: 12px;
    }
    #sub-header p
    {
        visibility: hidden;
    }
}

/* --- HOME PAGE --- */
#home-hero
{
    background: #444444;
    background: -moz-linear-gradient(top,  #444444 0%, #262626 100%);
    background: -webkit-linear-gradient(top,  #444444 0%,#262626 100%);
    background: linear-gradient(to bottom,  #444444 0%,#262626 100%);

}
#home-features, #home-author, #home-browser .box
{
    background: #444;
}
#home-partners, footer
{
    background: #333;
    
    border: none;
}
#home-mobile, #home-addon
{
    background: #444;
    
    border: none;
}
#home-addon .box img, #home-mobile .box img
{
    filter: contrast(0) brightness(20);
}

#home-browser .back
{
    filter: brightness(.25);
}

.text-style-1, .margin-top-2
{
    color: white;
}

h1, .h1, h2, .h2
{
    color: white;
}
#carbonads, .coolors-ad
{
    visibility: hidden !important;
}
}
