css:
@-moz-document url-prefix("https://t.me/s/") {
/* HEADER */
.tgme_header {
    height: 56px;
}

.tgme_header_link {
    padding-top: 10px;
}

.tgme_header_search {
    padding-top: 11px;
}

.tgme_header_search_form_input {
    border-radius: 80px;
}

.tgme_header_right_column {
    top: 56px;
}

/* BODY */
body {
    background-color: #ebf1f5;
}

.tgme_channel_history {
    padding-top: 6px;
}

/* MESSAGES */
.tgme_widget_message_service_date_wrap,
.service_message {
  margin-left: 45px;   
}
.tgme_widget_message.service_message {
    display: block;
    width: auto;
}

.tgme_widget_message_wrap {
    padding: 6px 0;
}

.tgme_widget_message_bubble {
    border-top-left-radius: 0;
    border-top-right-radius: 6px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    box-shadow: none;
    padding: 10px 14px;
}

.bubble_icon .border_1x,
.bubble_icon .border_2x {
    display: none;
}

.tgme_widget_message_bubble > .tgme_widget_message_photo_wrap,
.tgme_widget_message_bubble > .tgme_widget_message_video_player,
.tgme_widget_message_bubble > .tgme_widget_message_grouped_wrap,
.tgme_widget_message_bubble > .tgme_widget_message_location_wrap {
    margin: 10px -12px;
    min-width: calc(100% + 24px) !important;
    max-width: calc(100% + 24px) !important;
}

.tgme_widget_message_inline_button {
    border-radius: 6px;
}

.tgme_widget_message_text, .tgme_widget_message_poll, .tgme_widget_message_inline_button {
    line-height: 20px;
}

/* SIDEBAR */
.tgme_right_column {
    padding: 24px 0;
}

.tgme_channel_info {
    padding: 10px 14px;
    border-radius: 6px;
    box-shadow: none;
}

a.tgme_channel_join_telegram,
a.tgme_channel_download_telegram {
    border-radius: 6px;
}

.tgme_channel_download_telegram_bottom {
    margin-top: 10px;
    font-size: 12px;
}
}
