css:
@-moz-document url-prefix("https://www.multitran.com")
{
  input[name="s"]
  {
    max-width: 80%;
  }

  .middle_col
  {
    padding-left: 0;
    padding-right: 0;
    max-width: 800px;
    overflow: visible;
    clear: both;
  }

  img[alt="Multitran dictionary"]
  {
    max-width: 150px;
    height: auto;
  }
/* Hide Ads */
  .adsbygoogle, #div-gpt-ad-1529757375277-0, left_banner, right_banner
  {
    display: none !important;
  }

/* Colors from clean styles */

  body
  {
    background: #303030 !important;
  }

body * {color: #999 !important; }
body a {color: #2e7fbe !important; }
a.menu1:link, a.menu1:visited, a.menu1:hover, a.menu1:active {
    display: block;
    font-weight: bold;
    font-size: 0.7em;
    font-family: "Verdana", Geneva, sans-serif;
    color: #ffffff;
    background-color: #194A76;
    width: 8.5em;
    text-align: center;
    text-transform: uppercase;
    /* padding: 4px; */
    padding-top: 4px;
    padding-right: 4px;
    padding-bottom: 4px;
    text-decoration: none;
    border-radius: 4px;
    float: left;
    margin-top: 0.1em;
    margin-bottom: 0.1em;
    margin-right: 0.4em;
}
a.menu:link, a.menu:visited {
    display: block;
    font-weight: bold;
    font-size: 0.7em;
    font-family: "Verdana", Geneva, sans-serif;
    color: #ffffff;
    background-color: #427db15c;
    width: 8.5em;
    text-align: center;
    text-transform: uppercase;
    /* padding: 4px; */
    padding-top: 4px;
    padding-right: 4px;
    padding-bottom: 4px;
    text-decoration: none;
    border-radius: 4px;
    float: left;
    margin-top: 0.1em;
    margin-bottom: 0.1em;
    margin-right: 0.4em;
}
  td[bgcolor="#dbdbdb"]
  {
    background: #dce3ee !important;
  }
}
