css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://twitter.com/?small") {
* {
font-size:small;
line-height:1.3em;
min-width:1px;
}

html {
background-color:#fff;
}

strong {
width:50% !important;
display:block !important;
}

#navigation {
display:none;
}

#profile-image {
width:32px !important;
margin:0 !important;
padding:0 !important;
}

textarea {
width:160px !important;
height: 3em !important;
background-color:#fff;
padding-right:25px !important;
} 

fieldset {
width:260px !important;
background:#fff;
margin:0;
}
#currently {display:none; }

#status {
width:220px !important;
}

.status-btn {
width:115px;
}




tr {
width:100% !important;
margin:0 !important;
padding:0 !important;
}

td {
margin:0 !important;
padding:0 !important;
}

body,a,#navigation,#update-submit,#container,.info {
width:100% !important;
min-width:1px;
margin:0 !important;
padding:0 !important;
}

#content {
width:160px !important;
min-width:1px;
margin:0 !important;
padding:0 !important;
}

#lf, #whatistwitter, #header, .forgot, .intro, #videobutton, .complete, #side_base,#footer,#user_search_form,label,.tabMenu,.yellow-box, 
.pagination {
display:none !important;
}


.wrapper {
width:100% !important;
min-width:1px;
margin:70px 0 0 !important;
padding:0 !important;
}

.content-bubble-arrow {display:none; }

.column { background-color:#fff; }

.status-body {
width: 160px !important;
}

li {width: 200px !important; } 

.hentry { width:250px !important; }


#timeline, .statuses { width:180px !important; }


.tab, .wrapper { width:180px !important;}

.wrapper { margin:0 !important; }

#status-field-char-counter { 
margin-right:0px !important;
}

.del, .reply { width:20px !important; }

}
