css:
/* DO NOT MODIFY HERE! Just a basic support for our color variables. Use gitlab.user.styl if you want to have greater customizability. */

@-moz-document url-prefix(https://git.conservice.com)
  {
    :root {
    --dark: #2e2e2e;
    --darker: #212121;
    --light: #d1d1d1;
    --lighter: #e9e9e9;
    --accent: #7289da;
    --icon: #e1e1e1;
    --yellow: #f0c674;
    --orange: #de935f;
    --red: #a54242;
    --magenta: #b294bb;
    --violet: #85678f;
    --blue: #5f819d;
    --cyan: #5e8d87;
    --green: #8c9440;
    --shadow: rgba(0, 0, 0, 0.07);
    --box-shadow: 0 2px 3px 0 var(--shadow);
    --t: transparent !important;
    --code-font: \"custom_font\", \"Menlo\", \"DejaVu Sans Mono\", \"Liberation Mono\", \"Consolas\", \"Ubuntu Mono\", \"Courier New\", \"andale mono\", \"lucida console\", monospace;
 --width: 100%;
    --radius: 4px;
    --bg-invert: 1;
    --bg-filter: opacity(0.1);
    --bg-size: 15px;
    --bg-repeat: repeat;
    --bg-pos: center;
    --background: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA8AAAAPCAYAAAA71pVKAAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAAOxAAADsQBlSsOGwAAABl0RVh0U29mdHdhcmUAd3d3Lmlua3NjYXBlLm9yZ5vuPBoAAAA7SURBVCiRY2BgYHBgQABbBgaGlwwMDE4ExBwYGBheMRBQRJQY/TUyQN3uQIZGBzhBhsbRACNR44gMMADUtz9VRtuY9AAAAABJRU5ErkJggg==");
}

/* DO NOT MODIFY HERE! It's a minified CSS after compiling it down from .styl source file. Modify the source at GitLab instead. */
::-webkit-scrollbar {
    width: 12px
}
::-webkit-scrollbar-track {
    background: var(--dark)
}
::-webkit-scrollbar-thumb {
    border: 2px solid var(--dark);
    background: var(--darker)
}
::placeholder {
    color: var(--lighter) !important
}
::-webkit-input-placeholder {
    color: var(--lighter) !important
}
h1,
h2,
h3,
h4,
h5,
h6,
p {
    color: var(--light) !important
}
ol,
ul {
    color: var(--light)
}
a {
    transition: 200ms ease-in-out;
    color: var(--accent)
}
a:focus,
a:hover {
    color: var(--accent)
}
hr {
    border-top: none;
    border-color: var(--shadow) !important;
    background-color: var(--shadow) !important
}
img.lazy,
img.transparent {
    border: 1px solid var(--dark) !important;
    background: transparent !important
}
code {
    border: 1px solid var(--darker);
    background-color: var(--dark)
}
table td {
    background-color: var(--dark)
}
gl-emoji {
    line-height: 1.2em
}
time {
    color: var(--light)
}
.fa,
svg {
    color: var(--icon) !important;
    fill: var(--icon) !important
}
.fa:hover,
svg:hover {
    opacity: 0.8 !important
}
.award-control-icon svg path,
.commit-icon path,
.nav-controls .has-tooltip svg > path {
    fill: var(--icon) !important
}
.bg-white {
    background-color: var(--dark) !important
}
.bg-info {
    background-color: var(--darker) !important
}
.text-white {
    color: var(--light) !important
}
.text-tertiary {
    color: var(--lighter) !important
}
.text-secondary {
    color: var(--light) !important
}
.text-danger {
    color: var(--red) !important
}
.badge.unverified {
    color: var(--lighter) !important;
    border-color: var(--lighter) !important;
    background-color: var(--dark) !important
}
.badge.verified {
    color: var(--green) !important;
    border-color: var(--green) !important;
    background-color: var(--dark) !important
}
.badge-success {
    color: var(--light) !important;
    background-color: var(--green) !important
}
.badge-primary {
    color: var(--light) !important;
    background-color: var(--blue) !important
}
.badge-info {
    color: var(--light) !important;
    background-color: var(--blue) !important
}
.badge-pill {
    color: var(--light) !important
}
.badge-pill.badge {
    color: var(--red) !important;
    background-color: var(--dark) !important
}
.badge-pill.issues-count {
    color: var(--white) !important;
    background-color: var(--green) !important
}
.badge-pill.merge-requests-count {
    color: var(--white) !important;
    background-color: var(--orange) !important
}
.badge-pill.todos-count {
    color: var(--white) !important;
    background-color: var(--blue) !important
}
.shortcut-mappings .key {
    color: var(--light) !important
}
.cgray,
.committed_ago,
.hint {
    color: var(--lighter) !important
}
.warning_message {
    -webkit-border-radius: 0 var(--radius) var(--radius) 0;
    -moz-border-radius: 0 var(--radius) var(--radius) 0;
    border-radius: 0 var(--radius) var(--radius) 0;
    border-left-color: var(--accent);
    color: var(--light) !important;
    background-color: var(--darker) !important
}
.bs-callout {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.bs-callout.gcp-signup-offer {
    border-left-color: var(--blue) !important
}
.card-footer > pre {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.btn {
    transition: transform 0.2s ease-in-out !important;
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--dark)
}
.btn.btn-default,
.btn.focus,
.btn:focus,
.btn:hover {
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--dark)
}
.btn.active,
.btn:active {
    color: var(--accent) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.btn.active > .fa,
.btn.active > i,
.btn:active > .fa,
.btn:active > i {
    color: inherit !important
}
.btn.focus,
.btn:focus {
    box-shadow: 0 0 2px 0 var(--accent)
}
.btn-link {
    color: var(--accent) !important
}
.btn.click-to-expand {
    padding: 3px
}
.btn .comment-btn,
.btn .commit-btn,
.btn-create,
.btn-new,
.btn-register,
.btn-save,
.btn-success,
.btn-success.dropdown-toggle,
.btn.comment-btn {
    color: var(--white) !important;
    border-color: var(--green) !important;
    background-color: var(--green) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-info,
.btn-primary,
.btn-register {
    color: var(--white) !important;
    border-color: var(--blue) !important;
    background-color: var(--blue) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-warning {
    color: var(--white) !important;
    border-color: var(--orange) !important;
    background-color: var(--orange) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-close,
.btn-close-color {
    color: var(--red) !important;
    border-color: var(--red) !important;
    background-color: var(--dark) !important
}
.btn-close-color.dropdown-toggle {
    color: var(--white) !important;
    border-color: var(--red) !important;
    background-color: var(--red) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-cancel {
    color: var(--white) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important
}
.btn-danger,
.btn-red,
.btn-remove {
    color: var(--white) !important;
    border-color: var(--red) !important;
    background-color: var(--red) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-inverted.btn-remove {
    color: var(--white) !important;
    border-color: var(--red) !important;
    background-color: var(--red) !important;
    box-shadow: var(--box-shadow) !important
}
.btn-blank,
.btn-clipboard {
    color: var(--lighter) !important;
    background-color: var(--t) !important
}
.btn-text-field {
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important
}
.btn-missing {
    border-style: solid !important;
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.btn.disabled,
.btn.disabled:hover,
.btn[disabled],
.btn[disabled]:hover {
    opacity: 0.6 !important;
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
    box-shadow: var(--box-shadow) !important
}
.btn:hover:not([disabled="disabled"]),
.btn:focus:not([disabled="disabled"]) {
    transform: translateY(-2px)
}
.btn.disabled:hover {
    transform: translateY(0px)
}
.input-group-append,
.input-group-btn,
.input-group-prepend {
    background-color: var(--t) !important
}
.input-group-append .btn:focus,
.input-group-btn .btn:focus,
.input-group-prepend .btn:focus {
    background-color: var(--darker) !important
}
.award-control.is-active,
.award-control.user-authored {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.award-control.active {
    color: var(--light) !important;
    border-color: var(--blue) !important;
    background-color: var(--dark) !important
}
.project-feature-toggle {
    background-color: var(--darker) !important
}
.project-feature-toggle.is-checked {
    background-color: var(--accent) !important
}
.project-feature-toggle .toggle-icon {
    background-color: var(--dark) !important
}
.status-box-mr-merged {
    color: var(--light) !important;
    background-color: var(--blue) !important
}
.status-box-mr-closed {
    color: var(--light) !important;
    background-color: var(--red) !important
}
.status-box-open {
    color: var(--light) !important;
    background-color: var(--green) !important
}
.status-box-issue-closed {
    color: var(--light) !important;
    background-color: var(--blue) !important
}
[class*="committer-link"],
[class*="author-link"],
.author_name a {
    color: var(--accent) !important
}
.nav-links:not(.quick-link) {
    border-bottom-color: var(--t)
}
.label-bold {
    color: var(--light)
}
.option-title {
    color: var(--light) !important
}
.option-description,
.option-disabled-reason {
    color: var(--lighter) !important
}
.deployment-info,
.js-deploy-time {
    color: var(--light) !important
}
.flex-grid .grid-row {
    border-color: var(--darker) !important
}
.flex-grid .grid-cell {
    border-color: var(--darker) !important
}
.flex-grid .grid-cell .property-label {
    color: var(--lighter) !important
}
.promotion-backdrop {
    background-color: var(--t) !important
}
.gl-responsive-table-row-layout {
    border-color: var(--darker) !important
}
.gl-responsive-table-row-layout .table-section:not(:first-child) {
    border-color: var(--darker) !important
}
.gl-responsive-table-row-layout .badge.badge-pill {
    color: var(--light) !important;
    background-color: var(--darker) !important
}
.gl-responsive-table-row-layout .table-button-footer {
    border-color: var(--darker) !important;
    background-color: var(--t) !important
}
@media screen and (max-width: 767px) {
    .gl-responsive-table-row-layout .table-button-footer {
        background-color: var(--darker) !important
    }
}
img[alt="404"] + .container {
    margin-bottom: 1em;
    border-color: var(--darker) !important
}
body,
body.navless {
    color: var(--light) !important;
    background-color: var(--t) !important
}
body .navbar-empty,
body.navless .navbar-empty {
    border-color: var(--darker) !important;
    background-color: var(--darker) !important
}
.login-page .new-session-tabs {
    border-color: var(--darker);
    box-shadow: 0 0 0 1px var(--darker)
}
.login-page .new-session-tabs li {
    color: var(--lighter) !important;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important
}
.login-page .new-session-tabs li a.active,
.login-page .new-session-tabs li a:hover {
    color: var(--light) !important;
    border-color: var(--accent) !important;
    background-color: var(--darker) !important
}
.login-page .login-box {
    -webkit-border-radius: 0 0 var(--radius) var(--radius);
    -moz-border-radius: 0 0 var(--radius) var(--radius);
    border-radius: 0 0 var(--radius) var(--radius);
    box-shadow: 0 0 0 1px var(--darker);
    background-color: var(--dark)
}
.login-page .login-box .form-control {
    background-color: var(--darker) !important
}
.login-page .omniauth-container {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
    background-color: var(--dark) !important;
    box-shadow: 0 0 0 1px var(--darker)
}
.login-page .omniauth-container .omniauth-btn {
    background-color: var(--darker) !important
}
.description-block,
.light,
.task-list-item {
    color: var(--light) !important
}
hr.footer-fixed {
    border-top-color: var(--darker);
    background: none !important
}
.footer-container {
    background: none !important
}
.footer-container a {
    color: var(--accent)
}
#CybotCookiebotDialog {
    color: var(--light) !important;
    background-color: var(--darker) !important;
    box-shadow: 0 0 4px 0 var(--shadow)
}
#CybotCookiebotDialog [id*="ButtonsSelectPane"] {
    border-color: var(--dark) !important;
    background-color: var(--dark) !important
}
#CybotCookiebotDialog [id*="DetailsWrapper"] {
    color: var(--accent) !important;
    border-color: var(--dark) !important;
    background-color: var(--accent) !important
}
#CybotCookiebotDialog [id*="DetailsWrapper"] a {
    color: var(--light)
}
#CybotCookiebotDialog [id*="DialogDetail"] {
    border-width: 1px;
    color: var(--light) !important;
    border-color: var(--dark) !important;
    background-color: var(--darker) !important
}
#CybotCookiebotDialog [id*="DialogDetail"] [id*="BodyContentTabs"] {
    border-color: var(--red)
}
#CybotCookiebotDialog [id*="DialogDetail"] a {
    color: var(--accent) !important;
    border-color: var(--dark) !important
}
#CybotCookiebotDialog [id*="DialogDetail"] a:hover,
#CybotCookiebotDialog [id*="DialogDetail"] a[class*="Selected"] {
    background-color: var(--dark) !important
}
#CybotCookiebotDialog [id*="DialogDetail"] thead th {
    color: var(--light) !important;
    border-color: var(--dark) !important;
    background-color: var(--dark) !important
}
#CybotCookiebotDialog [id*="DialogDetail"] tbody td {
    color: var(--light) !important;
    border-color: var(--dark) !important;
    background-color: var(--t) !important
}
.navbar {
    border-color: var(--darker) !important;
    background-color: var(--darker) !important;
    box-shadow: var(--box-shadow) !important
}
.navbar .logo-text svg {
    fill: var(--light) !important
}
.navbar .search form {
    color: var(--lighter) !important;
    border-color: var(--dark) !important;
    background-color: var(--dark) !important
}
.navbar .search form #search {
    color: var(--lighter) !important
}
.navbar a,
.navbar button {
    transition: all 0.2s ease-in-out;
    color: var(--lighter) !important
}
.navbar a:focus,
.navbar a:hover,
.navbar button:focus,
.navbar button:hover {
    color: var(--light) !important;
    background-color: var(--dark) !important
}
.navbar-nav > li.active > a,
.navbar-sub-nav > li.active > * {
    color: var(--light) !important;
    background-color: var(--dark) !important
}
.navbar .dropdown.show > button {
    background-color: var(--dark) !important
}
.navbar-toggler {
    border-left-color: var(--dark) !important
}
.navbar .dropdown.show > a {
    color: var(--light) !important;
    background-color: var(--dark) !important
}
.navbar .dropdown.show button.btn:hover {
    transform: none !important
}
.navbar .header-user li:last-child::before {
    content: var(--note);
    display: block;
    padding: 4px 12px 8px;
    border-bottom: 1px solid var(--shadow);
    color: var(--light)
}
.dropdown-menu,
.dropdown-menu-nav {
    box-shadow: var(--box-shadow) !important;
    border-color: var(--darker);
    background-color: var(--darker)
}
[class*="dropdown-loading"] {
    background-color: var(--dark)
}
.fa-spinner:before {
    color: var(--light)
}
.dropdown-title {
    color: var(--lighter);
    border-bottom-color: var(--darker)
}
.dropdown-menu-toggle,
.dropdown-toggle {
    color: var(--light);
    border-color: var(--darker) !important;
    background-color: var(--dark)
}
.default-dropdown-input,
.dropdown-input-field {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
[class*="frequent-items"] .section-header {
    color: var(--light) !important
}
[class*="frequent-items"] .section-empty,
[class*="frequent-items"] [class*="namespace"] {
    color: var(--lighter) !important
}
[class*="frequent-items"] [class*="dropdown-sidebar"] {
    border-color: var(--shadow) !important
}
.dropdown-menu.frequent-items-dropdown-menu.show {
    background-color: var(--darker) !important
}
.default-dropdown-input:focus,
.dropdown-input-field:focus {
    border-color: var(--blue);
    box-shadow: 0 0 1px 3px var(--shadow)
}
[class*="divider"] {
    border-color: var(--shadow) !important;
    background-color: var(--t) !important
}
.dropdown-menu .current-user,
.dropdown-menu li .menu-item,
.dropdown-menu li a,
.dropdown-menu li button,
.navbar-sub-nav {
    color: var(--light) !important
}
.dropdown-bold-header,
.dropdown-header {
    color: var(--lighter) !important
}
.dropdown-menu .droplab-item-selected > .menu-item,
.dropdown-menu > li:hover > .menu-item,
.dropdown-menu li a.is-focused,
.dropdown-menu li a:focus,
.dropdown-menu li a:not(.disable-hover):hover,
.dropdown-menu li button:hover {
    color: var(--light);
    background-color: var(--shadow) !important
}
#nav-projects-dropdown > button[aria-expanded="true"],
#nav-groups-dropdown > button[aria-expanded="true"],
.dropdown.show > a > *,
.dropdown.show > button > *,
.show > #clone-dropdown > *,
.show > #notifications-button,
.show > button,
.show > button > * {
    transition: none !important;
    transition-delay: none !important;
    color: var(--accent) !important;
    fill: var(--accent) !important
}
.dropdown-menu-inner-content,
.dropdown-toggle-text.is-default {
    color: var(--light)
}
.dropdown-menu .form-group label,
.dropdown-menu-inner-title {
    color: var(--lighter)
}
.nav .dropdown.show a:hover svg,
.nav a svg {
    fill: var(--icon) !important
}
.dropdown.show .dropdown-menu {
    max-height: 340px !important
}
.dropdown-item {
    transition: background 0.15s ease-in-out
}
.dropdown-item:focus,
.dropdown-item:hover {
    color: var(--light);
    background-color: var(--dark)
}
.dropdown-content-faded-mask::after {
    background: none
}
.select2-drop,
.select2-drop.select2-drop-above {
    color: var(--light);
    border-color: var(--darker);
    background-color: var(--darker)
}
.select2-container-multi .select2-choices .select2-search-field input.select2-active {
    background: var(--dark) !important
}
.select2-choice,
.select2-container-multi .select2-choices,
.select2-drop input,
.select2-search-choice {
    color: var(--light) !important;
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.select2-drop .select2-result-label {
    color: var(--light) !important
}
.select2-default,
.select2-results .select2-highlighted {
    color: var(--lighter) !important
}
.select2-results .select2-highlighted .select2-result-label {
    background-color: var(--darker)
}
.select2-results .select2-highlighted .select2-result-label:hover {
    background-color: var(--shadow) !important
}
.select2-arrow {
    padding-left: 10px;
    background-color: var(--dark) !important
}
.gitlab-tabs {
    -webkit-border-radius: var(--radius) var(--radius) 0 0;
    -moz-border-radius: var(--radius) var(--radius) 0 0;
    border-radius: var(--radius) var(--radius) 0 0;
    border-color: var(--darker) !important;
    background-color: var(--darker) !important
}
.gitlab-tabs .nav-link.active,
.gitlab-tabs .nav-link:hover {
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0
}
.gitlab-tabs li:not(:last-child) {
    border-right-color: var(--darker)
}
.gitlab-tabs li a.active,
.gitlab-tabs li a:hover {
    color: var(--lighter) !important;
    border-color: var(--accent) !important;
    background-color: var(--dark) !important
}
.gitlab-tab-content {
    -webkit-border-radius: 0 0 var(--radius) var(--radius);
    -moz-border-radius: 0 0 var(--radius) var(--radius);
    border-radius: 0 0 var(--radius) var(--radius);
    border-color: var(--darker) !important;
    background-color: var(--dark) !important
}
.gitlab-tab-content [class*="input-group"] {
    -webkit-border-radius: var(--radius);
    -moz-border-radius: var(--radius);
    border-radius: var(--radius);
