css:
@-moz-document url-prefix("https://mynoise.net") {
@font-face {
	font-family:'Lato';
	font-style: italic;
	font-weight: 400;
	font-display: swap;
	src: local('Lato Italic'), local('Lato-Italic'), url(https://fonts.gstatic.com/s/lato/v15/S6u8w4BMUTPHjxsAXC-q.woff2) format('woff2');
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
@font-face {
	font-family:'Lato';
	font-style: normal;
	font-weight: 400;
	font-display: swap;
	src: local('Lato Regular'), local('Lato-Regular'), url(https://fonts.gstatic.com/s/lato/v15/S6uyw4BMUTPHjx4wXg.woff2) format('woff2');
	unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;
}
::selection {
	background: lightgrey;
}
::-webkit-scrollbar {
	width: 0 !important;
}
* {
	font-family:'Lato', 'Roboto', Arial, sans-serif !important;
	letter-spacing: 0 !important;
	border-color: grey !important;
	box-shadow: none !important;
	color: black !important;
	border-radius: 0 !important;
	text-shadow: none !important;
	opacity: 1 !important;
	outline: none !important;
}
#bgcontainer, #lilBoxR, .menuNew, .Lslide, .jspVerticalBar, #calGrid img, .contextPlay img, #s0 .ui-slider-range, #s1 .ui-slider-range, #s2 .ui-slider-range, #s3 .ui-slider-range, #s4 .ui-slider-range, #s5 .ui-slider-range, #s6 .ui-slider-range, #s7 .ui-slider-range, #s8 .ui-slider-range, #s9 .ui-slider-range, .sectionIcon {
	display: none !important;
}
.player {
	border:1px solid black;
	margin-top: 10px;
}
body {
	background-color: whitesmoke;
}
.content {
	text-align: left !important;
}
#container {
	background: none !important;
}
#bgimage, #s0, #s1, #s2, #s3 {
	background: none !important;
	background-color: none !important;
}
img {
	-webkit-filter: grayscale(100%);
	filter: grayscale(100%);
	border: none !important;
	padding: 0 !important;
	margin: 0 !important;
}
#searchTxt, input, select {
	background: none !important;
	border:1px solid #000 !important;
	padding: 2px !important;
}
.mixer #s0, .mixer #s1, .mixer #s2, .mixer #s3, .mixer #s4, .mixer #s5, .mixer #s6, .mixer #s7, .mixer #s8, .mixer #s9 {
	background: none !important;
	border: 1px solid black !important;
	width: 15px !important;
}
#s0 .ui-slider-handle, #s1 .ui-slider-handle, #s2 .ui-slider-handle, #s3 .ui-slider-handle, #s4 .ui-slider-handle, #s5 .ui-slider-handle, #s6 .ui-slider-handle, #s7 .ui-slider-handle, #s8 .ui-slider-handle, #s9 .ui-slider-handle {
	box-sizing: border-box;
	background: none !important;
	border-bottom: none !important;
	border-left: none !important;
	border-right: none !important;
	border-top: 2px solid black !important;
	width: 25px !important;
	height:10px !important;
	margin-bottom: -9px !important;
}
.btnImg {
	background: none !important;
	border: none !important;
}
.btnImg:active, .active {
	background: none !important;
	opacity: .5 !important;
}
.iNB, .iHC, .iST, .iMN, .iEE, .iTN, .iMU, .iCL, .iHP, .iSearch, .iFV, .iUFV, .iCU {
	-webkit-filter: grayscale(100%);
	filter: grayscale(100%);
}
.iNB, .iHC, .iST, .iMN, .iEE, .iTN, .iMU, .iCL, .iHP, .iSearch, .iFV, .iUFV, .iCU {
	width: 14px !important;
	height: 14px !important;
}
.iFV, .iUFV {
	content: url(https://img.icons8.com/small/14/000000/star.png);
}
.iNB {
	content: url(https://img.icons8.com/small/14/000000/moon-symbol.png);
}
.iHC {
	content: url(https://img.icons8.com/small/14/000000/heart.png);
}
.iST {
	content: url(https://img.icons8.com/small/14/000000/happy.png);
}
.iMN {
	content: url(https://img.icons8.com/small/14/000000/stones.png);
}
.iEE {
	content: url(https://img.icons8.com/small/14/000000/spider.png);
}
.iTN {
	content: url(https://img.icons8.com/small/14/000000/alarm.png);
}
.iMU {
	content: url(https://img.icons8.com/small/14/000000/music.png);
}
.iCL {
	content: url(https://img.icons8.com/small/14/000000/define-location.png);
}
.iHP {
	content: url(https://img.icons8.com/small/14/000000/headphones.png);
}
.jspContainer .actionlink {
	content: url(https://img.icons8.com/small/12/000000/like.png);
}
.btn {
	width: 16px !important;
	height: 16px !important;
}
.btn:nth-of-type(1) img {
	content: url(https://img.icons8.com/small/16/000000/reset.png)
}
.btn:nth-of-type(2) img {
	content: url(https://img.icons8.com/small/16/000000/move-grabber.png)
}
.btn:nth-of-type(3) img {
	content: url(https://img.icons8.com/small/16/000000/switch.png)
}
.btn:nth-of-type(4) img {
	content: url(https://img.icons8.com/small/16/000000/speed.png)
}
.btn:nth-of-type(5) img {
	content: url(https://img.icons8.com/small/16/000000/low-volume.png)
}
.btn:nth-of-type(6) img {
	content: url(https://img.icons8.com/small/16/000000/high-volume.png)
}
.btn:nth-of-type(7) img {
	content: url(https://img.icons8.com/small/16/000000/no-audio.png)
}
.btn:nth-of-type(8) img {
	content: url(https://img.icons8.com/small/16/000000/time.png)
}
.LfixedImg, .mynoiseTitle img {
	-webkit-filter: brightness(0%);
	filter: brightness(0%);
}
p a {
	text-decoration: underline;
}
span.DIM a {
	text-decoration: none !important;
}
.albumLeftSmall, .album {
	margin: 0 20px 10px 0 !important;
}
img.albumLeft {
	margin: 20px 20px 20px 0 !important;
}
img.over {
	-webkit-filter: grayscale(100%) invert(100%) brightness(96%);
	filter: grayscale(100%) invert(100%) brightness(96%);
	margin: 10px 10px 0 0 !important;
}
img.suggestImg {
	margin: 0 20px 10px 0 !important;
}
}

@-moz-document url-prefix("https://mynoise.net/Albums") {
body {
	padding: 50px 150px !important;
	background: whitesmoke !important;
}
div {
	background: none !important;
}
}
