css:
@-moz-document domain("onedayonly.co.za") {
h1,
h2,
h3,
h4,
h5 {
    color: #eee !important;
}
p,
li {
    color: #eee !important;
}

/*********  Header   ***********************************************/
header > div:first-child {
    background: linear-gradient( to right, #777, #333, #262626) !important;
}

header svg#prefix__Layer_1 {
    height: 50px !important;
}

/*********  Home   ***********************************************/
html {
    scrollbar-color: #aaa #111;
}
body {
    background: #262626 !important;
    color: #ddd !important;
}
div[id="__next"] > div > div > div[width="1"] > div > div[id] > div {
    background: #282828;
    border-color: #666;
}

div[id="__next"] > div > div > div[width="1"] > div > div[id] button {
    display: none;
}
.collapsed {
    height: unset !important;
}
::-webkit-scrollbar {
    height: 12px;
    width: 12px;
    background: #111 !important;
}
::-webkit-scrollbar-thumb {
    background: #aaa !important;
}
::-webkit-scrollbar-corner {
    background: #111 !important;
}
}
